SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_repasse_conta_estorno ( nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
ds_observacao_w		varchar(4000);


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	repasse_terceiro_item
where	nr_interno_conta	= nr_interno_conta_p;

ds_observacao_w	:= wheb_mensagem_pck.get_texto(303692,'NR_SEQUENCIA=' || nr_sequencia_w);

insert	into	repasse_terceiro_item(
			nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			vl_repasse,
			nr_seq_terceiro,
			ds_observacao,
			dt_lancamento,
			nr_interno_conta)
	SELECT	nextval('repasse_terceiro_item_seq'),
		nm_usuario_p,
		a.dt_atualizacao,
		(a.vl_repasse * -1),
		a.nr_seq_terceiro,
		ds_observacao_w,
		a.dt_lancamento,
		a.nr_interno_conta
	from	repasse_terceiro_item a
	where	nr_interno_conta	= nr_interno_conta_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
