CREATE OR REPLACE PROCEDURE gerar_repasse_tre ( nr_seq_tipo_valor_p bigint, ie_tipo_valor_p text, vl_item_p bigint, nr_adiant_pago_p bigint, cd_material_p bigint, nr_repasse_terceiro_p bigint, cd_centro_custo_p bigint, nm_usuario_p text, nr_seq_terc_regra_esp_p bigint, nr_seq_terc_regra_item_p bigint) AS $body$
DECLARE


nr_sequencia_item_w	bigint;
vl_item_repasse_w		double precision;


BEGIN

vl_item_repasse_w		:= vl_item_p;

if (coalesce(ie_tipo_valor_p,0) = 2) then
	vl_item_repasse_w	:= vl_item_repasse_w * -1;
elsif (coalesce(ie_tipo_valor_p,0) = 3) then /* pagamento fixo. Para 'futuros tratamentos'. Fiz o if mais para frente. */
	vl_item_repasse_w	:= vl_item_p;
elsif (coalesce(ie_tipo_valor_p,0) in (7,6)) then /* material/medicamento / Adiantamento */
	vl_item_repasse_w	:= vl_item_repasse_w * -1;
end if;
if (coalesce(vl_item_repasse_w,0)	<> 0) then
	select	coalesce(max(nr_sequencia_item),0) + 1
	into STRICT	nr_sequencia_item_w
	from	repasse_terceiro_item
	where	nr_repasse_terceiro	= nr_repasse_terceiro_p;

	insert into repasse_terceiro_item(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		vl_repasse,
		nr_seq_tipo_valor,
		nr_repasse_terceiro,
		nr_sequencia_item,
		cd_centro_custo,
		nr_adiant_pago,
		cd_material,
		nr_seq_terc_regra_esp,
		nr_seq_terc_regra_item)
	values (nextval('repasse_terceiro_item_seq'),
		clock_timestamp(),
		nm_usuario_p,
		coalesce(vl_item_repasse_w,0),
		nr_seq_tipo_valor_p,
		nr_repasse_terceiro_p,
		nr_sequencia_item_w,
		cd_centro_custo_p,
		nr_adiant_pago_p,
		cd_material_p,
		nr_seq_terc_regra_esp_p,
		nr_seq_terc_regra_item_p);
end if;

commit;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
