CREATE OR REPLACE PROCEDURE gerar_requisicao_integracao ( nr_documento_externo_p bigint, cd_estabelecimento_p bigint, cd_local_estoque_p bigint, cd_local_estoque_destino_p bigint, dt_solicitacao_requisicao_p timestamp, dt_liberacao_p timestamp, nm_usuario_p text, cd_operacao_estoque_p bigint, cd_pessoa_requisitante_p text, ie_urgente_p text, cd_material_p bigint, qt_material_requisitada_p bigint, ds_arquivo_p text, nr_requisicao_p INOUT bigint) AS $body$
DECLARE


nr_requisicao_w			bigint;
nr_requisicao_existente_w		bigint;
nr_seq_item_w			integer;
qt_estoque_w			double precision;
qt_conv_estoque_consumo_w	double precision;
cd_unidade_medida_estoque_w	varchar(30);
cd_unidade_medida_consumo_w	varchar(30);



BEGIN

if (coalesce(nr_requisicao_p::text, '') = '') then

	select	nextval('requisicao_seq')
	into STRICT	nr_requisicao_w
	;

	insert into requisicao_material(
		nr_requisicao,
		cd_estabelecimento,
		cd_local_estoque,
		dt_solicitacao_requisicao,
		dt_atualizacao,
		nm_usuario,
		cd_operacao_estoque,
		cd_pessoa_requisitante,
		cd_local_estoque_destino,
		ie_urgente,
		ie_geracao,
		nr_documento_externo,
		ds_observacao)
	values (nr_requisicao_w,
		cd_estabelecimento_p,
		cd_local_estoque_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		cd_operacao_estoque_p,
		cd_pessoa_requisitante_p,
		cd_local_estoque_destino_p,
		substr(ie_urgente_p,1,1),
		'I',
		nr_documento_externo_p,
		wheb_mensagem_pck.get_texto(302197,'DS_ARQUIVO_P='||ds_arquivo_p));

nr_requisicao_p := nr_requisicao_w;
end if;

select	coalesce(max(nr_sequencia),0) +1
into STRICT	nr_seq_item_w
from	item_requisicao_material
where	nr_requisicao = coalesce(nr_requisicao_w,nr_requisicao_p);

select	qt_conv_estoque_consumo,
	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UME'),1,30) cd_unidade_medida_estoque,
	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UMS'),1,30) cd_unidade_medida_consumo
into STRICT	qt_conv_estoque_consumo_w,
	cd_unidade_medida_estoque_w,
	cd_unidade_medida_consumo_w
from	material
where	cd_material = cd_material_p;

qt_estoque_w := dividir(qt_material_requisitada_p,qt_conv_estoque_consumo_w);

insert into item_requisicao_material(
	nr_requisicao,
	nr_sequencia,
	cd_estabelecimento,
	cd_material,
	qt_material_requisitada,
	qt_material_atendida,
	vl_material,
	dt_atualizacao,
	nm_usuario,
	cd_unidade_medida,
	ie_acao,
	cd_motivo_baixa,
	qt_estoque,
	cd_unidade_medida_estoque,
	ie_geracao)
values (coalesce(nr_requisicao_w,nr_requisicao_p),
	nr_seq_item_w,
	cd_estabelecimento_p,
	cd_material_p,
	qt_material_requisitada_p,
	0,
	0,
	clock_timestamp(),
	nm_usuario_p,
	cd_unidade_medida_consumo_w,
	'I',
	0,
	qt_estoque_w,
	cd_unidade_medida_estoque_w,
	'I');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
