CREATE OR REPLACE PROCEDURE gerar_requisicao_mat_nutr ( cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint, cd_operacao_estoque_p bigint, cd_local_estoque_p bigint, cd_centro_custo_p bigint, cd_pessoa_requisitante_p text, nr_seq_receita_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_requisicao_w				bigint;
nr_sequencia_w				integer := 0;
cd_material_w				integer;
qt_conversao_w				double precision;
qt_componente_w				double precision;
cd_unidade_medida_consumo_w		varchar(30);
cd_unidade_medida_estoque_w		varchar(30);

c01 CURSOR FOR
	SELECT	b.cd_material,
		b.qt_conversao,
		a.qt_componente,
		substr(obter_dados_material_estab(c.cd_material,cd_estabelecimento_p,'UMS'),1,30) cd_unidade_medida_consumo,
		substr(obter_dados_material_estab(c.cd_material,cd_estabelecimento_p,'UME'),1,30) cd_unidade_medida_estoque
	from	material c,
		nut_genero_alim b,
		nut_rec_real_comp a
	where	coalesce(nr_seq_gen_alim_sub,a.nr_seq_gen_alim)	= b.nr_sequencia
	and	b.cd_material					= c.cd_material
	and	a.nr_seq_rec_real				= nr_seq_receita_p;


BEGIN


select	nextval('requisicao_seq')
into STRICT	nr_requisicao_w
;

begin
insert	into requisicao_material(
	nr_requisicao,
	cd_estabelecimento,
	cd_local_estoque,
	cd_setor_atendimento,
	dt_solicitacao_requisicao,
	dt_atualizacao,
	nm_usuario,
	cd_operacao_estoque,
	cd_pessoa_requisitante,
	cd_estabelecimento_destino,
	cd_local_estoque_destino,
	cd_centro_custo,
	ie_urgente)
values (	nr_requisicao_w,
	cd_estabelecimento_p,
	cd_local_estoque_p,
	cd_setor_atendimento_p,
	clock_timestamp(),
	clock_timestamp(),
	nm_usuario_p,
	cd_operacao_estoque_p,
	cd_pessoa_requisitante_p,
	cd_estabelecimento_p,
	null,
	cd_centro_custo_p,
	'N');
exception
	when others then
        	Null;
end;

OPEN c01;
LOOP
FETCH c01 into
	cd_material_w,
	qt_conversao_w,
	qt_componente_w,
	cd_unidade_medida_consumo_w,
	cd_unidade_medida_estoque_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	qt_componente_w	:= dividir(qt_componente_w,qt_conversao_w);
	nr_sequencia_w   	:= (nr_sequencia_w + 1);
      	insert into item_requisicao_material(
	        	nr_requisicao,
		nr_sequencia,
		cd_estabelecimento,
		cd_material,
        		qt_material_requisitada,
		qt_material_atendida,
		vl_material,
		dt_atualizacao,
		nm_usuario,
		cd_unidade_medida,
		qt_estoque,
		cd_unidade_medida_estoque,
		ie_acao,
		cd_motivo_baixa)
      	values (	nr_requisicao_w,
		nr_sequencia_w,
		cd_estabelecimento_p,
	                cd_material_w,
		qt_componente_w,
		0,
		0,
		clock_timestamp(),
		nm_usuario_p,
		cd_unidade_medida_consumo_w,
		0,
		cd_unidade_medida_estoque_w,
		'1',
		0);
	end;
END LOOP;
CLOSE c01;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
