CREATE OR REPLACE PROCEDURE gerar_reserva_cme ( nr_seq_agenda_p bigint, nr_seq_conjunto_p bigint, nr_seq_conjunto_agenda_p bigint, cd_estab_destino_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

				 
nr_seq_evento_w	bigint;				
 

BEGIN 
 
insert	into	reserva_agenda_cme(nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		nr_seq_agenda, 
		cd_estab_destino, 
		nr_seq_conjunto_agenda, 
		nr_seq_conjunto)	 
values (nextval('reserva_agenda_cme_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_seq_agenda_p, 
		cd_estab_destino_p, 
		nr_seq_conjunto_agenda_p, 
		nr_seq_conjunto_p);
commit;		
 
select	max(nr_seq_evento) 
into STRICT	nr_seq_evento_w 
from	regra_envio_sms 
where	cd_estabelecimento		= cd_estabelecimento_p 
and	ie_evento_disp			= 'SECME' 
and	coalesce(ie_situacao,'A') = 'A';
 
if (nr_seq_evento_w IS NOT NULL AND nr_seq_evento_w::text <> '') then 
	CALL gerar_evento_agenda_cirurgica(nr_seq_evento_w,nr_seq_agenda_p,nr_seq_conjunto_p,0,nm_usuario_p,cd_estabelecimento_p,'N');
end if;	
 
end	;
