			cd_profissional,
			nr_seq_tipo,
			ds_comunicado,
			nm_usuario,
			nm_usuario_nrec,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nr_sequencia,
			nr_prescricao,
			nr_seq_prescricao,
			nr_seq_pres_mat_com)
	values (clock_timestamp(),
			cd_profissional_p,
			nr_seq_tipo_p,
			ds_Comunicado_w,
			nm_usuario_p,
			nm_usuario_p,
