CREATE OR REPLACE PROCEDURE gerar_resposta_parecer ( nr_seq_regulacao_p bigint, nm_usuario_p text, nr_seq_interno_p INOUT bigint) AS $body$
DECLARE


nr_seq_interno_ori_w	bigint;
nr_parecer_ori_w		bigint;
nr_seq_interno_w		bigint;
cd_profissional_w		varchar(10);


BEGIN

If ( coalesce(nr_seq_regulacao_p,0) > 0) then

	Select 	coalesce(max(nr_seq_resp_parecer_ori),0),
			coalesce(max(nr_seq_parecer_ori),0)
	into STRICT	nr_seq_interno_ori_w,
			nr_parecer_ori_w
	from	regulacao_atend
	where	nr_sequencia = nr_seq_regulacao_p;

	if ( nr_seq_interno_ori_w > 0 ) then

		nr_seq_interno_w := nr_seq_interno_ori_w;

	elsif ( nr_parecer_ori_w > 0) then

		Select 	nextval('parecer_medico_seq'),
				Obter_Pf_Usuario(nm_usuario_p,'C')
		into STRICT	nr_seq_interno_w,
				cd_profissional_w
		;

		insert into parecer_medico(	nr_seq_interno,
										nr_parecer,
										nr_sequencia,
										cd_medico,
										dt_atualizacao,
										nm_usuario,
										ie_situacao,
										ie_status,
										nr_seq_regulacao )
							values (	nr_seq_interno_w,
										nr_parecer_ori_w,
										1,
										cd_profissional_w,
										clock_timestamp(),
										nm_usuario_p,
										'A',
										'F',
										nr_seq_regulacao_p);

		commit;

		update	regulacao_atend
		set		dt_atualizacao = clock_timestamp(),
				nm_usuario = nm_usuario_p,
				nr_seq_resp_parecer_ori = nr_seq_interno_w
		where	nr_sequencia = nr_seq_regulacao_p;

		commit;

	end if;

end if;

nr_seq_interno_p := nr_seq_interno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
