SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_resultado_exame (nr_prescricao_p text, cd_exame_p text, nr_seq_interno_p text, ds_tipo_resultado_p text, ds_resultado_p text, ds_observacao_p text, ds_referencia_p text, ds_result_status_p text, ds_date_time_obs_p text, ds_units_p text, nm_usuario_p text, cd_exame_princ_p INOUT text) AS $body$
DECLARE



nr_seq_prescr_w    		prescr_procedimento.nr_sequencia%TYPE;
ie_status_atend_w		prescr_procedimento.ie_status_atend%TYPE;
ie_status_receb_w		prescr_procedimento.ie_status_atend%TYPE;

cd_exame_princ_w		exame_laboratorio.cd_exame%TYPE;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%TYPE;
ie_sexo_w				pessoa_fisica.ie_sexo%TYPE;
nr_seq_resultado_w		exame_lab_resultado.nr_seq_resultado%TYPE;
nr_seq_exame_princ_w	exame_laboratorio.nr_seq_exame%TYPE;

cd_analito_w			exame_laboratorio.cd_exame%TYPE;
nr_seq_analito_w		exame_laboratorio.nr_seq_exame%TYPE;

nr_seq_material_w		material_exame_lab.nr_sequencia%TYPE;

ds_resultado_w			exame_lab_result_item.ds_resultado%TYPE;
qt_resultado_w			exame_lab_result_item.qt_resultado%TYPE;
pr_resultado_w			exame_lab_result_item.pr_resultado%TYPE;
nr_seq_result_lab_w		result_laboratorio.nr_sequencia%TYPE;

nr_anos_w				smallint;
ds_erro_w				varchar(4000);

ie_campo_calculo_w		varchar(1);
ie_formato_resultado_w	varchar(3);


BEGIN

if (cd_exame_p IS NOT NULL AND cd_exame_p::text <> '') then

	SELECT 	MAX(nr_sequencia),
			MAX(ie_status_atend),
			MAX(coalesce(b.cd_exame_integracao, b.cd_exame)),
			MAX(a.nr_seq_exame)
	INTO STRICT	nr_seq_prescr_w,
			ie_status_atend_w,
			cd_exame_princ_w,
			nr_seq_exame_princ_w
	FROM 	prescr_procedimento a,
			exame_laboratorio b
	WHERE 	a.nr_seq_exame = b.nr_seq_exame
	AND		a.nr_prescricao = nr_prescricao_p
	AND		a.nr_seq_interno = nr_seq_interno_p
	AND		coalesce(a.ie_suspenso,'N') = 'N'
	AND		coalesce(b.ie_situacao,'A') = 'A';

	cd_exame_princ_p := cd_exame_princ_w;

	SELECT	MAX(cd_estabelecimento),
			MAX(obter_sexo_prescricao(nr_prescricao))
	INTO STRICT	cd_estabelecimento_w,
			ie_sexo_w
	FROM	prescr_medica
	WHERE	nr_prescricao = nr_prescricao_p;

	SELECT	MAX(ie_status_receb)
	INTO STRICT	ie_status_receb_w
	FROM	lab_parametro
	WHERE	cd_estabelecimento = cd_estabelecimento_w;

	SELECT  TRUNC((clock_timestamp() -  dt_nascimento)/ 365.25)
	INTO STRICT   	nr_anos_w
	FROM 	prescr_medica a,
			pessoa_fisica b
	WHERE 	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	AND		a.nr_prescricao = nr_prescricao_p;

	IF (ie_status_atend_w <= 40) THEN

		SELECT  MAX(a.nr_seq_resultado),
				MAX(b.nr_seq_material)
		INTO STRICT	nr_seq_resultado_w,
				nr_seq_material_w
		FROM	exame_lab_resultado a,
				exame_lab_result_item b
		WHERE	a.nr_seq_resultado = b.nr_seq_resultado
		AND		a.nr_prescricao = nr_prescricao_p
		AND		b.nr_seq_prescr	= nr_seq_prescr_w
		AND		(b.nr_seq_material IS NOT NULL AND b.nr_seq_material::text <> '');

		if (coalesce(nr_seq_resultado_w::text, '') = '') then
			begin
			select 	MAX(nr_sequencia)
			into STRICT 	nr_seq_result_lab_w
			from 	result_laboratorio
			where	nr_prescricao	= nr_prescricao_p
			and		nr_seq_prescricao = nr_seq_prescr_w;
			
			if (coalesce(nr_seq_result_lab_w::text, '') = '') then
				begin
				SELECT * FROM Lab_insere_result_lab_pdf(nr_prescricao_p, nr_seq_prescr_w, ' ', nm_usuario_p, ds_erro_w, nr_seq_result_lab_w) INTO STRICT ds_erro_w, nr_seq_result_lab_w;
				end;
			end if;
			end;
		end if;

		IF (coalesce(nr_seq_resultado_w::text, '') = '') THEN
			CALL gera_exame_result_lab(nr_prescricao_p, nr_seq_prescr_w, 'N', 'N', 'S', nm_usuario_p);

			SELECT 	MAX(a.nr_seq_resultado),
					MAX(b.nr_seq_material)
			INTO STRICT	nr_seq_resultado_w,
					nr_seq_material_w
			FROM	exame_lab_resultado a,
					exame_lab_result_item b
			WHERE	a.nr_seq_resultado = b.nr_seq_resultado
			AND		a.nr_prescricao = nr_prescricao_p
			AND		b.nr_seq_prescr	= nr_seq_prescr_w
			AND 	(b.nr_seq_material IS NOT NULL AND b.nr_seq_material::text <> '');
		END IF;WITH RECURSIVE cte AS (


		SELECT	MAX(coalesce(e.cd_exame_integracao, e.cd_exame)),MAX(e.nr_seq_exame)
		INTO STRICT 	cd_analito_w,nr_seq_analito_w
		FROM	exame_laboratorio e WHERE nr_seq_superior = nr_seq_exame_princ_w
  UNION ALL


		SELECT	MAX(coalesce(e.cd_exame_integracao, e.cd_exame)),MAX(e.nr_seq_exame)
		INTO STRICT 	cd_analito_w,nr_seq_analito_w
		FROM	exame_laboratorio e JOIN cte c ON (c.nr_seq_exame = e.nr_seq_superior)

) SELECT * FROM cte WHERE coalesce(coalesce(e.cd_exame_integracao, e.cd_exame), e.nr_seq_exame) = cd_exame_p;
;

		CALL gravar_log_lab(1234, 'atualizar_lab_result_item ('||nr_prescricao_p||', '||nr_seq_prescr_w||', nvl('||cd_analito_w||','||cd_exame_p||'), '||ds_resultado_p||', 0, '||ds_resultado_p||', , null, n, eclipse, to_date('||ds_date_time_obs_p||', yyyymmddhh24miss), '||ds_referencia_p||', '||ds_units_p||', null, null, ds_erro_w, null);',nm_usuario_p);

		SELECT	MAX(ie_formato_resultado)
		INTO STRICT	ie_formato_resultado_w
		FROM	exame_laboratorio
		WHERE	nr_seq_exame	 = coalesce(nr_seq_analito_w, nr_seq_exame_princ_w);

		IF (SUBSTR(ie_formato_resultado_w,1,1) = 'P') THEN
			SELECT 	(coalesce(REPLACE(ds_resultado_p, '.', ','),0))::numeric
			INTO STRICT   	pr_resultado_w
			;
		ELSIF (SUBSTR(ie_formato_resultado_w,1,1) = 'V') OR (SUBSTR(ie_formato_resultado_w,2,1) = 'V') THEN
			BEGIN
			SELECT 	(coalesce(ds_resultado_p,0))::numeric
			INTO STRICT 	qt_resultado_w
			;
			EXCEPTION
				WHEN OTHERS THEN
				BEGIN
				SELECT 	(coalesce(REPLACE(ds_resultado_p, '.', ','),0))::numeric
				INTO STRICT 	qt_resultado_w
				;
				EXCEPTION
					WHEN OTHERS THEN
						BEGIN
						SELECT 	(coalesce(REPLACE(ds_resultado_p, ',', '.'),0))::numeric
						INTO STRICT 	qt_resultado_w
						;

						EXCEPTION
							WHEN OTHERS THEN
							ds_resultado_w := trim(both ds_resultado_p);
						END;
				END;
			END;
		ELSE
			ds_resultado_w := trim(both ds_resultado_p);
		END IF;

	  ds_erro_w := atualizar_lab_result_item(nr_prescricao_p, nr_seq_prescr_w, coalesce(cd_analito_w,cd_exame_princ_w), qt_resultado_w, pr_resultado_w, ds_resultado_w, ds_observacao_p, NULL, 'N', nm_usuario_p, TO_DATE(ds_date_time_obs_p, 'yyyymmddhh24miss'), ds_referencia_p, ds_units_p, NULL, NULL, ds_erro_w, nr_seq_analito_w);

		COMMIT;
	END IF;

end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_resultado_exame (nr_prescricao_p text, cd_exame_p text, nr_seq_interno_p text, ds_tipo_resultado_p text, ds_resultado_p text, ds_observacao_p text, ds_referencia_p text, ds_result_status_p text, ds_date_time_obs_p text, ds_units_p text, nm_usuario_p text, cd_exame_princ_p INOUT text) FROM PUBLIC;

