CREATE OR REPLACE PROCEDURE gerar_resultado_vancocinemia ( nm_usuario_p text, nr_atendimento_p bigint, nr_prescricao_p bigint, ie_novo_resultado_p text, dt_resultado_p timestamp, qt_nivel_serico_p bigint, ie_commit_p text) AS $body$
DECLARE


nr_atendimento_w		bigint;

BEGIN

nr_atendimento_w	:= coalesce(nr_atendimento_p,0);

if (nr_atendimento_w = 0) and (coalesce(nr_prescricao_p,0) > 0) then
	select	coalesce(max(nr_atendimento),0)
	into STRICT	nr_atendimento_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;
end if;

if (nr_atendimento_w > 0) and (ie_novo_resultado_p IS NOT NULL AND ie_novo_resultado_p::text <> '') and (qt_nivel_serico_p IS NOT NULL AND qt_nivel_serico_p::text <> '') then
	insert into REP_result_vanco(
					nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_atendimento,
					dt_resultado,
					ie_novo_resultado,
					qt_nivel_serico
				) values (
					nextval('rep_result_vanco_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_atendimento_w,
					dt_resultado_p,
					ie_novo_resultado_p,
					qt_nivel_serico_p);
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
