SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_resumo_acuidade (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_registro_w		varchar(30);
vl_od_pl_av_cc_w	varchar(40);
vl_od_pl_av_sc_w	varchar(40);
vl_oe_pl_av_cc_w	varchar(40);
vl_oe_pl_av_sc_w	varchar(40);
vl_od_pp_av_w		varchar(40);
vl_oe_pp_av_w		varchar(40);
ds_acuidade_visual_w	varchar(32000);	
ds_observacao_w		varchar(1000);	
nm_profissional_w	varchar(255);
ds_espaco_20_w		varchar(255) 	:= lpad(' ',1,' ');
ds_enter_w		varchar(30)	:= chr(13) || chr(10);	
ds_quebra_w		varchar(255) 	:= chr(13) || chr(10);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(327202, null, wheb_usuario_pck.get_nr_seq_idioma);--DATA: 
expressao2_w	varchar(255) := obter_desc_expressao_idioma(728918, null, wheb_usuario_pck.get_nr_seq_idioma);--PROFISSIONAL: 
expressao3_w	varchar(255) := obter_desc_expressao_idioma(603813, null, wheb_usuario_pck.get_nr_seq_idioma);--PARA LONGE 
expressao4_w	varchar(255) := obter_desc_expressao_idioma(774098, null, wheb_usuario_pck.get_nr_seq_idioma);--OD  AV/SC 
expressao5_w	varchar(255) := obter_desc_expressao_idioma(294731, null, wheb_usuario_pck.get_nr_seq_idioma);--OD  AV/CC 
expressao6_w	varchar(255) := obter_desc_expressao_idioma(774103, null, wheb_usuario_pck.get_nr_seq_idioma);--OE  AV/SC 
expressao7_w	varchar(255) := obter_desc_expressao_idioma(294749, null, wheb_usuario_pck.get_nr_seq_idioma);--OE  AV/CC 
expressao8_w	varchar(255) := obter_desc_expressao_idioma(774104, null, wheb_usuario_pck.get_nr_seq_idioma);--PARA PERTO 
expressao9_w	varchar(255) := obter_desc_expressao_idioma(294729, null, wheb_usuario_pck.get_nr_seq_idioma);--OD  AV 
expressao10_w	varchar(255) := obter_desc_expressao_idioma(294747, null, wheb_usuario_pck.get_nr_seq_idioma);--OE  AV 
expressao11_w	varchar(255) := obter_desc_expressao_idioma(294639, null, wheb_usuario_pck.get_nr_seq_idioma);--OBSERVAÇÃO 
C01 CURSOR FOR 
SELECT to_char(dt_registro,'dd/mm/yyyy hh24:mi:ss'), 
	  obter_nome_pf(cd_profissional) nm_profissional, 
	  obter_desc_tipo_acuidade(vl_od_pl_av_cc) vl_od_pl_av_cc, 
	  obter_desc_tipo_acuidade(vl_od_pl_av_sc) vl_od_pl_av_sc, 
	  obter_desc_tipo_acuidade(vl_oe_pl_av_cc) vl_oe_pl_av_cc, 
	  obter_desc_tipo_acuidade(vl_oe_pl_av_sc) vl_oe_pl_av_sc, 
	  obter_desc_tipo_acuidade(vl_od_pp_av) vl_od_pp_av, 
	  obter_desc_tipo_acuidade(vl_oe_pp_av) vl_oe_pp_av, 
	  ds_observacao 
from  oft_acuidade_visual 
WHERE 	((nr_seq_consulta = nr_seq_consulta_p) OR ((ie_mostra_todos_p = 'S') AND nr_seq_consulta IN (SELECT c.nr_sequencia 
                                    	 	 	   FROM  atendimento_paciente b, 
                                       				   oft_consulta c 
                                    				   WHERE  c.nr_atendimento = b.nr_atendimento 
                                    			   AND  b.cd_pessoa_fisica = cd_pessoa_fisica_p))) 
and	coalesce(ie_situacao,'A') = 'A' 
order  by dt_registro desc;

 

BEGIN 
	 
open C01;
loop 
	fetch C01 into 
		dt_registro_w,		  
		nm_profissional_w, 
		vl_od_pl_av_cc_w, 
		vl_od_pl_av_sc_w, 
		vl_oe_pl_av_cc_w, 
		vl_oe_pl_av_sc_w, 
		vl_od_pp_av_w, 
		vl_oe_pp_av_w, 
		ds_observacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_acuidade_visual_w	:=	'';
	ds_acuidade_visual_w	:= 	ds_acuidade_visual_w || expressao1_w || ' ' || dt_registro_w || ds_espaco_20_w || 
								expressao2_w || ' ' ||nm_profissional_w || ds_enter_w ||ds_enter_w;
	ds_acuidade_visual_w	:= 	ds_acuidade_visual_w || expressao3_w ||	ds_enter_w;						
 
	if (vl_od_pl_av_cc_w IS NOT NULL AND vl_od_pl_av_cc_w::text <> '') or (vl_od_pl_av_sc_w IS NOT NULL AND vl_od_pl_av_sc_w::text <> '') then 
		ds_acuidade_visual_w	:= ds_acuidade_visual_w ||lpad(expressao4_w || ': ',15,' ')||vl_od_pl_av_sc_w ||ds_espaco_20_w;
		ds_acuidade_visual_w	:= ds_acuidade_visual_w ||lpad(expressao5_w || ': ',15,' ')||vl_od_pl_av_cc_w ||ds_enter_w;
		 
	end if;
	 
	if (vl_oe_pl_av_cc_w IS NOT NULL AND vl_oe_pl_av_cc_w::text <> '') or (vl_oe_pl_av_sc_w IS NOT NULL AND vl_oe_pl_av_sc_w::text <> '') then 
		ds_acuidade_visual_w	:= ds_acuidade_visual_w ||lpad(expressao6_w || ': ',15,' ')||vl_oe_pl_av_sc_w ||ds_espaco_20_w;
		ds_acuidade_visual_w	:= ds_acuidade_visual_w ||lpad(expressao7_w ||': ',15,' ')||vl_oe_pl_av_cc_w ||ds_enter_w;
		 
	end if;
	 
	ds_acuidade_visual_w	:= 	ds_acuidade_visual_w || ds_enter_w|| expressao8_w ||ds_enter_w;						
	 
	if (vl_od_pp_av_w IS NOT NULL AND vl_od_pp_av_w::text <> '') or (vl_oe_pp_av_w IS NOT NULL AND vl_oe_pp_av_w::text <> '') then 
		ds_acuidade_visual_w	:= ds_acuidade_visual_w ||lpad(expressao9_w || ': ',15,' ')||vl_od_pp_av_w ||ds_enter_w;
		ds_acuidade_visual_w	:= ds_acuidade_visual_w ||lpad(expressao10_w || ': ',15,' ')||vl_oe_pp_av_w ||ds_enter_w;
	end if;
	ds_acuidade_visual_w := ds_acuidade_visual_w || ds_enter_w || expressao11_w || ': ' || ds_observacao_w ||ds_enter_w;
	ds_acuidade_visual_w := ds_acuidade_visual_w;
	end;
	 
	CALL gravar_registro_resumo_oft(ds_acuidade_visual_w,nr_seq_item_p,nm_usuario_p,to_date(dt_registro_w,'dd/mm/yyyy hh24:mi:ss'));
	 
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
