SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_resumo_anamnese (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nm_profissional_w	varchar(255);
dt_anamnese_w		varchar(30);
ds_anamnese_w		varchar(1000);	
ds_historia_ocular_w	varchar(1000);
ds_medicamentos_w	varchar(1000);	
ds_alergia_w		varchar(1000);
ds_ger_anamnese_w	varchar(32000);
ds_espaco_20_w		varchar(255) 	:= lpad(' ',1,' ');
ds_enter_w		varchar(30)	:= chr(13) || chr(10);	
 
expressao1_w	varchar(255) := obter_desc_expressao_idioma(343703, null, wheb_usuario_pck.get_nr_seq_idioma);--DATA 
expressao2_w	varchar(255) := obter_desc_expressao_idioma(728918, null, wheb_usuario_pck.get_nr_seq_idioma);--PROFISSIONAL: 
expressao3_w	varchar(255) := obter_desc_expressao_idioma(297166, null, wheb_usuario_pck.get_nr_seq_idioma);--QUEIXA 
expressao4_w	varchar(255) := obter_desc_expressao_idioma(774109, null, wheb_usuario_pck.get_nr_seq_idioma);--HIST. OFTALMOLÃ“GICA 
expressao5_w	varchar(255) := obter_desc_expressao_idioma(774110, null, wheb_usuario_pck.get_nr_seq_idioma);--MEDICAMENTOS 
expressao6_w	varchar(255) := obter_desc_expressao_idioma(283346, null, wheb_usuario_pck.get_nr_seq_idioma);--ALERGIAS 
C01 CURSOR FOR 
	SELECT 	obter_nome_pf(cd_profissional) nm_profissional, 
		to_char(dt_anamnese,'dd/mm/yyyy hh24:mi:ss') dt_anamnese, 
		substr(ds_anamnese,1,1000), 
		substr(ds_historia_ocular,1,1000), 
		substr(ds_medicamentos,1,1000), 
		substr(ds_alergia,1,1000) 
	from  	oft_anamnese 
	WHERE 	((nr_seq_consulta = nr_seq_consulta_p) OR ((ie_mostra_todos_p = 'S') AND nr_seq_consulta IN (SELECT c.nr_sequencia 
													   FROM  atendimento_paciente b, 
														   oft_consulta c 
													   WHERE  c.nr_atendimento = b.nr_atendimento 
													   AND  b.cd_pessoa_fisica = cd_pessoa_fisica_p))) 
	and	coalesce(ie_situacao,'A') = 'A'												   
	order 	by dt_anamnese desc;


BEGIN 
 
open C01;
loop 
	fetch C01 into 
		nm_profissional_w, 
		dt_anamnese_w, 
		ds_anamnese_w, 
		ds_historia_ocular_w, 
		ds_medicamentos_w, 
		ds_alergia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_ger_anamnese_w	:=	'';
	ds_ger_anamnese_w	:= 	substr(ds_ger_anamnese_w || upper(expressao1_w) || ': '|| dt_anamnese_w || ds_espaco_20_w || 
							upper(expressao2_w) || ' ' ||nm_profissional_w || ds_enter_w || ds_enter_w || 
							upper(expressao3_w) || ': ' || ds_anamnese_w ||ds_enter_w|| ds_enter_w || 
							upper(expressao4_w) || ': ' || ds_historia_ocular_w || ds_enter_w|| ds_enter_w || 
							upper(expressao5_w) || ': ' || ds_medicamentos_w || ds_enter_w|| ds_enter_w || 
							upper(expressao6_w) || ': ' || ds_alergia_w || ds_enter_w||ds_enter_w,1,32000);
							 
		 
	 
 
	CALL gravar_registro_resumo_oft(ds_ger_anamnese_w,nr_seq_item_p,nm_usuario_p,to_date(dt_anamnese_w,'dd/mm/yyyy hh24:mi:ss'));
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
