CREATE OR REPLACE PROCEDURE gerar_resumo_auto_refracao (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nm_profissional_w	varchar(255);
ds_espaco_20_w		varchar(255) 	:= lpad(' ',1,' ');
ds_enter_w		varchar(30)	:= chr(13) || chr(10);	
ds_observacao_w		varchar(1000);
dt_registro_w		varchar(30);
ds_auto_refracao_w	varchar(32000);
vl_od_ard_esf_w		varchar(10);
vl_od_ard_cil_w		varchar(10);
vl_od_ard_eixo_w		varchar(10);
qt_reflexo_od_w		varchar(10);
vl_oe_ard_esf_w		varchar(10);
vl_oe_ard_cil_w		varchar(10);
vl_oe_ard_eixo_w		varchar(10);	
qt_reflexo_are_od_w 	varchar(10);
vl_oe_are_eixo_w		varchar(10);	
qt_reflexo_are_oe_w	varchar(10);
qt_reflexo_oe_w		varchar(10);
qt_distancia_np_w		varchar(10);	
vl_od_are_esf_w		varchar(10);
vl_od_lenso_esf_w 	real;
vl_od_lenso_cil_w	real;
vl_od_lenso_eixo_w	smallint;
vl_adicao_lanso_w 	real;	
vl_oe_lenso_esf_w	real;
vl_oe_lenso_cil_w	real;
vl_oe_lenso_eixo_w	smallint;
vl_oe_are_esf_w		varchar(10);
vl_od_are_cil_w		varchar(10);
vl_od_are_eixo_w		varchar(10);
vl_oe_are_cil_w		varchar(10);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(327202, null, wheb_usuario_pck.get_nr_seq_idioma);--DATA: 
expressao2_w	varchar(255) := obter_desc_expressao_idioma(728918, null, wheb_usuario_pck.get_nr_seq_idioma);--PROFISSIONAL: 
expressao3_w	varchar(255) := obter_desc_expressao_idioma(284058, null, wheb_usuario_pck.get_nr_seq_idioma);--AUTO REFRAÇÃO DINÂMICA 
expressao4_w	varchar(255) := obter_desc_expressao_idioma(294739, null, wheb_usuario_pck.get_nr_seq_idioma);--OD DE 
expressao5_w	varchar(255) := obter_desc_expressao_idioma(347654, null, wheb_usuario_pck.get_nr_seq_idioma);--DC 
expressao6_w	varchar(255) := obter_desc_expressao_idioma(289084, null, wheb_usuario_pck.get_nr_seq_idioma);--EIXO 
expressao7_w	varchar(255) := obter_desc_expressao_idioma(774164, null, wheb_usuario_pck.get_nr_seq_idioma);--Reflexo 
expressao8_w	varchar(255) := obter_desc_expressao_idioma(288080, null, wheb_usuario_pck.get_nr_seq_idioma);--DNP 
expressao9_w	varchar(255) := obter_desc_expressao_idioma(294758, null, wheb_usuario_pck.get_nr_seq_idioma);--OE DE 
expressao10_w	varchar(255) := obter_desc_expressao_idioma(284059, null, wheb_usuario_pck.get_nr_seq_idioma);--AUTO REFRAÇÃO ESTÁTICA 
expressao11_w	varchar(255) := obter_desc_expressao_idioma(292498, null, wheb_usuario_pck.get_nr_seq_idioma);--Lensometria 
expressao12_w	varchar(255) := obter_desc_expressao_idioma(283205, null, wheb_usuario_pck.get_nr_seq_idioma);--Adição 
expressao13_w	varchar(255) := obter_desc_expressao_idioma(294639, null, wheb_usuario_pck.get_nr_seq_idioma);--OBSERVAÇÃO 
C01 CURSOR FOR 
SELECT obter_nome_pf(cd_profissional) nm_profissional, 
	  to_char(dt_exame,'dd/mm/yyyy hh24:mi:ss'), 
	  vl_od_ard_esf, 
	  vl_od_ard_cil, 
	  vl_od_ard_eixo, 
	  qt_reflexo_od, 
	  vl_oe_ard_esf, 
	  vl_oe_ard_cil, 
	  vl_oe_ard_eixo, 
	  qt_reflexo_oe, 
	  qt_distancia_np, 
	  vl_od_are_esf, 
	  vl_od_are_cil, 
	  vl_od_are_eixo, 
	  qt_reflexo_are_od, 
	  vl_oe_are_esf, 
	  vl_oe_are_cil, 
	  vl_oe_are_eixo, 
	  qt_reflexo_are_oe, 
	  ds_observacao, 
	  vl_od_lenso_esf, 
	  vl_od_lenso_cil, 
	  vl_od_lenso_eixo, 
	  vl_adicao, 
	  vl_oe_lenso_esf, 
	  vl_oe_lenso_cil, 
	  vl_oe_lenso_eixo	 
from  oft_auto_refracao 
WHERE 	((nr_seq_consulta = nr_seq_consulta_p) OR ((ie_mostra_todos_p = 'S') AND nr_seq_consulta IN (SELECT c.nr_sequencia 
                                    	 	 	   FROM  atendimento_paciente b, 
                                       				   oft_consulta c 
                                    				   WHERE  c.nr_atendimento = b.nr_atendimento 
                                    			   AND  b.cd_pessoa_fisica = cd_pessoa_fisica_p))) 
and  coalesce(ie_situacao,'A') = 'A'	  	  
order by dt_exame desc;


BEGIN 
 
open C01;
loop 
	fetch C01 into 
		nm_profissional_w, 
		dt_registro_w, 
		vl_od_ard_esf_w, 
		vl_od_ard_cil_w, 
		vl_od_ard_eixo_w, 
		qt_reflexo_od_w, 
		vl_oe_ard_esf_w, 
		vl_oe_ard_cil_w, 
		vl_oe_ard_eixo_w, 
		qt_reflexo_oe_w, 
		qt_distancia_np_w, 
		vl_od_are_esf_w, 
		vl_od_are_cil_w, 
		vl_od_are_eixo_w, 
		qt_reflexo_are_od_w, 
		vl_oe_are_esf_w, 
		vl_oe_are_cil_w, 
		vl_oe_are_eixo_w, 
		qt_reflexo_are_oe_w, 
		ds_observacao_w, 
		vl_od_lenso_esf_w, 	 
		vl_od_lenso_cil_w,	 
		vl_od_lenso_eixo_w,	 
		vl_adicao_lanso_w, 
		vl_oe_lenso_esf_w,	 
		vl_oe_lenso_cil_w,	 
		vl_oe_lenso_eixo_w;	
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_auto_refracao_w	:= '';
	ds_auto_refracao_w	:= ds_auto_refracao_w || upper(expressao1_w) || ' ' || dt_registro_w || ds_espaco_20_w || 
							upper(expressao2_w) || ' ' ||nm_profissional_w || ds_enter_w || ds_enter_w;
	ds_auto_refracao_w	:= ds_auto_refracao_w ||upper(expressao3_w)||ds_enter_w;	
	if (vl_od_ard_esf_w IS NOT NULL AND vl_od_ard_esf_w::text <> '') or (vl_od_ard_cil_w IS NOT NULL AND vl_od_ard_cil_w::text <> '') or (vl_od_ard_eixo_w IS NOT NULL AND vl_od_ard_eixo_w::text <> '') or (qt_reflexo_od_w IS NOT NULL AND qt_reflexo_od_w::text <> '')	then 
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao4_w) || ': ',12,' ')|| oft_obter_valor_com_sinal(vl_od_ard_esf_w);
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao5_w) || ': ',12,' ')||oft_obter_valor_com_sinal_neg(vl_od_ard_cil_w);
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao6_w) || ': ',12,' ')||vl_od_ard_eixo_w;
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(expressao7_w || ': ',12,' ')||qt_reflexo_od_w;
	end if;
	 
	if (qt_distancia_np_w IS NOT NULL AND qt_distancia_np_w::text <> '') then 
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao8_w) || ': ',10,' ')||qt_distancia_np_w ||ds_enter_w;
	else		 
		ds_auto_refracao_w	:= ds_auto_refracao_w ||ds_enter_w;
	end if;	
	 
	if (vl_oe_ard_esf_w IS NOT NULL AND vl_oe_ard_esf_w::text <> '') or (vl_oe_ard_cil_w IS NOT NULL AND vl_oe_ard_cil_w::text <> '') or (vl_oe_ard_eixo_w IS NOT NULL AND vl_oe_ard_eixo_w::text <> '') or (qt_reflexo_oe_w IS NOT NULL AND qt_reflexo_oe_w::text <> '')	then 
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao9_w) || ': ',12,' ')||oft_obter_valor_com_sinal(vl_oe_ard_esf_w);
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao5_w) || ': ',12,' ')||oft_obter_valor_com_sinal_neg(vl_oe_ard_cil_w);
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao6_w) || ': ',12,' ')||vl_oe_ard_eixo_w;
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(expressao7_w || ': ',12,' ')||qt_reflexo_oe_w ||ds_enter_w;
	end if;
	 
	ds_auto_refracao_w	:= ds_auto_refracao_w || ds_enter_w|| upper(expressao10_w)||ds_enter_w;	
	 
	if (vl_od_are_esf_w IS NOT NULL AND vl_od_are_esf_w::text <> '') or (vl_od_are_cil_w IS NOT NULL AND vl_od_are_cil_w::text <> '') or (vl_od_are_eixo_w IS NOT NULL AND vl_od_are_eixo_w::text <> '') or (qt_reflexo_are_od_w IS NOT NULL AND qt_reflexo_are_od_w::text <> '')	then 
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao4_w) || ': ',12,' ')||oft_obter_valor_com_sinal(vl_od_are_esf_w);
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao5_w) || ': ',12,' ')||oft_obter_valor_com_sinal_neg(vl_od_are_cil_w);
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao6_w) || ': ',12,' ')||vl_od_are_eixo_w;
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(expressao7_w || ': ',12,' ')||qt_reflexo_are_od_w ||ds_enter_w;
	end if;
	 
	if (vl_oe_are_esf_w IS NOT NULL AND vl_oe_are_esf_w::text <> '') or (vl_oe_are_cil_w IS NOT NULL AND vl_oe_are_cil_w::text <> '') or (vl_oe_are_eixo_w IS NOT NULL AND vl_oe_are_eixo_w::text <> '') or (qt_reflexo_are_oe_w IS NOT NULL AND qt_reflexo_are_oe_w::text <> '')	then 
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(expressao9_w || ': ',12,' ')||oft_obter_valor_com_sinal(vl_oe_are_esf_w);
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao5_w) || ': ',12,' ')||oft_obter_valor_com_sinal_neg(vl_oe_are_cil_w);
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao6_w) || ': ',12,' ')||vl_oe_are_eixo_w;
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(expressao7_w || ': ',12,' ')||qt_reflexo_are_oe_w ||ds_enter_w;
	end if;
	 
	ds_auto_refracao_w	:= ds_auto_refracao_w || ds_enter_w|| upper(expressao11_w) ||ds_enter_w;	
	 
	if (vl_od_lenso_esf_w IS NOT NULL AND vl_od_lenso_esf_w::text <> '') or (vl_od_lenso_cil_w IS NOT NULL AND vl_od_lenso_cil_w::text <> '') or (vl_od_lenso_eixo_w IS NOT NULL AND vl_od_lenso_eixo_w::text <> '') or (vl_adicao_lanso_w IS NOT NULL AND vl_adicao_lanso_w::text <> '')	then 
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao4_w) || ': ',12,' ')||oft_obter_valor_com_sinal(vl_od_lenso_esf_w);
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao5_w) || ': ',12,' ')||oft_obter_valor_com_sinal_neg(vl_od_lenso_cil_w);
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao6_w) || ': ',12,' ')||vl_od_lenso_eixo_w;
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao12_w) || ': ',12,' ')||to_char(vl_adicao_lanso_w, 'fm999990.00') ||ds_enter_w;
	end if;
	 
	if (vl_oe_lenso_esf_w IS NOT NULL AND vl_oe_lenso_esf_w::text <> '') or (vl_oe_lenso_cil_w IS NOT NULL AND vl_oe_lenso_cil_w::text <> '') or (vl_oe_lenso_eixo_w IS NOT NULL AND vl_oe_lenso_eixo_w::text <> '') then 
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao9_w) || ': ',12,' ')|| oft_obter_valor_com_sinal(vl_oe_lenso_esf_w);
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao5_w) || ': ',12,' ')|| oft_obter_valor_com_sinal_neg(vl_oe_lenso_cil_w);
		ds_auto_refracao_w	:= ds_auto_refracao_w ||lpad(upper(expressao6_w) || ': ',12,' ')|| vl_oe_lenso_eixo_w ||ds_enter_w;
	end if;
	 
	if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then 
		ds_auto_refracao_w	:= ds_auto_refracao_w || ds_enter_w || upper(expressao13_w) || ': ' || ds_observacao_w ||ds_enter_w;
	end if;
	 
	ds_auto_refracao_w 	:= ds_auto_refracao_w;
	 
	CALL gravar_registro_resumo_oft(ds_auto_refracao_w,nr_seq_item_p,nm_usuario_p,to_date(dt_registro_w,'dd/mm/yyyy hh24:mi:ss'));
		 
	end;
end loop;
close C01;	
 
CALL gerar_resumo_refracao(cd_estabelecimento_p, 
			nr_seq_consulta_p,	 
			cd_pessoa_fisica_p, 
			ie_mostra_todos_p, 
			nr_seq_item_p, 
			nm_usuario_p);
	 
commit;
 
end;
$body$
