SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_resumo_biomicroscopia (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE



nm_profissional_w	varchar(255);
ds_espaco_20_w		varchar(255) 	:=  lpad(' ',1,' ');
ds_enter_w		varchar(30)	:=  chr(13) || chr(10);	
ds_observacao_w		oft_biomicroscopia.ds_observacao%type;	
dt_registro_w		varchar(30);
biomicroscopia_w	varchar(32000);
ds_biomicroscopia_oe_w 	varchar(1000);
ds_biomicroscopia_od_w	varchar(1000);
ds_segmento_w		varchar(255);


C01 CURSOR FOR
SELECT obter_nome_pf(cd_profissional) nm_profissional,
	   to_char(dt_exame,'dd/mm/yyyy hh24:mi:ss'),
	   ds_biomicroscopia_oe,
	   ds_biomicroscopia_od,
	   ds_observacao,
	   substr(OBTER_VALOR_DOMINIO(3738,ie_segmento),1,255) ds_segmento
from   oft_biomicroscopia
WHERE  	((nr_seq_consulta = nr_seq_consulta_p) OR ((ie_mostra_todos_p = 'S') AND  nr_seq_consulta IN (SELECT c.nr_sequencia 
                                                                        	 	  	      FROM    atendimento_paciente b, 
                                                                              				      oft_consulta c 
                                                                       				      WHERE   c.nr_atendimento = b.nr_atendimento 
                                                                        			      AND    b.cd_pessoa_fisica = cd_pessoa_fisica_p)))
and    coalesce(ie_situacao,'A') = 'A'	   	   
order  by dt_exame desc;


BEGIN

open C01;
loop
	fetch C01 into
		nm_profissional_w,
		dt_registro_w,
		ds_biomicroscopia_oe_w,
		ds_biomicroscopia_od_w,
		ds_observacao_w,
		ds_segmento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	biomicroscopia_w	:= '';
	biomicroscopia_w	:=  upper(wheb_mensagem_pck.get_texto(308491)) || ': ' || dt_registro_w || ds_espaco_20_w || -- DATA
				    upper(wheb_mensagem_pck.get_texto(307473)) || ': ' ||nm_profissional_w || ds_enter_w ||ds_enter_w; -- PROFISSIONAL
	biomicroscopia_w	:= substr(biomicroscopia_w ||ds_segmento_w || ds_enter_w||ds_enter_w,1,4000);
	biomicroscopia_w	:= substr(biomicroscopia_w ||lpad(wheb_mensagem_pck.get_texto(308495) || ': ',19,' ')||ds_biomicroscopia_od_w || ds_enter_w||ds_enter_w,1,4000); -- Biomicroscopia OD
	biomicroscopia_w	:= substr(biomicroscopia_w ||lpad(wheb_mensagem_pck.get_texto(308497) || ': ',19,' ')||ds_biomicroscopia_oe_w || ds_enter_w,1,4000);	-- Biomicroscopia OE
	biomicroscopia_w 	:= substr(biomicroscopia_w || ds_enter_w || upper(wheb_mensagem_pck.get_texto(203221)) || ': ' || ds_observacao_w ||ds_enter_w,1,4000); -- OBSERVAÇÃO
	
	CALL gravar_registro_resumo_oft(biomicroscopia_w,nr_seq_item_p,nm_usuario_p,to_date(dt_registro_w,'dd/mm/yyyy hh24:mi:ss'));
	end;
end loop;
close C01;
	

	
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_resumo_biomicroscopia (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) FROM PUBLIC;

