SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_resumo_correcao (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nm_profissional_w	varchar(255);
ds_espaco_20_w		varchar(255) 	:= lpad(' ',1,' ');
ds_enter_w		varchar(30)	:= chr(13) || chr(10);	
dt_registro_w		varchar(30);
ds_tipo_lente_w		varchar(80);
qt_tempo_uso_w		varchar(10);
ds_unidade_tempo_w	varchar(255);
vl_od_pl_dio_esf_w	varchar(10);
vl_od_pl_dio_cil_w	varchar(10);
vl_od_pl_eixo_w		varchar(10);
vl_od_pl_adicao_w	varchar(10);
vl_oe_pl_dio_esf_w	varchar(10);
vl_oe_pl_dio_cil_w	varchar(10);
vl_oe_pl_eixo_w		varchar(10);
vl_od_pp_dio_esf_w	varchar(10);
vl_od_pp_dio_cil_w	varchar(10);
vl_od_pp_eixo_w		varchar(10);
vl_oe_pp_dio_esf_w	varchar(10);
vl_oe_pp_dio_cil_w	varchar(10);
vl_oe_pp_eixo_w		varchar(10);
ds_observacao_w		varchar(1000);
ds_correcao_optica_w	varchar(32000);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(327202, null, wheb_usuario_pck.get_nr_seq_idioma);--DATA: 
expressao2_w	varchar(255) := obter_desc_expressao_idioma(728918, null, wheb_usuario_pck.get_nr_seq_idioma);--PROFISSIONAL: 
expressao3_w	varchar(255) := obter_desc_expressao_idioma(628224, null, wheb_usuario_pck.get_nr_seq_idioma);--TIPO DE LENTE 
expressao4_w	varchar(255) := obter_desc_expressao_idioma(764599, null, wheb_usuario_pck.get_nr_seq_idioma);--TEMPO DE USO 
expressao5_w	varchar(255) := obter_desc_expressao_idioma(295264, null, wheb_usuario_pck.get_nr_seq_idioma);--PARA LONGE 
expressao6_w	varchar(255) := obter_desc_expressao_idioma(294739, null, wheb_usuario_pck.get_nr_seq_idioma);--OD DE 
expressao7_w	varchar(255) := obter_desc_expressao_idioma(347654, null, wheb_usuario_pck.get_nr_seq_idioma);--DC 
expressao8_w	varchar(255) := obter_desc_expressao_idioma(289084, null, wheb_usuario_pck.get_nr_seq_idioma);--EIXO 
expressao9_w	varchar(255) := obter_desc_expressao_idioma(283205, null, wheb_usuario_pck.get_nr_seq_idioma);--ADIÇÃO 
expressao10_w	varchar(255) := obter_desc_expressao_idioma(294758, null, wheb_usuario_pck.get_nr_seq_idioma);--OE DE 
expressao11_w varchar(255) := obter_desc_expressao_idioma(295266, null, wheb_usuario_pck.get_nr_seq_idioma);--PARA PERTO 
expressao12_w	varchar(255) := obter_desc_expressao_idioma(294639, null, wheb_usuario_pck.get_nr_seq_idioma);--observação 
C01 CURSOR FOR 
SELECT obter_nome_pf(cd_profissional) nm_profissional, 
	  to_char(dt_registro,'dd/mm/yyyy hh24:mi:ss'), 
	  obter_desc_tipo_lente(nr_seq_tipo_lente) ds_tipo_lente, 
	  qt_tempo_uso, 
	  obter_valor_dominio(1913,ie_unidade_tempo) ds_unidade_tempo, 
	  vl_od_pl_dio_esf, 
	  vl_od_pl_dio_cil, 
	  vl_od_pl_eixo, 
	  vl_od_pl_adicao, 
	  vl_oe_pl_dio_esf, 
	  vl_oe_pl_dio_cil, 
	  vl_oe_pl_eixo, 
	  vl_od_pp_dio_esf, 
	  vl_od_pp_dio_cil, 
	  vl_od_pp_eixo, 
	  vl_oe_pp_dio_esf, 
	  vl_oe_pp_dio_cil, 
	  vl_oe_pp_eixo, 
	  ds_observacao 
from  oft_correcao_atual 
WHERE 	((nr_seq_consulta = nr_seq_consulta_p) OR ((ie_mostra_todos_p = 'S') AND nr_seq_consulta IN (SELECT c.nr_sequencia 
                                    	 	 	   FROM  atendimento_paciente b, 
                                       				   oft_consulta c 
                                    				   WHERE  c.nr_atendimento = b.nr_atendimento 
                                    			   AND  b.cd_pessoa_fisica = cd_pessoa_fisica_p))) 
and	coalesce(ie_situacao,'A') = 'A' 
order  BY dt_registro desc;

BEGIN
 
open C01;
loop 
	fetch C01 into 
		nm_profissional_w, 
		dt_registro_w, 
		ds_tipo_lente_w, 
		qt_tempo_uso_w, 
		ds_unidade_tempo_w, 
		vl_od_pl_dio_esf_w, 
		vl_od_pl_dio_cil_w, 
		vl_od_pl_eixo_w, 
		vl_od_pl_adicao_w, 
		vl_oe_pl_dio_esf_w, 
		vl_oe_pl_dio_cil_w, 
		vl_oe_pl_eixo_w, 
		vl_od_pp_dio_esf_w, 
		vl_od_pp_dio_cil_w, 
		vl_od_pp_eixo_w, 
		vl_oe_pp_dio_esf_w, 
		vl_oe_pp_dio_cil_w, 
		vl_oe_pp_eixo_w, 
		ds_observacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_correcao_optica_w	:=	'';
	ds_correcao_optica_w	:= 	ds_correcao_optica_w || upper(expressao1_w) || ' ' || dt_registro_w || ds_espaco_20_w || 
								upper(expressao2_w) || ' ' ||nm_profissional_w || ds_enter_w ||ds_enter_w;
	ds_correcao_optica_w	:= ds_correcao_optica_w ||upper(expressao3_w) || ': ' ||ds_tipo_lente_w ||ds_enter_w;
	ds_correcao_optica_w	:= ds_correcao_optica_w ||upper(expressao4_w) || ' : ' ||qt_tempo_uso_w || ' ' || ds_unidade_tempo_w || ds_enter_w||ds_enter_w;
	 
	if (vl_od_pl_dio_esf_w IS NOT NULL AND vl_od_pl_dio_esf_w::text <> '') or (vl_od_pl_dio_cil_w IS NOT NULL AND vl_od_pl_dio_cil_w::text <> '') or (vl_od_pl_eixo_w IS NOT NULL AND vl_od_pl_eixo_w::text <> '') 	 or (vl_od_pl_adicao_w IS NOT NULL AND vl_od_pl_adicao_w::text <> '') then 
		ds_correcao_optica_w	:= ds_correcao_optica_w || upper(expressao5_w) || ds_enter_w;
		ds_correcao_optica_w	:= ds_correcao_optica_w ||lpad(upper(expressao6_w) || ': ',15,' ')||vl_od_pl_dio_esf_w;
		ds_correcao_optica_w	:= ds_correcao_optica_w ||lpad(upper(expressao7_w) || ': ',15,' ')||vl_od_pl_dio_cil_w;
		ds_correcao_optica_w	:= ds_correcao_optica_w ||lpad(upper(expressao8_w) || ': ',15,' ')||vl_od_pl_eixo_w;
		ds_correcao_optica_w	:= ds_correcao_optica_w ||lpad(upper(expressao9_w) || ': ',15,' ')||vl_od_pl_adicao_w ||ds_enter_w;
	end if;
	 
	if (vl_oe_pl_dio_esf_w IS NOT NULL AND vl_oe_pl_dio_esf_w::text <> '') or (vl_oe_pl_dio_cil_w IS NOT NULL AND vl_oe_pl_dio_cil_w::text <> '') or (vl_oe_pl_eixo_w IS NOT NULL AND vl_oe_pl_eixo_w::text <> '') 	 then 
		ds_correcao_optica_w	:= ds_correcao_optica_w ||lpad(upper(expressao10_w) || ': ',15,' ')||vl_oe_pl_dio_esf_w;
		ds_correcao_optica_w	:= ds_correcao_optica_w ||lpad(upper(expressao7_w) || ': ',15,' ')||vl_oe_pl_dio_cil_w;
		ds_correcao_optica_w	:= ds_correcao_optica_w ||lpad(upper(expressao8_w) || ': ',15,' ')||vl_oe_pl_eixo_w ||ds_enter_w;
	end if;
	 
	if (vl_od_pp_dio_esf_w IS NOT NULL AND vl_od_pp_dio_esf_w::text <> '') or (vl_od_pp_dio_cil_w IS NOT NULL AND vl_od_pp_dio_cil_w::text <> '') or (vl_od_pp_eixo_w IS NOT NULL AND vl_od_pp_eixo_w::text <> '') 	 then 
		ds_correcao_optica_w	:= ds_correcao_optica_w || ds_enter_w || upper(expressao11_w) || ds_enter_w;
		ds_correcao_optica_w	:= ds_correcao_optica_w ||lpad(upper(expressao6_w) || ': ',15,' ')||vl_od_pp_dio_esf_w;
		ds_correcao_optica_w	:= ds_correcao_optica_w ||lpad(upper(expressao7_w) || ': ',15,' ')||vl_od_pp_dio_cil_w;
		ds_correcao_optica_w	:= ds_correcao_optica_w ||lpad(upper(expressao8_w) || ': ',15,' ')||vl_od_pp_eixo_w ||ds_enter_w;
	end if;
	 
	if (vl_oe_pp_dio_esf_w IS NOT NULL AND vl_oe_pp_dio_esf_w::text <> '') or (vl_oe_pp_dio_cil_w IS NOT NULL AND vl_oe_pp_dio_cil_w::text <> '') or (vl_oe_pp_eixo_w IS NOT NULL AND vl_oe_pp_eixo_w::text <> '') 	 then 
		ds_correcao_optica_w	:= ds_correcao_optica_w ||lpad(upper(expressao10_w) || ': ',15,' ')||vl_oe_pp_dio_esf_w;
		ds_correcao_optica_w	:= ds_correcao_optica_w ||lpad(upper(expressao7_w) || ': ',15,' ')||vl_oe_pp_dio_cil_w;
		ds_correcao_optica_w	:= ds_correcao_optica_w ||lpad(upper(expressao8_w) || ': ',15,' ')||vl_oe_pp_eixo_w ||ds_enter_w;
	end if;
	ds_correcao_optica_w := ds_correcao_optica_w || ds_enter_w || upper(expressao12_w) || ': ' || ds_observacao_w ||ds_enter_w;
	ds_correcao_optica_w := ds_correcao_optica_w;
	end;
end loop;
close C01;	
 
if (ds_correcao_optica_w IS NOT NULL AND ds_correcao_optica_w::text <> '') then 
	CALL gravar_registro_resumo_oft(ds_correcao_optica_w,nr_seq_item_p,nm_usuario_p,to_date(dt_registro_w,'dd/mm/yyyy hh24:mi:ss'));
end if;
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_resumo_correcao (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) FROM PUBLIC;

