SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_resumo_evolucoes (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nm_profissional_w	varchar(255);
ds_espaco_20_w		varchar(255) 	:= lpad(' ',1,' ');
ds_enter_w		varchar(30)	:= chr(13) || chr(10);	
ds_observacao_w		varchar(1000);
dt_registro_w		varchar(30);
ds_evolucao_w		varchar(32000);
nr_atendimento_w	bigint;
ds_tipo_evolucao_w	varchar(255);
ds_especialidade_w	varchar(255);
ds_especialidade_med_w	varchar(255);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(327202, null, wheb_usuario_pck.get_nr_seq_idioma);--DATA: 
expressao2_w	varchar(255) := obter_desc_expressao_idioma(728918, null, wheb_usuario_pck.get_nr_seq_idioma);--PROFISSIONAL: 
expressao3_w	varchar(255) := obter_desc_expressao_idioma(299785, null, wheb_usuario_pck.get_nr_seq_idioma);--TIPO EVOLUÇÃO 
expressao4_w	varchar(255) := obter_desc_expressao_idioma(289431, null, wheb_usuario_pck.get_nr_seq_idioma);--ESPECIALIDADE 
expressao5_w	varchar(255) := obter_desc_expressao_idioma(289450, null, wheb_usuario_pck.get_nr_seq_idioma);--ESPECIALIDADE PROFISSIONAL 
C01 CURSOR FOR 
SELECT  TO_CHAR(a.dt_evolucao,'dd/mm/yyyy hh24:mi:ss'), 
	 SUBSTR(obter_nome_pf(a.cd_medico),1,150) nm_medico, 
	 substr(obter_valor_dominio(72, ie_tipo_evolucao),1,200) ds_funcao, 
	 substr(obter_desc_espec_medica(CD_ESPECIALIDADE),1,200) ds_especialidade, 
	 substr(obter_desc_espec_medica(CD_ESPECIALIDADE_MEDICO),1,200) ds_especialidade_medico 
FROM	 evolucao_paciente a 
WHERE (a.nr_atendimento = nr_atendimento_w) 
ORDER BY a.dt_evolucao DESC;


BEGIN 
SELECT MAX(nr_atendimento) 
into STRICT nr_atendimento_w 
FROM oft_consulta 
WHERE nr_sequencia = nr_seq_consulta_p;
 
open C01;
loop 
	fetch C01 into 
		dt_registro_w, 
		nm_profissional_w, 
		ds_tipo_evolucao_w, 
		ds_especialidade_w, 
		ds_especialidade_med_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_evolucao_w		:= 	upper(expressao1_w) || ' ' || dt_registro_w || ds_espaco_20_w || ds_espaco_20_w || 
					upper(expressao2_w) || ' ' ||nm_profissional_w ||ds_espaco_20_w || ds_espaco_20_w || 
					upper(expressao3_w) || ': ' || ds_tipo_evolucao_w || ds_espaco_20_w || ds_espaco_20_w || 
					upper(expressao4_w) || ': ' || ds_especialidade_w || ds_espaco_20_w || ds_espaco_20_w || 
					upper(expressao5_w) || ': ' || ds_especialidade_med_w || ds_enter_w;
 
	 
	CALL gravar_registro_resumo_oft(ds_evolucao_w,nr_seq_item_p,nm_usuario_p,to_date(dt_registro_w,'dd/mm/yyyy hh24:mi:ss'));
	end;
end loop;
close C01;
	 
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
