SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_resumo_fundoscopia (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nm_profissional_w	varchar(255);
ds_espaco_20_w		varchar(255) 	:= lpad(' ',1,' ');
ds_enter_w		varchar(30)	:= chr(13) || chr(10);	
ds_observacao_w		varchar(1000);
dt_registro_w		varchar(30);
ds_fundoscopia_w	varchar(32000);
ds_fundoscopia_oe_w 	varchar(1000);
ds_fundoscopia_od_w	varchar(1000);

 
c01 CURSOR FOR 
SELECT 	obter_nome_pf(cd_profissional) nm_profissional, 
	to_char(dt_exame,'dd/mm/yyyy hh24:mi:ss'), 
	ds_fundoscopia_oe, 
	ds_fundoscopia_od, 
	ds_observacao 
from  	oft_fundoscopia 
WHERE 	((nr_seq_consulta = nr_seq_consulta_p) OR ((ie_mostra_todos_p = 'S') AND nr_seq_consulta IN (SELECT c.nr_sequencia 
                                    	 	 	   FROM  atendimento_paciente b, 
                                       				   oft_consulta c 
                                    				   WHERE  c.nr_atendimento = b.nr_atendimento 
                                    			   AND  b.cd_pessoa_fisica = cd_pessoa_fisica_p))) 
and  coalesce(ie_situacao,'A') = 'A'	  
order by dt_exame desc;


BEGIN 
 
open c01;
loop 
	fetch c01 into 
		nm_profissional_w, 
		dt_registro_w, 
		ds_fundoscopia_oe_w, 
		ds_fundoscopia_od_w, 
		ds_observacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	ds_fundoscopia_w	:= upper(wheb_mensagem_pck.get_texto(308491)) || ': ' || dt_registro_w || ds_espaco_20_w || -- DATA 
				  upper(wheb_mensagem_pck.get_texto(307473)) || ': ' ||nm_profissional_w || ds_enter_w ||ds_enter_w; -- PROFISSIONAL 
	ds_fundoscopia_w	:= ds_fundoscopia_w ||lpad(wheb_mensagem_pck.get_texto(308520) || ': ',15,' ')||ds_fundoscopia_od_w || ds_enter_w; -- FO OD 
	ds_fundoscopia_w	:= ds_fundoscopia_w ||lpad(wheb_mensagem_pck.get_texto(308521) || ': ',15,' ')||ds_fundoscopia_oe_w || ds_enter_w; -- FO OE 
	ds_fundoscopia_w 	:= ds_fundoscopia_w || ds_enter_w || upper(wheb_mensagem_pck.get_texto(203221)) || ': ' || ds_observacao_w; -- OBSERVAÇÃO 
	
	CALL gravar_registro_resumo_oft(ds_fundoscopia_w,nr_seq_item_p,nm_usuario_p,to_date(dt_registro_w,'dd/mm/yyyy hh24:mi:ss'));
	end;
end loop;
close c01;
	 
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_resumo_fundoscopia (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) FROM PUBLIC;

