SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_resumo_item_ceratometria (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nm_profissional_w		varchar(255);
ds_espaco_20_w			varchar(255) 	:= lpad(' ',1,' ');
ds_enter_w			varchar(30)	:= chr(13) || chr(10);	
ds_comentario_w			varchar(2000);
dt_registro_w			varchar(30);
ds_ceratometria_w		varchar(32000);
vl_od_eixo_ceratometria_w	varchar(15);
vl_od_eixo_ceratometria_k2_w	varchar(15);
vl_od_ma_cu_ceratometria_w	varchar(15);
vl_od_me_cu_ceratometria_w	varchar(15);
vl_oe_eixo_ceratometria_w	varchar(15);
vl_oe_eixo_ceratometria_k2_w	varchar(15);
vl_oe_ma_cu_ceratometria_w	varchar(15);
vl_oe_me_cu_ceratometria_w	varchar(15);
vl_od_me_cu_milimetros_w	varchar(15);
vl_oe_me_cu_milimetros_w	varchar(15);
vl_od_ma_cu_milimetros_w	varchar(15);
vl_oe_ma_cu_milimetros_w	varchar(15);
vl_media_od_w			varchar(15);
vl_media_oe_w			varchar(15);
vl_cilindro_negativo_od_w	varchar(15);
vl_eixo_negativo_od_w		varchar(15);
vl_cilindro_negativo_oe_w	varchar(15);
vl_eixo_negativo_oe_w		varchar(15);
vl_cilindro_positivo_od_w	varchar(15);
vl_eixo_positivo_od_w		varchar(15);
vl_cilindro_positivo_oe_w	varchar(15);
vl_eixo_positivo_oe_w		varchar(15);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(327202, null, wheb_usuario_pck.get_nr_seq_idioma);--DATA: 
expressao2_w	varchar(255) := obter_desc_expressao_idioma(728918, null, wheb_usuario_pck.get_nr_seq_idioma);--PROFISSIONAL: 
expressao3_w	varchar(255) := obter_desc_expressao_idioma(774182, null, wheb_usuario_pck.get_nr_seq_idioma);--CURVA 1 OD 
expressao4_w	varchar(255) := obter_desc_expressao_idioma(289090, null, wheb_usuario_pck.get_nr_seq_idioma);--EIXO 1 
expressao5_w	varchar(255) := obter_desc_expressao_idioma(286436, null, wheb_usuario_pck.get_nr_seq_idioma);--CURVA 2 
expressao6_w	varchar(255) := obter_desc_expressao_idioma(289091, null, wheb_usuario_pck.get_nr_seq_idioma);--EIXO 2 
expressao7_w	varchar(255) := obter_desc_expressao_idioma(293044, null, wheb_usuario_pck.get_nr_seq_idioma);--MÉDIA 
expressao8_w	varchar(255) := obter_desc_expressao_idioma(774183, null, wheb_usuario_pck.get_nr_seq_idioma);--Cilindro 
expressao9_w	varchar(255) := obter_desc_expressao_idioma(289084, null, wheb_usuario_pck.get_nr_seq_idioma);--Eixo 
expressao10_w	varchar(255) := obter_desc_expressao_idioma(774184, null, wheb_usuario_pck.get_nr_seq_idioma);--Curva 1 OE 
expressao11_w	varchar(255) := obter_desc_expressao_idioma(285521, null, wheb_usuario_pck.get_nr_seq_idioma);--Comentário 
 
C01 CURSOR FOR 
	SELECT	obter_nome_pf(cd_profissional) nm_profissional, 
		to_char(dt_registro,'dd/mm/yyyy hh24:mi:ss'), 
		ds_comentario, 
		vl_od_eixo_ceratometria, 
		vl_oe_eixo_ceratometria, 
		vl_od_ma_cu_ceratometria, 
		vl_oe_ma_cu_ceratometria, 
		vl_od_me_cu_ceratometria, 
		vl_oe_me_cu_ceratometria, 
		vl_od_eixo_ceratometria_k2, 
		vl_oe_eixo_ceratometria_k2, 
		obter_dados_ceratometria(nr_sequencia,'MC1D'), 
		obter_dados_ceratometria(nr_sequencia,'MC1E'), 
		obter_dados_ceratometria(nr_sequencia,'MC2D'), 
		obter_dados_ceratometria(nr_sequencia,'MC2E'), 
		obter_dados_ceratometria(nr_sequencia,'MD'), 
		obter_dados_ceratometria(nr_sequencia,'ME'), 
		obter_dados_ceratometria(nr_sequencia,'CND'), 
		obter_dados_ceratometria(nr_sequencia,'END'), 
		obter_dados_ceratometria(nr_sequencia,'CNE'), 
		obter_dados_ceratometria(nr_sequencia,'ENE'), 
		obter_dados_ceratometria(nr_sequencia,'CPD'), 
		obter_dados_ceratometria(nr_sequencia,'EPD'), 
		obter_dados_ceratometria(nr_sequencia,'CPE'), 
		obter_dados_ceratometria(nr_sequencia,'EPE') 
	from  	oft_cerastocopia 
	where 	((nr_seq_consulta = nr_seq_consulta_p) or ((ie_mostra_todos_p = 'S') and nr_seq_consulta in (	SELECT	c.nr_sequencia 
														from  atendimento_paciente b, 
															oft_consulta c 
														where  c.nr_atendimento = b.nr_atendimento 
														and  	b.cd_pessoa_fisica = cd_pessoa_fisica_p))) 
	and  coalesce(ie_situacao,'A') = 'A'	  
	order by dt_registro desc;

 

BEGIN 
 
open C01;
loop 
	fetch C01 into 
		nm_profissional_w, 
		dt_registro_w, 
		ds_comentario_w, 
		vl_od_eixo_ceratometria_w, 
		vl_oe_eixo_ceratometria_w, 
		vl_od_ma_cu_ceratometria_w, 
		vl_oe_ma_cu_ceratometria_w, 
		vl_od_me_cu_ceratometria_w, 
		vl_oe_me_cu_ceratometria_w, 
		vl_od_eixo_ceratometria_k2_w, 
		vl_oe_eixo_ceratometria_k2_w, 
		vl_od_me_cu_milimetros_w, 
		vl_oe_me_cu_milimetros_w, 
		vl_od_ma_cu_milimetros_w, 
		vl_oe_ma_cu_milimetros_w, 
		vl_media_od_w, 
		vl_media_oe_w, 
		vl_cilindro_negativo_od_w, 
		vl_eixo_negativo_od_w, 
		vl_cilindro_negativo_oe_w, 
		vl_eixo_negativo_oe_w, 
		vl_cilindro_positivo_od_w, 
		vl_eixo_positivo_od_w, 
		vl_cilindro_positivo_oe_w, 
		vl_eixo_positivo_oe_w;
  EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_ceratometria_w	:=	'';
	ds_ceratometria_w	:= 	ds_ceratometria_w || upper(expressao1_w) || ' ' || dt_registro_w || ds_espaco_20_w || 
							upper(expressao2_w) || ' ' ||nm_profissional_w || ds_enter_w||ds_enter_w;
							 
	if (vl_od_eixo_ceratometria_w IS NOT NULL AND vl_od_eixo_ceratometria_w::text <> '') or (vl_od_ma_cu_ceratometria_w IS NOT NULL AND vl_od_ma_cu_ceratometria_w::text <> '') or (vl_od_me_cu_ceratometria_w IS NOT NULL AND vl_od_me_cu_ceratometria_w::text <> '') or (vl_od_eixo_ceratometria_k2_w IS NOT NULL AND vl_od_eixo_ceratometria_k2_w::text <> '') then 
	 
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao3_w) || ': ',15,' ')||vl_od_me_cu_ceratometria_w;
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao4_w) || ': ',13,' ')||vl_od_eixo_ceratometria_w;
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao5_w) || ': ',13,' ')||vl_od_ma_cu_ceratometria_w;
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao6_w) || ': ',13,' ')||vl_od_eixo_ceratometria_k2_w;
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao7_w) || ': ',13,' ')||vl_media_od_w ||ds_enter_w;
		 
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao8_w) || ' -: ',15,' ')|| '-'||vl_cilindro_negativo_od_w;
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao9_w) || ' -: ',13,' ')||vl_eixo_negativo_od_w;
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao8_w) || ' +: ',13,' ')||vl_cilindro_positivo_od_w;
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao9_w) || ' +: ',13,' ')||vl_eixo_positivo_od_w ||ds_enter_w||ds_enter_w;
	end if;
	 
	if (vl_oe_eixo_ceratometria_w IS NOT NULL AND vl_oe_eixo_ceratometria_w::text <> '') or (vl_oe_ma_cu_ceratometria_w IS NOT NULL AND vl_oe_ma_cu_ceratometria_w::text <> '') or (vl_oe_me_cu_ceratometria_w IS NOT NULL AND vl_oe_me_cu_ceratometria_w::text <> '') or (vl_oe_eixo_ceratometria_k2_w IS NOT NULL AND vl_oe_eixo_ceratometria_k2_w::text <> '') then 
	 
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao10_w) || ': ',15,' ')||vl_oe_me_cu_ceratometria_w;
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao4_w) || ': ',13,' ')||vl_oe_eixo_ceratometria_w;
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao5_w) || ': ',13,' ')||vl_oe_ma_cu_ceratometria_w;
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao6_w) || ': ',13,' ')||vl_oe_eixo_ceratometria_k2_w;
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao7_w) || ': ',13,' ')||vl_media_oe_w ||ds_enter_w;
		 
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao8_w) ||' -: ',15,' ')||'-'||vl_cilindro_negativo_oe_w;
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao9_w) ||' -: ',13,' ')||vl_eixo_negativo_oe_w;
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao8_w) ||' +: ',13,' ')||vl_cilindro_positivo_oe_w;
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao9_w) ||' +: ',13,' ')||vl_eixo_positivo_oe_w ||ds_enter_w||ds_enter_w;
	end if;
	 
	if (ds_comentario_w IS NOT NULL AND ds_comentario_w::text <> '') then 
		ds_ceratometria_w	:= ds_ceratometria_w ||lpad(upper(expressao11_w) || ': ',13,' ')||ds_comentario_w ||ds_enter_w;
	END IF;	
	 
	CALL gravar_registro_resumo_oft(ds_ceratometria_w,nr_seq_item_p,nm_usuario_p,to_date(dt_registro_w,'dd/mm/yyyy hh24:mi:ss'));
 
	end;
end loop;
close C01;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_resumo_item_ceratometria (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) FROM PUBLIC;

