CREATE OR REPLACE PROCEDURE gerar_resumo_med_receita (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nm_profissional_w	varchar(255);
ds_espaco_20_w		varchar(255) 	:= lpad(' ',1,' ');
ds_enter_w		varchar(30)	:= chr(13) || chr(10);	
ds_observacao_w		varchar(1000);
dt_registro_w		varchar(30);
ds_geral_w		varchar(32000);
nr_seq_rtf_srtring_w	bigint;
ds_consentimento_w	varchar(32000);
nr_sequencia_item_w	bigint;
qt_existe_w		bigint;
nr_atendimento_w	bigint;
ds_receita_w		varchar(32000);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(327202, null, wheb_usuario_pck.get_nr_seq_idioma);--DATA: 
expressao2_w	varchar(255) := obter_desc_expressao_idioma(728918, null, wheb_usuario_pck.get_nr_seq_idioma);--PROFISSIONAL: 
C01 CURSOR FOR 
SELECT  TO_CHAR(dt_receita,'dd/mm/yyyy hh24:mi:ss'), 
	 obter_nome_pf(CD_MEDICO) nm_profissional, 
	 nr_sequencia 
FROM 	 med_receita 
WHERE 	 nr_atendimento_hosp = nr_atendimento_w 
AND	 coalesce(ie_situacao,'A') = 'A' 
ORDER BY dt_receita DESC;


BEGIN 
 
SELECT MAX(nr_atendimento) 
into STRICT nr_atendimento_w 
FROM oft_consulta 
WHERE nr_sequencia = nr_seq_consulta_p;
 
open C01;
loop 
fetch C01 into	 
	dt_registro_w, 
	nm_profissional_w, 
	nr_sequencia_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_receita_w	:=	'';
	ds_receita_w	:= 	ds_receita_w || upper(expressao1_w) || ' ' || dt_registro_w || ds_espaco_20_w || 
								upper(expressao2_w) || ' ' || nm_profissional_w || ds_enter_w ||ds_enter_w;
			select	count(*) 
			into STRICT	qt_existe_w 
			from	med_receita 
			where	nr_sequencia = nr_sequencia_item_w 
			and	(ds_receita IS NOT NULL AND ds_receita::text <> '');
			 
			if ( qt_existe_w > 0 ) then 
				begin 
				nr_seq_rtf_srtring_w  := converte_rtf_string('select	ds_receita 
								from	med_receita 
								where	nr_sequencia = :nr_sequencia_p', nr_sequencia_item_w, nm_usuario_p, nr_seq_rtf_srtring_w );
				select	ds_texto 
				into STRICT	ds_geral_w 
				from	tasy_conversao_rtf 
				where	nr_sequencia = nr_seq_rtf_srtring_w;
				end;
				ds_receita_w := ds_receita_w || ds_geral_w || ds_enter_w;
			end if;
	 
	CALL gravar_registro_resumo_oft(ds_receita_w,nr_seq_item_p,nm_usuario_p,to_date(dt_registro_w,'dd/mm/yyyy hh24:mi:ss'));
	end;
end loop;
close C01;
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
