SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_resumo_microscopia_esp (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nm_profissional_w	varchar(255);
ds_espaco_20_w		varchar(255) 	:= lpad(' ',1,' ');
ds_enter_w		varchar(30)	:= chr(13) || chr(10);	
ds_observacao_w		varchar(1000);
dt_registro_w		varchar(30);
ds_microscopia_w	varchar(32000);
qt_oe_microscopia_w	double precision;
qt_od_microscopia_w	double precision;

C01 CURSOR FOR 
SELECT obter_nome_pf(cd_profissional) nm_profissional, 
	TO_CHAR(dt_registro,'dd/mm/yyyy hh24:mi:ss'), 
	qt_od_microscopia, 
	qt_oe_microscopia, 
	ds_observacao 
FROM  oft_microscopia_especular 
WHERE 	((nr_seq_consulta = nr_seq_consulta_p) OR ((ie_mostra_todos_p = 'S') AND nr_seq_consulta IN (SELECT c.nr_sequencia 
                                    	 	 	   FROM  atendimento_paciente b, 
                                       				   oft_consulta c 
                                    				   WHERE  c.nr_atendimento = b.nr_atendimento 
                                    			   AND  b.cd_pessoa_fisica = cd_pessoa_fisica_p)))	 
and  coalesce(ie_situacao,'A') = 'A'  
ORDER BY dt_registro desc;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nm_profissional_w, 
	dt_registro_w, 
	qt_od_microscopia_w, 
	qt_oe_microscopia_w, 
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_microscopia_w	:=	'';
	ds_microscopia_w	:= 	ds_microscopia_w || obter_desc_expressao(343703) || ' ' || dt_registro_w || ds_espaco_20_w || 
						'PROFISSIONAL: ' ||nm_profissional_w || ds_enter_w||ds_enter_w;
	ds_microscopia_w	:= ds_microscopia_w ||lpad('OD (cél/mm2): ',15,' ')||to_char(qt_od_microscopia_w, 'fm999990.000') || ds_enter_w;
	ds_microscopia_w	:= ds_microscopia_w ||lpad('OE (cél/mm2): ',15,' ')||to_char(qt_oe_microscopia_w, 'fm999990.000') || ds_enter_w;
	ds_microscopia_w := ds_microscopia_w || ds_enter_w || 'OBSERVAÇÃO: ' || ds_observacao_w ||ds_enter_w;
	 
	CALL gravar_registro_resumo_oft(ds_microscopia_w,nr_seq_item_p,nm_usuario_p,to_date(dt_registro_w,'dd/mm/yyyy hh24:mi:ss'));
	end;
end loop;
close C01;
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_resumo_microscopia_esp (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) FROM PUBLIC;

