CREATE OR REPLACE PROCEDURE gerar_resumo_migracao ( dt_resumo_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_seq_gerencia_w	bigint := 9;
nr_seq_estagio_w	bigint := 12;

nr_seq_projeto_w	bigint;
qt_horas_prev_proj_w	double precision;

qt_hor_possiv_res_w	double precision;
qt_hor_real_res_w	double precision;
qt_hor_sent_ant_res_w	double precision;
qt_hor_real_sent_w	double precision;

pr_inic_res_migr_w	real;
pr_possiv_res_migr_w	real;
pr_real_res_migr_w	real;

dt_provavel_termino_w	timestamp;

c01 CURSOR FOR
SELECT	p.nr_sequencia,
	coalesce(proj_obter_qt_total_horas_cron(p.nr_sequencia,0,'T','E'),0) qt_horas_prev_proj
from	proj_projeto p
where	p.nr_seq_gerencia = nr_seq_gerencia_w
and	p.nr_seq_estagio = nr_seq_estagio_w;


BEGIN
if (dt_resumo_p IS NOT NULL AND dt_resumo_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into	nr_seq_projeto_w,
			qt_horas_prev_proj_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		delete
		from	w_resumo_migracao
		where	nr_seq_projeto = nr_seq_projeto_w
		and	dt_resumo >= dt_resumo_p;

		qt_hor_possiv_res_w 	:= calc_hor_possiv_per_res_migr(nr_seq_projeto_w, dt_resumo_p);

		qt_hor_real_res_w	:= calc_hor_real_per_res_migr(nr_seq_projeto_w, dt_resumo_p);

		qt_hor_sent_ant_res_w	:= calc_hor_sent_per_res_migr(nr_seq_projeto_w, dt_resumo_p);

		qt_hor_real_sent_w	:= coalesce(obter_horas_sent_proj_migr(nr_seq_projeto_w, nr_seq_estagio_w), 0);

		pr_inic_res_migr_w	:= obter_perc_real_inic_res_migr(nr_seq_projeto_w, dt_resumo_p);

		pr_possiv_res_migr_w	:= obter_perc_valor(qt_hor_sent_ant_res_w + qt_hor_possiv_res_w, qt_horas_prev_proj_w);

		pr_real_res_migr_w	:= proj_obter_perc_cron(nr_seq_projeto_w, 'E');

		dt_provavel_termino_w	:= adic_dias_uteis_horas_data(clock_timestamp(), 8, qt_horas_prev_proj_w - qt_hor_real_sent_w, 1);

		insert into w_resumo_migracao(
			nr_sequencia,
			dt_resumo,
			nm_usuario,
			nr_seq_projeto,
			qt_horas_prev_resumo,
			qt_horas_real_resumo,
			qt_horas_sent_resumo,
			qt_horas_sent_geral,
			pr_inicial,
			pr_previsto,
			pr_final,
			dt_provavel_termino)
		values (
			nextval('w_resumo_migracao_seq'),
			dt_resumo_p,
			nm_usuario_p,
			nr_seq_projeto_w,
			qt_hor_possiv_res_w,
			qt_hor_real_res_w,
			qt_hor_sent_ant_res_w,
			qt_hor_real_sent_w,
			pr_inic_res_migr_w,
			pr_possiv_res_migr_w,
			pr_real_res_migr_w,
			dt_provavel_termino_w);
		end;
	end loop;
	close c01;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
