SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_resumo_potenc_acuidade (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_registro_w			varchar(30);
vl_potencial_od_w		varchar(40);
vl_potencial_oe_w		varchar(40);
ds_potencial_acuidade_visual_w	varchar(32000);	
ds_observacao_w			varchar(1000);	
nm_profissional_w		varchar(255);
ds_espaco_20_w			varchar(255) 	:= lpad(' ',1,' ');
ds_enter_w			varchar(30)	:= chr(13) || chr(10);	
ds_quebra_w			varchar(255) 	:= chr(13) || chr(10);
ds_profissional_w		varchar(255);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(327202, null, wheb_usuario_pck.get_nr_seq_idioma);--DATA: 
expressao2_w	varchar(255) := obter_desc_expressao_idioma(728918, null, wheb_usuario_pck.get_nr_seq_idioma);--PROFISSIONAL: 
expressao3_w	varchar(255) := obter_desc_expressao_idioma(296507, null, wheb_usuario_pck.get_nr_seq_idioma);--PROFISSÃO 
expressao4_w	varchar(255) := obter_desc_expressao_idioma(296131, null, wheb_usuario_pck.get_nr_seq_idioma);--Potencial OD 
expressao5_w	varchar(255) := obter_desc_expressao_idioma(305740, null, wheb_usuario_pck.get_nr_seq_idioma);--Potencial OE 
expressao6_w	varchar(255) := obter_desc_expressao_idioma(294639, null, wheb_usuario_pck.get_nr_seq_idioma);--Observação 
C01 CURSOR FOR 
SELECT to_char(dt_registro,'dd/mm/yyyy hh24:mi:ss'), 
	  obter_nome_pf(cd_profissional) nm_profissional, 
	  substr(obter_desc_cargo(x.cd_cargo),1,255) ds_profissional, 
	  obter_desc_tipo_acuidade(vl_potencial_od) vl_potencial_od, 
	  obter_desc_tipo_acuidade(vl_potencial_oe) vl_potencial_oe, 
	  a.ds_observacao 
from  OFT_POTENCIAL_ACUIDADE a, 
	pessoa_fisica x 
WHERE 	((nr_seq_consulta = nr_seq_consulta_p) OR ((ie_mostra_todos_p = 'S') AND nr_seq_consulta IN (SELECT c.nr_sequencia 
                                    	 	 	   FROM  atendimento_paciente b, 
                                       				   oft_consulta c 
                                    				   WHERE  c.nr_atendimento = b.nr_atendimento 
                                    			   AND  b.cd_pessoa_fisica = cd_pessoa_fisica_p))) 
and 	cd_profissional 	= 	x.cd_pessoa_fisica 
and	coalesce(ie_situacao,'A') = 'A' 
order  by dt_registro desc;

 

BEGIN 
	 
open C01;
loop 
	fetch C01 into 
		dt_registro_w,		  
		nm_profissional_w, 
		ds_profissional_w, 
		vl_potencial_od_w, 
		vl_potencial_oe_w, 
		ds_observacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_potencial_acuidade_visual_w	:= 	ds_potencial_acuidade_visual_w || upper(expressao1_w) || ' ' || dt_registro_w || ds_espaco_20_w || 
								upper(expressao2_w) || ' ' ||nm_profissional_w || ds_enter_w ||ds_enter_w || 
								upper(expressao3_w) || ': ' || ds_profissional_w || ds_enter_w || ds_enter_w;					
	if (vl_potencial_od_w IS NOT NULL AND vl_potencial_od_w::text <> '') or (vl_potencial_oe_w IS NOT NULL AND vl_potencial_oe_w::text <> '') then 
		ds_potencial_acuidade_visual_w	:= ds_potencial_acuidade_visual_w ||lpad(expressao4_w || ': ',15,' ')||vl_potencial_od_w ||ds_espaco_20_w;
		ds_potencial_acuidade_visual_w	:= ds_potencial_acuidade_visual_w ||lpad(expressao5_w || ': ',15,' ')||vl_potencial_oe_w ||ds_enter_w;
	end if;					
	 
	 
	ds_potencial_acuidade_visual_w := ds_potencial_acuidade_visual_w || ds_enter_w || upper(expressao6_w) || ': ' || ds_observacao_w ||ds_enter_w;
	ds_potencial_acuidade_visual_w := ds_potencial_acuidade_visual_w;
	 
	end;
	 
	CALL gravar_registro_resumo_oft(ds_potencial_acuidade_visual_w,nr_seq_item_p,nm_usuario_p,to_date(dt_registro_w,'dd/mm/yyyy hh24:mi:ss'));
	 
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
