SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_resumo_prescr_medica (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nm_profissional_w	varchar(255);
ds_espaco_20_w		varchar(255) 	:= lpad(' ',1,' ');
ds_enter_w		varchar(30)	:= chr(13) || chr(10);	
ds_observacao_w		varchar(1000);
dt_registro_w		varchar(30);
ds_prescricao_w		varchar(32000);
dt_prescricao_w		timestamp;
ds_itens_prescr_w	varchar(255);
nr_prescricao_w		bigint;
nr_atendimento_w	bigint;

expressao1_w	varchar(255) := obter_desc_expressao_idioma(327202, null, wheb_usuario_pck.get_nr_seq_idioma);--DATA: 
expressao2_w	varchar(255) := obter_desc_expressao_idioma(728918, null, wheb_usuario_pck.get_nr_seq_idioma);--PROFISSIONAL: 
expressao3_w	varchar(255) := obter_desc_expressao_idioma(296208, null, wheb_usuario_pck.get_nr_seq_idioma);--PRESCRIÇÃO 
expressao4_w	varchar(255) := obter_desc_expressao_idioma(307953, null, wheb_usuario_pck.get_nr_seq_idioma);--ITENS PRESCRIÇÃO 
C01 CURSOR FOR 
SELECT  a.dt_prescricao, 
	 a.nr_prescricao, 
	 SUBSTR(Obter_nome_medico(A.CD_MEDICO,'N'),1,150) NM_MEDICO, 
	 substr(obter_itens_prescr(a.nr_prescricao, a.ds_itens_prescr),1,255) ds_itens_prescr 
FROM	 prescr_medica a 
WHERE (a.nr_atendimento = nr_atendimento_w) 
and   coalesce(a.dt_suspensao::text, '') = '' 
order by a.nr_prescricao desc;


BEGIN 
SELECT MAX(nr_atendimento) 
into STRICT nr_atendimento_w 
FROM oft_consulta 
WHERE nr_sequencia = nr_seq_consulta_p;
 
open C01;
loop 
	fetch C01 into 
		dt_prescricao_w, 
		nr_prescricao_w, 
		nm_profissional_w, 
		ds_itens_prescr_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_prescricao_w		:= 	upper(expressao1_w) || ' ' || dt_prescricao_w || ds_espaco_20_w || 
					upper(expressao3_w) || ' ' ||nr_prescricao_w||ds_espaco_20_w || 
					upper(expressao2_w) || ' ' ||nm_profissional_w ||ds_espaco_20_w || 
					upper(expressao4_w) || ' ' || ds_itens_prescr_w || ds_enter_w;
 
	 
	CALL gravar_registro_resumo_oft(ds_prescricao_w,nr_seq_item_p,nm_usuario_p,dt_prescricao_w);
	end;
end loop;
close C01;
	 
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
