SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_resumo_pupilometria (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nm_profissional_w	varchar(255);
ds_espaco_20_w		varchar(255) 	:= lpad(' ',1,' ');
ds_enter_w		varchar(30)	:= chr(13) || chr(10);	
ds_observacao_w		varchar(1000);
dt_registro_w		varchar(30);
qt_od_pupilometria_w	real;
qt_oe_pupilometria_w	real;
ds_pupilometria_w		varchar(32000);
ds_lado_w		varchar(255);

C01 CURSOR FOR 
SELECT obter_nome_pf(cd_profissional) nm_profissional, 
	TO_CHAR(dt_registro,'dd/mm/yyyy hh24:mi:ss'), 
	qt_od_pupilometria, 
	qt_oe_pupilometria, 
	ds_observacao, 
	SUBSTR(obter_valor_dominio(1372,IE_OLHO_DOMINANTE ),1,255) ds_lado 
FROM  oft_pupilometria 
WHERE 	((nr_seq_consulta = nr_seq_consulta_p) OR ((ie_mostra_todos_p = 'S') AND nr_seq_consulta IN (SELECT c.nr_sequencia 
                                    	 	 	   FROM  atendimento_paciente b, 
                                       				   oft_consulta c 
                                    				   WHERE  c.nr_atendimento = b.nr_atendimento 
                                    			   AND  b.cd_pessoa_fisica = cd_pessoa_fisica_p))) 
and  coalesce(ie_situacao,'A') = 'A'	  
ORDER BY dt_registro;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nm_profissional_w, 
	dt_registro_w, 
	qt_od_pupilometria_w, 
	qt_oe_pupilometria_w, 
	ds_observacao_w, 
	ds_lado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_pupilometria_w	:=	'';
	ds_pupilometria_w	:=	'DATA: ' || dt_registro_w || ds_espaco_20_w || 
					'PROFISSIONAL: ' ||nm_profissional_w || ds_enter_w||ds_enter_w;
	ds_pupilometria_w := ds_pupilometria_w ||lpad('OD (mm): ',15,' ')|| to_char(qt_od_pupilometria_w, 'fm999990.00') || ds_enter_w;
	ds_pupilometria_w := ds_pupilometria_w ||lpad('OE (mm): ',15,' ')|| to_char(qt_oe_pupilometria_w, 'fm999990.00') || ds_enter_w;
	ds_pupilometria_w := ds_pupilometria_w ||lpad('OLHO DOMINANTE: ',22,' ')||ds_lado_w || ds_enter_w;
	ds_pupilometria_w := ds_pupilometria_w || ds_enter_w || obter_desc_expressao(330933)/*'OBSERVAÇÃO: '*/
 || ds_observacao_w ||ds_enter_w;
	 
	CALL gravar_registro_resumo_oft(ds_pupilometria_w,nr_seq_item_p,nm_usuario_p,to_date(dt_registro_w,'dd/mm/yyyy hh24:mi:ss'));
	end;
end loop;
close C01;	
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_resumo_pupilometria (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, nm_usuario_p text) FROM PUBLIC;

