		if (ie_oculta_inf_refracao_w = 'S') then 
			ds_refracao_w	:= ds_refracao_w ||lpad(upper(expressao5_w) || ': ',12,' ')||oft_obter_valor_com_sinal_neg(vl_od_pp_are_cil_w);
		else 
			ds_refracao_w	:= ds_refracao_w ||lpad(upper(expressao5_w) || ': ',12,' ')||oft_obter_valor_com_sinal(vl_od_pp_are_cil_w);
		end if;
		ds_refracao_w	:= ds_refracao_w ||lpad(upper(expressao6_w) || ': ',12,' ')||vl_od_pp_are_eixo_w;
		ds_refracao_w	:= ds_refracao_w ||lpad(upper(expressao7_w) || ': ',12,' ')||vl_od_pp_are_av_sc_w ||ds_enter_w;
	end if;
	 
	if (vl_oe_pp_are_esf_w IS NOT NULL AND vl_oe_pp_are_esf_w::text <> '') or (vl_oe_pp_are_cil_w IS NOT NULL AND vl_oe_pp_are_cil_w::text <> '') or (vl_oe_pp_are_eixo_w IS NOT NULL AND vl_oe_pp_are_eixo_w::text <> '') or (vl_oe_pp_are_av_sc_w IS NOT NULL AND vl_oe_pp_are_av_sc_w::text <> '')	then 
		ds_refracao_w	:= ds_refracao_w ||lpad(upper(expressao9_w) || ': ',12,' ')||oft_obter_valor_com_sinal(vl_oe_pp_are_esf_w);
		if (ie_oculta_inf_refracao_w = 'S') then 
			ds_refracao_w	:= ds_refracao_w ||lpad(upper(expressao5_w) || ': ',12,' ')||oft_obter_valor_com_sinal_neg(vl_oe_pp_are_cil_w);
		else 
			ds_refracao_w	:= ds_refracao_w ||lpad(upper(expressao5_w) || ': ',12,' ')||oft_obter_valor_com_sinal(vl_oe_pp_are_cil_w);
		end if;
		ds_refracao_w	:= ds_refracao_w ||lpad(upper(expressao6_w) || ': ',12,' ')||vl_oe_pp_are_eixo_w;
		ds_refracao_w	:= ds_refracao_w ||lpad(upper(expressao7_w) || ': ',12,' ')||vl_oe_pp_are_av_sc_w ||ds_enter_w;
	end if;
	 
	end if;
	 
	if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then 
		ds_refracao_w	:= ds_refracao_w || ds_enter_w || expressao13_w || ': ' || ds_observacao_w ||ds_enter_w;
	end if;
	 
	 
	 
	ds_refracao_w := ds_refracao_w;
	CALL gravar_registro_resumo_oft(ds_refracao_w,nr_seq_item_p,nm_usuario_p,to_date(dt_registro_w,'dd/mm/yyyy hh24:mi:ss'));
	end;
end loop;
close C01;
 
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
