SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_resumo_tonometria (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, ie_tipo_tonometria_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nm_profissional_w	varchar(255);
ds_espaco_20_w		varchar(255) 	:= lpad(' ',1,' ');
ds_enter_w		varchar(30)	:= chr(13) || chr(10);	
dt_registro_w		varchar(30);
ie_tipo_tonometria_w	smallint;
vl_pressao_od_w		varchar(10);
vl_pressao_oe_w		varchar(10);
ds_tonometria_w		varchar(32000);
ds_observacao_w		varchar(255);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(327202, null, wheb_usuario_pck.get_nr_seq_idioma);--DATA: 
expressao2_w	varchar(255) := obter_desc_expressao_idioma(728918, null, wheb_usuario_pck.get_nr_seq_idioma);--PROFISSIONAL: 
expressao3_w	varchar(255) := obter_desc_expressao_idioma(294741, null, wheb_usuario_pck.get_nr_seq_idioma);--OD (mmHg) 
expressao4_w	varchar(255) := obter_desc_expressao_idioma(294760, null, wheb_usuario_pck.get_nr_seq_idioma);--OE (mmHg) 
expressao5_w	varchar(255) := obter_desc_expressao_idioma(502224, null, wheb_usuario_pck.get_nr_seq_idioma);--Obs: 
C01 CURSOR FOR 
SELECT obter_nome_pf(cd_profissional) nm_profissional, 
	  to_char(dt_exame,'dd/mm/yyyy hh24:mi:ss'), 
	  vl_pressao_od, 
	  vl_pressao_oe, 
	  ie_tipo_tonometria, 
	  ds_observacao 
from  oft_tonometria 
WHERE 	((nr_seq_consulta = nr_seq_consulta_p) OR ((ie_mostra_todos_p = 'S') AND nr_seq_consulta IN (SELECT c.nr_sequencia 
                                    	 	 	   FROM  atendimento_paciente b, 
                                       				   oft_consulta c 
                                    				   WHERE  c.nr_atendimento = b.nr_atendimento 
                                    			   AND  b.cd_pessoa_fisica = cd_pessoa_fisica_p))) 
and  coalesce(ie_situacao,'A') = 'A'	  
order by dt_exame desc;


BEGIN 
	 
open C01;
loop 
	fetch C01 into 
		nm_profissional_w, 
		dt_registro_w, 
		vl_pressao_oe_w, 
		vl_pressao_od_w, 
		ie_tipo_tonometria_w, 
		ds_observacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	if (ie_tipo_tonometria_w = ie_tipo_tonometria_p) then 
		ds_tonometria_w	:=	'';
		ds_tonometria_w	:= 	upper(expressao1_w) || ' ' || dt_registro_w || ds_espaco_20_w || 
						upper(expressao2_w) || ' ' ||nm_profissional_w || ds_enter_w||ds_enter_w;
		ds_tonometria_w	:= ds_tonometria_w ||lpad(upper(expressao3_w) || ': ',15,' ')||vl_pressao_oe_w || ds_enter_w;
		ds_tonometria_w	:= ds_tonometria_w ||lpad(upper(expressao4_w) || ': ',15,' ')||vl_pressao_od_w || ds_enter_w;
		ds_tonometria_w := ds_tonometria_w ||lpad(expressao5_w || ' ' ,15, ' ') 	|| ds_observacao_w || ds_enter_w;
	end if;
	 
	CALL gravar_registro_resumo_oft(ds_tonometria_w,nr_seq_item_p,nm_usuario_p,to_date(dt_registro_w,'dd/mm/yyyy hh24:mi:ss'));	
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_resumo_tonometria (cd_estabelecimento_p bigint, nr_seq_consulta_p bigint, cd_pessoa_fisica_p text, ie_mostra_todos_p text, nr_seq_item_p bigint, ie_tipo_tonometria_p bigint, nm_usuario_p text) FROM PUBLIC;

