SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_retorno_citibank_240 ( nr_seq_cobr_escrit_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_titulo_w			varchar(255);
ds_observacao_w			varchar(255);
vl_titulo_w			titulo_receber.vl_titulo%type;
vl_saldo_inclusao_w		titulo_receber.vl_saldo_titulo%type;
nr_sequencia_w			w_retorno_banco.nr_sequencia%type;
nr_titulo_w			titulo_receber.nr_titulo%type;
nr_sequencia_banco_w 		w_retorno_banco.nr_sequencia%type;

c01 CURSOR FOR
	SELECT	a.nr_sequencia,
		trim(both substr(a.ds_string,63,10))
	from	w_retorno_banco a
	where	a.nr_seq_cobr_escrit		= nr_seq_cobr_escrit_p
	and 	trim(both substr(a.ds_string,14,1))	= 'P';


BEGIN

open	c01;
loop
fetch	c01 into
	nr_sequencia_w,
	ds_titulo_w;
	begin

	select	max(nr_titulo)
	into STRICT	nr_titulo_w
	from	titulo_receber
	where	nr_titulo_externo = ds_titulo_w;

	if (coalesce(nr_titulo_w::text, '') = '') then
		select	max(nr_titulo)
		into STRICT	nr_titulo_w
		from	titulo_receber
		where	nr_titulo	= somente_numero(ds_titulo_w);
	end if;

	if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then

		select	max(vl_titulo),
			max(vl_saldo_titulo)
		into STRICT	vl_titulo_w,
			vl_saldo_inclusao_w
		from	titulo_receber
		where	nr_titulo	= nr_titulo_w;

		nr_sequencia_banco_w := nr_sequencia_w + 1;

		insert	into titulo_receber_cobr(	nr_sequencia,
							nr_titulo,
							cd_banco,
							vl_cobranca,
							dt_atualizacao,
							nm_usuario,
							nr_seq_cobranca,
							vl_saldo_inclusao)
					values (	nextval('titulo_receber_cobr_seq'),
							nr_titulo_w,
							745,
							vl_titulo_w,
							clock_timestamp(),
							nm_usuario_p,
							nr_seq_cobr_escrit_p,
							vl_saldo_inclusao_w);

	else
		ds_observacao_w := substr(wheb_mensagem_pck.get_texto(302700,'ds_titulo_w=' || ds_titulo_w),1,4000);
		CALL fin_gerar_log_controle_banco(3,ds_observacao_w,nm_usuario_p,'N');
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
