SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_retorno_citibank_600 (nr_seq_banco_escrit_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/* 	Rotina criada para o Layout Pag-For 
Estrutura: 
 
Header de arquivo 
Registros transação (títulos) 
Trailler de arquivo 
 
*/
 
 
ds_nr_titulo_w		varchar(30);
ds_vl_pagamento_w	varchar(17);
ds_ocorrencia_w		varchar(1);
nr_titulo_w		bigint;
cd_tipo_baixa_w		bigint;
nr_seq_trans_escrit_w	bigint;
nr_seq_conta_banco_w	bigint;
nr_sequencia_w		bigint;
vl_pagamento_w		double precision;
cd_estabelecimento_w	bigint;
qt_titulo_w		bigint;
dt_baixa_w		timestamp;
cd_banco_w		smallint;
qt_retorno_liq_w	bigint;

qt_baixa_w		bigint;
nr_seq_movto_w		movto_trans_financ.nr_sequencia%type;
vl_total_baixa_w	double precision	:= 0;
dt_remessa_retorno_w	banco_escritural.dt_remessa_retorno%type;
ie_liquidacao_w		varchar(1);

/* registro transação */
 
c01 CURSOR FOR 
SELECT	substr(ds_conteudo,71,30) ds_nr_titulo, 
	substr(ds_conteudo,54,17) ds_vl_pagamento, 
	substr(ds_conteudo,49,1) ds_ocorrencia,	/* cada ocorrência possui dois dígitos, mas o registro pode possuir até 5 ocorrências */
 
	to_date(substr(ds_conteudo,411,6),'yy/mm/dd') dt_baixa 
from	w_interf_retorno_itau 
where	nr_seq_banco_escrit		= nr_seq_banco_escrit_p 
order	by nr_sequencia;


BEGIN 
 
select	max(a.cd_estabelecimento), 
	max(a.nr_seq_conta_banco), 
	max(a.nr_seq_trans_financ), 
	max(b.cd_banco), 
	max(a.dt_remessa_retorno) 
into STRICT	cd_estabelecimento_w, 
	nr_seq_conta_banco_w, 
	nr_seq_trans_escrit_w, 
	cd_banco_w, 
	dt_remessa_retorno_w 
FROM banco_escritural a
LEFT OUTER JOIN banco_estabelecimento b ON (a.nr_seq_conta_banco = b.nr_sequencia)
WHERE a.nr_sequencia		= nr_seq_banco_escrit_p;
 
if (coalesce(nr_seq_trans_escrit_w::text, '') = '') then 
 
	select	max(nr_seq_trans_escrit) 
	into STRICT	nr_seq_trans_escrit_w 
	from	parametro_tesouraria 
	where	cd_estabelecimento	= cd_estabelecimento_w;
 
end if;
 
select	coalesce(max(cd_tipo_baixa_padrao),1) 
into STRICT	cd_tipo_baixa_w 
from	parametros_contas_pagar 
where	cd_estabelecimento	= cd_estabelecimento_w;
 
ie_liquidacao_w := obter_param_usuario(857, 30, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_liquidacao_w);
 
open c01;
loop 
fetch c01 into 
	ds_nr_titulo_w, 
	ds_vl_pagamento_w, 
	ds_ocorrencia_w, 
	dt_baixa_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	begin 
 
	select	max(a.nr_titulo) 
	into STRICT	nr_titulo_w 
	from	titulo_pagar a 
	where	a.nr_titulo	= (ds_nr_titulo_w)::numeric;
 
	if (coalesce(nr_titulo_w::text, '') = '') then 
		/* O título ds_nr_titulo_w não existe no sistema! */
 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(226797,'DS_NR_TITULO_W='||ds_nr_titulo_w);
	end if;
 
	vl_pagamento_w		:= (ds_vl_pagamento_w)::numeric;
	vl_pagamento_w		:= dividir_sem_round((vl_pagamento_w)::numeric,100);
 
	select	count(*) 
	into STRICT	qt_titulo_w 
	from	titulo_pagar_escrit a 
	where	a.nr_titulo	= nr_titulo_w 
	and	a.nr_seq_escrit	= nr_seq_banco_escrit_p;
 
	if (qt_titulo_w = 0) then 
		CALL Gerar_Titulo_Escritural(nr_titulo_w,nr_seq_banco_escrit_p,nm_usuario_p);
	end if;
 
	update	titulo_pagar_escrit 
	set	ds_erro			= ds_ocorrencia_w 
	where	nr_seq_escrit		= nr_seq_banco_escrit_p 
	and	nr_titulo		= nr_titulo_w;
 
	qt_retorno_liq_w	:= 0;
 
	/* se tiver alguma das ocorrências de liquidação, baixa o título */
 
	select	count(*) 
	into STRICT	qt_retorno_liq_w 
	from	erro_escritural a 
	where	a.ie_rejeitado	= 'L' 
	and	position(a.cd_erro in ds_ocorrencia_w)	> 0 
	and	a.cd_banco	= cd_banco_w;
 
	if (qt_retorno_liq_w	> 0) then 
 
		select	count(*) 
		into STRICT	qt_baixa_w 
		from	titulo_pagar_baixa a 
		where	a.nr_seq_escrit	= nr_seq_banco_escrit_p 
		and	a.nr_titulo	= nr_titulo_w;
 
		if (coalesce(qt_baixa_w,0)	= 0) then 
 
			vl_total_baixa_w	:= coalesce(vl_total_baixa_w,0) + coalesce(vl_pagamento_w,0);
 
		end if;
	end if;
 
	exception 
	when others then 
		rollback;
		delete	from w_interf_retorno_itau 
		where	nr_seq_banco_escrit	= nr_seq_banco_escrit_p;
		commit;
		CALL wheb_mensagem_pck.exibir_mensagem_abort(226798, 
							'SQL_ERRM=' || sqlerrm || 
							';NR_TITULO_W=' || nr_titulo_w || 
							';DS_NR_TITULO_W=' || ds_nr_titulo_w || 
							';DS_VL_PAGAMENTO_W=' || ds_vl_pagamento_w || 
							';DS_OCORRENCIA_W=' || ds_ocorrencia_w);
	end;
end loop;
close c01;
 
if (coalesce(vl_total_baixa_w,0)	<> 0) then 
 
	CALL gerar_movto_trans_bco(nr_seq_banco_escrit_p,dt_remessa_retorno_w,cd_estabelecimento_w,nm_usuario_p,'I');
 
end if;
 
update	banco_escritural 
set	dt_baixa	= clock_timestamp(), 
	nm_usuario	= nm_usuario_p, 
	dt_atualizacao	= clock_timestamp() 
where	nr_sequencia	= nr_seq_banco_escrit_p;
 
delete	from w_interf_retorno_itau 
where	nr_seq_banco_escrit	= nr_seq_banco_escrit_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_retorno_citibank_600 (nr_seq_banco_escrit_p bigint, nm_usuario_p text) FROM PUBLIC;

