SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_retorno_hsbc_400 ( nr_seq_cobr_escrit_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_reg_T_w		bigint;
nr_seq_reg_U_w		bigint;
nr_titulo_w		bigint;
vl_titulo_w		double precision;
vl_acrescimo_w		double precision;
vl_desconto_w		double precision;
vl_abatimento_w		double precision;
vl_liquido_w		double precision;
vl_outras_despesas_w	double precision;
dt_liquidacao_w		varchar(6);
ds_titulo_w		varchar(255);
vl_cobranca_w		double precision;
vl_alterar_w		double precision;
cd_ocorrencia_w		varchar(5);
nr_seq_ocorrencia_ret_w	bigint;

/* Procedure criada na OS 283300, poderá ainda sofrer alterações */

c01 CURSOR FOR
	SELECT	trim(both substr(ds_string,38,25)),
		somente_numero(substr(ds_string,153,13))/100,
		somente_numero(substr(ds_string,267,13))/100,
		somente_numero(substr(ds_string,241,13))/100,
		somente_numero(substr(ds_string,228,13))/100,
		somente_numero(substr(ds_string,254,13))/100,
		0,
		substr(ds_string,147,6),
		substr(ds_string,109,2)
	from	w_retorno_banco
	where	nr_seq_cobr_escrit	= nr_seq_cobr_escrit_p
	and	substr(ds_string,1,1)	= '1';

BEGIN

open C01;
loop
fetch C01 into
	ds_titulo_w,
	vl_cobranca_w,
	vl_acrescimo_w,
	vl_desconto_w,
	vl_abatimento_w,
	vl_liquido_w,
	vl_outras_despesas_w,
	dt_liquidacao_w,
	cd_ocorrencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	vl_alterar_w	:= 0;

	select	max(nr_titulo)
	into STRICT	nr_titulo_w
	from	titulo_receber
	where	nr_titulo	= somente_numero(ds_titulo_w);

	/* Se encontrou o título importa, senão grava no log */

	if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then

		select	vl_titulo
		into STRICT	vl_titulo_w
		from	titulo_receber
		where	nr_titulo	= nr_titulo_w;


		select 	max(a.nr_sequencia)
		into STRICT	nr_seq_ocorrencia_ret_w
		from	banco_ocorr_escrit_ret a
		where	a.cd_banco	= 399
		and	a.cd_ocorrencia = cd_ocorrencia_w;

		/* Tratar acrescimos/descontos */

		if (vl_titulo_w <> vl_liquido_w) then
			vl_alterar_w	:= vl_liquido_w - vl_titulo_w;

			if (vl_alterar_w > 0) then
				vl_acrescimo_w	:= vl_alterar_w;
			else
				vl_desconto_w	:= abs(vl_alterar_w);
			end if;
		end if;

		insert	into titulo_receber_cobr(	NR_SEQUENCIA,
							NR_TITULO,
							CD_BANCO,
							VL_COBRANCA,
							VL_DESCONTO,
							VL_ACRESCIMO,
							VL_DESPESA_BANCARIA,
							VL_LIQUIDACAO,
							DT_LIQUIDACAO,
							DT_ATUALIZACAO,
							NM_USUARIO,
							NR_SEQ_COBRANCA,
							nr_seq_ocorrencia_ret)
					values (	nextval('titulo_receber_cobr_seq'),
							nr_titulo_w,
							001,
							vl_titulo_w,
							vl_desconto_w,
							vl_acrescimo_w,
							vl_outras_despesas_w,
							vl_liquido_w,
							coalesce(to_Date(dt_liquidacao_w,'ddmmyy'),clock_timestamp()),
							clock_timestamp(),
							nm_usuario_p,
							nr_seq_cobr_escrit_p,
							nr_seq_ocorrencia_ret_w);
	else
		CALL fin_gerar_log_controle_banco(	3,
						substr(wheb_mensagem_pck.get_texto(302918, 'DS_TITULO_W=' || ds_titulo_w),1,4000),
						nm_usuario_p,
						'N');
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
