SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_retorno_itau (nr_seq_banco_escrit_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_nr_titulo_w		varchar(255);
ds_dt_pagamento_w	varchar(255);
ds_vl_pagamento_w	varchar(255);
ds_nr_documento_w	varchar(255);
ds_ocorrencia_w		varchar(255);

cd_reg_favorecido_w	varchar(50);
cd_conf_envio_w		varchar(50);
cd_retorno_liq_w	varchar(50);

nr_titulo_w		bigint;
cd_tipo_baixa_w		bigint;
nr_seq_trans_escrit_w	bigint;
nr_seq_conta_banco_w	bigint;
nr_sequencia_w		bigint;
dt_pagamento_w		timestamp;
vl_pagamento_w		double precision;
nr_documento_w		varchar(255);
ds_forma_pagto_w	varchar(50);

cd_estabelecimento_w	bigint;
nr_sequencia_inicial_w	bigint;
nr_sequencia_final_w	bigint;
nr_seq_interf_w		bigint;
vl_saldo_titulo_w	double precision;
vl_imposto_w		double precision;
qt_titulo_w		bigint;
vl_despesa_w		double precision;
vl_acrescimo_w		double precision;
ie_vl_associado_w	varchar(2);
qt_baixa_w		bigint;

ds_vl_desconto_w	varchar(15);
vl_desconto_w		double precision;
cd_banco_w			banco_escritural.cd_banco%type;	
cd_banco_pagto_w	banco_escritural.cd_banco%type;	
qt_retorno_liq_w	bigint;
ie_estornar_baixa_w	erro_escritural.ie_estornar_baixa%type;
nr_seq_trans_fin_baixa_w	titulo_pagar.nr_seq_trans_fin_baixa%type;
ie_movto_bco_pag_escrit_w	parametros_contas_pagar.ie_movto_bco_pag_escrit%type;
ie_bloquear_titulo_w		erro_escritural.ie_bloquear_titulo%type;
ie_excluir_titulo_w			erro_escritural.ie_excluir_titulo%type;
cd_moeda_w					moeda.cd_moeda%type;
ds_sigla_moeda_w			varchar(255);

vl_juros_w			titulo_pagar_escrit.vl_juros%type;
vl_multa_w			titulo_pagar_escrit.vl_multa%type;
vl_remessa_w		titulo_pagar_escrit.vl_escritural%type;

c01 CURSOR FOR
SELECT	nr_sequencia,
	substr(ds_conteudo,12,2) ds_forma_pagto
from	w_interf_retorno_itau
where	substr(ds_conteudo, 8, 1)	= '1'
and	nr_seq_banco_escrit		= nr_seq_banco_escrit_p
order	by nr_sequencia;

c02 CURSOR FOR
SELECT	nr_sequencia,
	substr(ds_conteudo,74,20) ds_nr_titulo,
	substr(ds_conteudo,155,8) ds_dt_pagamento,
	substr(ds_conteudo,163,15) ds_vl_pagamento,
	substr(ds_conteudo,198,6) ds_nr_documento,
	substr(ds_conteudo,231,10) ds_ocorrencia,
	null ds_vl_desconto,
	substr(ds_conteudo,102,3) ds_sigla_moeda
from	w_interf_retorno_itau
where	substr(ds_conteudo, 8, 1)	= '3'
and	nr_seq_banco_escrit		= nr_seq_banco_escrit_p
and	ds_forma_pagto_w		in ('01','03','41','07','06','43','05','10')
and	nr_sequencia			> nr_sequencia_inicial_w
and	nr_sequencia			< nr_sequencia_final_w 		-- pagamento em doc/ted/cc/cp/op
union

SELECT	nr_sequencia,
	substr(ds_conteudo,183,20) ds_nr_titulo,
	substr(ds_conteudo,145,8) ds_dt_pagamento,
	substr(ds_conteudo,153,15) ds_vl_pagamento,
	'' ds_nr_documento,
	substr(ds_conteudo,231,10) ds_ocorrencia,
	substr(ds_conteudo,115,15) ds_vl_desconto,
	'' ds_sigla_moeda
from	w_interf_retorno_itau
where	substr(ds_conteudo, 8, 1)	= '3'
and	nr_seq_banco_escrit		= nr_seq_banco_escrit_p
and	ds_forma_pagto_w		in ('30','31')
and	nr_sequencia			> nr_sequencia_inicial_w
and	nr_sequencia			< nr_sequencia_final_w 		-- pagamento com bloqueto
union

select	nr_sequencia,
	substr(ds_conteudo,175,20) ds_nr_titulo,
	substr(ds_conteudo,137,8) ds_dt_pagamento,
	substr(ds_conteudo,145,15) ds_vl_pagamento,
	'' ds_nr_documento,
	substr(ds_conteudo,231,10) ds_ocorrencia,
	'' ds_vl_desconto,
	'' ds_sigla_moeda
from	w_interf_retorno_itau
where	substr(ds_conteudo, 8, 1)	= '3'
and	nr_seq_banco_escrit		= nr_seq_banco_escrit_p
and	ds_forma_pagto_w		in ('19','91','13')
and	nr_sequencia			> nr_sequencia_inicial_w
and	nr_sequencia			< nr_sequencia_final_w 		-- pagamento de concessionarios e tributos com codigo de barras
union

select	nr_sequencia,
	substr(ds_conteudo,196,20) ds_nr_titulo,
	substr(ds_conteudo,100,8) ds_dt_pagamento,
	substr(ds_conteudo,86,14) ds_vl_pagamento,
	'' ds_nr_documento,
	substr(ds_conteudo,231,10) ds_ocorrencia,
	'' ds_vl_desconto,
	'' ds_sigla_moeda
from	w_interf_retorno_itau
where	substr(ds_conteudo, 8, 1)	= '3'
and	nr_seq_banco_escrit		= nr_seq_banco_escrit_p
and	ds_forma_pagto_w		in ('17')
and	nr_sequencia			> nr_sequencia_inicial_w
and	nr_sequencia			< nr_sequencia_final_w 		--  pagamento de gps
union

select	nr_sequencia,
	substr(ds_conteudo,196,20) ds_nr_titulo,
	substr(ds_conteudo,128,8) ds_dt_pagamento,
	substr(ds_conteudo,106,14) ds_vl_pagamento,
	'' ds_nr_documento,
	substr(ds_conteudo,231,10) ds_ocorrencia,
	'' ds_vl_desconto,
	'' ds_vl_multa
from	w_interf_retorno_itau
where	substr(ds_conteudo, 8, 1)	= '3'
and	nr_seq_banco_escrit		= nr_seq_banco_escrit_p
and	ds_forma_pagto_w		in ('16')
and	nr_sequencia			> nr_sequencia_inicial_w
and	nr_sequencia			< nr_sequencia_final_w 		-- pagamento de DARF  Segmento N
order	by nr_sequencia;												


BEGIN

begin
select	b.cd_reg_favorecido,
	b.cd_conf_envio,
	b.cd_retorno_liq,
	a.cd_estabelecimento,
	a.nr_seq_conta_banco,
	a.cd_banco
into STRICT	cd_reg_favorecido_w,
	cd_conf_envio_w,
	cd_retorno_liq_w,
	cd_estabelecimento_w,
	nr_seq_conta_banco_w,
	cd_banco_w
from	banco_retorno_cp b,
	banco_escritural a
where	a.cd_banco		= b.cd_banco
and	a.nr_sequencia		= nr_seq_banco_escrit_p;
exception
when others then
	/* Cadastro do retorno de pagamento escritural nao encontrado! Verifica os cadastros financeiros. */

	CALL wheb_mensagem_pck.exibir_mensagem_abort(198870);
end;

select max((substr(ds_conteudo,1,3))::numeric )
into STRICT	cd_banco_pagto_w
from	w_interf_retorno_itau
where	substr(ds_conteudo, 8, 1)	= '0'
and	nr_seq_banco_escrit		= nr_seq_banco_escrit_p;

if (cd_banco_w <> cd_banco_pagto_w) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1212889);
end if;

select	nr_seq_trans_escrit
into STRICT	nr_seq_trans_escrit_w
from	parametro_tesouraria
where	cd_estabelecimento	= cd_estabelecimento_w;

select	coalesce(cd_tipo_baixa_padrao, 1),
		coalesce(ie_movto_bco_pag_escrit,'T')
into STRICT	cd_tipo_baixa_w,
		ie_movto_bco_pag_escrit_w
from	parametros_contas_pagar
where	cd_estabelecimento	= cd_estabelecimento_w;

ie_vl_associado_w := obter_param_usuario(857, 33, Obter_Perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_vl_associado_w);

open c01;
loop
fetch c01 into
	nr_sequencia_inicial_w,
	ds_forma_pagto_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	
	begin
	select	min(nr_sequencia)
	into STRICT	nr_sequencia_final_w
	from	w_interf_retorno_itau
	where	substr(ds_conteudo, 8, 1)	<> '3'
	and	nr_seq_banco_escrit		= nr_seq_banco_escrit_p
	and	nr_sequencia			> nr_sequencia_inicial_w;

	open c02;
	loop
	fetch c02 into
		nr_seq_interf_w,
		ds_nr_titulo_w,
		ds_dt_pagamento_w,
		ds_vl_pagamento_w,
		ds_nr_documento_w,
		ds_ocorrencia_w,
		ds_vl_desconto_w,
		ds_sigla_moeda_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		
		nr_titulo_w		:= (ds_nr_titulo_w)::numeric;
		dt_pagamento_w		:= to_date(ds_dt_pagamento_w, 'ddmmyyyy');
		vl_pagamento_w		:= (ds_vl_pagamento_w)::numeric;
		vl_pagamento_w		:= dividir_sem_round((vl_pagamento_w)::numeric, 100);
		nr_documento_w		:= ds_nr_documento_w;
		vl_desconto_w		:= somente_numero(ds_vl_desconto_w) / 100;

		select 	max(cd_moeda)
		into STRICT 	cd_moeda_w
		from 	moeda
		where 	ds_sigla_moeda = ds_sigla_moeda_w;
		
		select	count(*)
		into STRICT	qt_titulo_w
		from	titulo_pagar_escrit a
		where	a.nr_titulo	= nr_titulo_w
		and	a.nr_seq_escrit	= nr_seq_banco_escrit_p;

		if (qt_titulo_w	= 0) then

			CALL gerar_titulo_escritural(nr_titulo_w,nr_seq_banco_escrit_p,nm_usuario_p);

			if (coalesce(cd_moeda_w,0) <> 0) then
				update	titulo_pagar_escrit
				set	ie_moeda = cd_moeda_w
				where	nr_seq_escrit = nr_seq_banco_escrit_p
				and	nr_titulo		= nr_titulo_w;				
			end if;
			
		end if;
		
		
		/*Gravar todas as ocorrencias no titulo*/

		update	titulo_pagar_escrit
		set		ds_erro			= ds_ocorrencia_w
		where	nr_seq_escrit	= nr_seq_banco_escrit_p
		and		nr_titulo		= nr_titulo_w;
		
   		/*if 	(cd_reg_favorecido_w = ds_ocorrencia_w) then
			update	titulo_pagar_escrit
			set	ds_erro			= ds_ocorrencia_w
			where	nr_seq_escrit		= nr_seq_banco_escrit_p
			and	nr_titulo		= nr_titulo_w;
		elsif	(cd_conf_envio_w = ds_ocorrencia_w) then
			update	titulo_pagar_escrit
			set	ds_erro			= ds_ocorrencia_w
			where	nr_seq_escrit		= nr_seq_banco_escrit_p
			and	nr_titulo		= nr_titulo_w;
		elsif	(cd_retorno_liq_w = ds_ocorrencia_w) then*/
		
		select	count(*)
		into STRICT	qt_retorno_liq_w
		from	erro_escritural a
		where	a.ie_rejeitado						= 'L'
		and		position(a.cd_erro in ds_ocorrencia_w)	> 0
		and		a.cd_banco							= cd_banco_w;
		
		
		if	(((position(cd_retorno_liq_w in ds_ocorrencia_w) > 0)
            or (qt_retorno_liq_w > 0))
			and coalesce(ie_movto_bco_pag_escrit_w,'T') = 'T') THEN  /*Se tiver ocorrencia de liquida??o, */
				
			RAISE NOTICE 'nr_titulo_w = %', nr_titulo_w;

			select	coalesce(max(a.vl_juros),0),
				coalesce(max(a.vl_multa),0),
				coalesce(max(a.vl_escritural),0)
			into STRICT	vl_juros_w,
				vl_multa_w,
				vl_remessa_w
			from	banco_escritural b,
				titulo_pagar_escrit a
			where	b.nr_sequencia	=	nr_seq_banco_escrit_p
			and	a.nr_seq_escrit		=	b.nr_sequencia
			and	a.nr_titulo		=	nr_titulo_w;

			if (vl_remessa_w <> coalesce(vl_pagamento_w,0)) then
				vl_pagamento_w	:= coalesce(vl_pagamento_w,0) - coalesce(vl_juros_w,0) - coalesce(vl_multa_w,0);
			end if;

			select	sum(a.vl_imposto)
			into STRICT	vl_imposto_w
			from	tributo b,
				w_titulo_pagar_imposto a
			where	coalesce(b.ie_saldo_tit_pagar,'S')	= 'S'
			and	a.cd_tributo	= b.cd_tributo
			and	a.nr_seq_escrit	= nr_seq_banco_escrit_p
			and	a.nr_titulo	= nr_titulo_w;

			select	max(a.vl_saldo_titulo),
				max(a.vl_outras_despesas),
				max(a.vl_outros_acrescimos)
			into STRICT	vl_saldo_titulo_w,
				vl_despesa_w,
				vl_acrescimo_w
			from	titulo_pagar a
			where	a.nr_titulo	= nr_titulo_w;

			if	((coalesce(vl_pagamento_w,0) + coalesce(vl_imposto_w,0)) > coalesce(vl_saldo_titulo_w,0)) then
			
				vl_pagamento_w	:= coalesce(vl_pagamento_w,0) - coalesce(vl_imposto_w,0);

			end if;

			if (ie_vl_associado_w	= 'S') then

				vl_pagamento_w	:= coalesce(vl_pagamento_w,0) - coalesce(vl_despesa_w,0) - coalesce(vl_acrescimo_w,0);

			end if;
			

			qt_baixa_w	:= coalesce(qt_baixa_w,0) + 1;
			
			select	max(nr_seq_trans_fin_baixa)
			into STRICT	nr_seq_trans_fin_baixa_w
			from	titulo_pagar
			where	nr_titulo = nr_titulo_w;

			CALL baixa_titulo_pagar(cd_estabelecimento_w,
					cd_tipo_baixa_w,
					nr_titulo_w,
					coalesce(vl_pagamento_w,0) + coalesce(vl_desconto_w,0),
					nm_usuario_p,
					coalesce(nr_seq_trans_fin_baixa_w,nr_seq_trans_escrit_w),
					null,
					nr_seq_banco_escrit_p,
					dt_pagamento_w,
					nr_seq_conta_banco_w);

			select	max(nr_sequencia)
			into STRICT	nr_sequencia_w
			from	titulo_pagar_baixa
			where	nr_titulo	= nr_titulo_w;

			CALL gerar_movto_tit_baixa(nr_titulo_w,
					nr_sequencia_w,
					'P',
					nm_usuario_p,
					'N');

			CALL atualizar_saldo_tit_pagar(nr_titulo_w, nm_usuario_p);
			CALL Gerar_W_Tit_Pag_imposto(nr_titulo_w, nm_usuario_p);
		else
			update	titulo_pagar_escrit
			set	ds_erro			= ds_ocorrencia_w
			where	nr_seq_escrit		= nr_seq_banco_escrit_p
			and	nr_titulo		= nr_titulo_w;

			SELECT * FROM consistir_erro_escritural(
				ds_ocorrencia_w, nr_seq_banco_escrit_p, cd_banco_w, nr_titulo_w, cd_estabelecimento_w, nm_usuario_p, ie_bloquear_titulo_w, ie_excluir_titulo_w
			) INTO STRICT ie_bloquear_titulo_w, ie_excluir_titulo_w
			;

			select	max(a.ie_estornar_baixa)
			into STRICT	ie_estornar_baixa_w
			from	erro_escritural a
			where	position(a.cd_erro in ds_ocorrencia_w)	> 0;
			
			if (ie_estornar_baixa_w = 'S') then
				select	max(nr_sequencia)
				into STRICT	nr_sequencia_w
				from	titulo_pagar_baixa
				where	nr_titulo	= nr_titulo_w
				and	(nr_seq_escrit IS NOT NULL AND nr_seq_escrit::text <> '');
				
				if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
					CALL estornar_tit_pagar_baixa(nr_titulo_w,
								nr_sequencia_w,
								dt_pagamento_w,
								nm_usuario_p,
								'N');
				end if;
			end if;
		end if;
		
	end loop;
	close c02;

	exception
	when others then
		rollback;
		delete	from w_interf_retorno_itau
		where	nr_seq_banco_escrit	= nr_seq_banco_escrit_p;
		commit;
		/* sqlerrm
		nr_titulo_w = nr_titulo_w
		ds_nr_titulo_w = ds_nr_titulo_w
		ds_dt_pagamento_w = ds_dt_pagamento_w
		ds_vl_pagamento_w = ds_vl_pagamento_w
		ds_nr_documento_w = ds_nr_documento_w
		ds_ocorrencia_w = ds_ocorrencia_w */
		CALL wheb_mensagem_pck.exibir_mensagem_abort(198871,	'SQL_ERRM=' || sqlerrm ||
								';NR_TITULO_W=' || nr_titulo_w ||
								';DS_NR_TITULO_W=' || ds_nr_titulo_w ||
								';DS_DT_PAGAMENTO_W=' || ds_dt_pagamento_w ||
								';DS_VL_PAGAMENTO_W=' || ds_vl_pagamento_w ||
								';DS_NR_DOCUMENTO_W=' || ds_nr_documento_w ||
								';DS_OCORRENCIA_W=' || ds_ocorrencia_w);
	end;
end loop;
close c01;

if	((coalesce(ie_movto_bco_pag_escrit_w,'T') = 'T') and coalesce(qt_baixa_w,0)	> 0) then

	update	banco_escritural
	set	dt_baixa	= clock_timestamp()
	where	nr_sequencia	= nr_seq_banco_escrit_p;

end if;

delete	from w_interf_retorno_itau
where	nr_seq_banco_escrit	= nr_seq_banco_escrit_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_retorno_itau (nr_seq_banco_escrit_p bigint, nm_usuario_p text) FROM PUBLIC;

