SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_retorno_nossacaixa_240 ( nr_seq_cobr_escrit_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_reg_T_w		bigint;
nr_seq_reg_U_w		bigint;
nr_titulo_w		bigint;
vl_titulo_w		double precision;
vl_acrescimo_w		double precision;
vl_desconto_w		double precision;
vl_abatimento_w		double precision;
vl_liquido_w		double precision;
vl_outras_despesas_w	double precision;
dt_liquidacao_w		varchar(8);
ds_titulo_w		varchar(255);
vl_cobranca_w		double precision;
vl_alterar_w		double precision;
cd_ocorrencia_w		bigint;
nr_seq_ocorrencia_ret_w	bigint;

c01 CURSOR FOR
	SELECT	trim(both substr(ds_string,59,10)),
		(substr(ds_string,80,15))::numeric /100,
		0,
		0,
		0,
		0,
		0,
		substr(ds_string,72,8),
		substr(ds_string,226,2)
	from	w_retorno_banco
	where	nr_seq_cobr_escrit	= nr_seq_cobr_escrit_p
	and	substr(ds_string,14,1)	= 'T';

BEGIN

open C01;
loop
fetch C01 into
	ds_titulo_w,
	vl_cobranca_w,
	vl_acrescimo_w,
	vl_desconto_w,
	vl_abatimento_w,
	vl_liquido_w,
	vl_outras_despesas_w,
	dt_liquidacao_w,
	cd_ocorrencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	vl_alterar_w	:= 0;

	select	max(nr_titulo)
	into STRICT	nr_titulo_w
	from	titulo_receber
	where	nr_titulo	= somente_numero(ds_titulo_w);

	/* Se encontrou o título importa, senão grava no log */

	if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then

		select	vl_titulo
		into STRICT	vl_titulo_w
		from	titulo_receber
		where	nr_titulo	= nr_titulo_w;


		select 	coalesce(to_char(max(a.nr_sequencia)),0)
		into STRICT	nr_seq_ocorrencia_ret_w
		from	banco_ocorr_escrit_ret a
		where	a.cd_banco = 151
		and	a.cd_ocorrencia = cd_ocorrencia_w;

		/* Tratar acrescimos/descontos */

		if (vl_titulo_w <> vl_liquido_w) then
			vl_alterar_w	:= vl_liquido_w - vl_titulo_w;

			if (vl_alterar_w > 0) then
				vl_acrescimo_w	:= vl_alterar_w;
			else
				vl_desconto_w	:= abs(vl_alterar_w);
			end if;
		end if;

		insert	into titulo_receber_cobr(
			nr_sequencia,
			nr_titulo,
			cd_banco,
			vl_cobranca,
			vl_desconto,
			vl_acrescimo,
			vl_despesa_bancaria,
			vl_liquidacao,
			dt_liquidacao,
			dt_atualizacao,
			nm_usuario,
			nr_seq_cobranca,
			nr_seq_ocorrencia_ret)
		values (	nextval('titulo_receber_cobr_seq'),
			nr_titulo_w,
			151,
			vl_titulo_w,
			vl_desconto_w,
			vl_acrescimo_w,
			vl_outras_despesas_w,
			vl_liquido_w,
			coalesce(to_Date(dt_liquidacao_w,'ddmmyyyy'),clock_timestamp()),
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_cobr_escrit_p,
			nr_seq_ocorrencia_ret_w);
	else
		--fin_gerar_log_controle_banco(3,substr('Não foi importado o título ' || ds_titulo_w || ', pois não foi encontrado no Tasy',1,4000),nm_usuario_p,'N');
		CALL fin_gerar_log_controle_banco(3,substr(wheb_mensagem_pck.get_texto(304526,'DS_TITULO=' || ds_titulo_w),1,4000),nm_usuario_p,'N');
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_retorno_nossacaixa_240 ( nr_seq_cobr_escrit_p bigint, nm_usuario_p text) FROM PUBLIC;

