CREATE OR REPLACE PROCEDURE gerar_retorno_valor_a_menor (cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_retorno_p bigint, vl_amenor_max_p bigint, nr_seq_ret_gerado_p INOUT text, ie_aceitar_glosa_p text) AS $body$
DECLARE


nr_seq_ret_item_w	bigint;
nr_seq_retorno_w	bigint;
ie_aceitar_glosa_w	varchar(10);

c01 CURSOR FOR
SELECT	nr_sequencia
from	convenio_retorno_item
where	nr_seq_retorno	= nr_seq_retorno_p
and	vl_amenor between 0 and vl_amenor_max_p
and	vl_amenor <> 0;


BEGIN

ie_aceitar_glosa_w	:= coalesce(ie_aceitar_glosa_p,'S');

nr_seq_ret_gerado_p	:= null;

select	nextval('convenio_retorno_seq')
into STRICT	nr_seq_retorno_w
;

insert into convenio_retorno(nr_sequencia,
	cd_convenio,
	cd_estabelecimento,
	dt_retorno,
	ie_status_retorno,
	dt_atualizacao,
	nm_usuario,
	nm_usuario_retorno,
	nr_seq_protocolo,
	nr_seq_prot_adic,
	ds_observacao,
	nr_seq_tipo,
	dt_baixa_cr)
SELECT	nr_seq_retorno_w,
	a.cd_convenio,
	cd_estabelecimento_p,
	clock_timestamp(),
	'R',
	clock_timestamp(),
	nm_usuario_p,
	nm_usuario_p,
	a.nr_seq_protocolo,
	a.nr_seq_prot_adic,
	substr(wheb_mensagem_pck.get_texto(310217),1,255) || nr_seq_retorno_p,
	nr_seq_tipo,
	clock_timestamp()
from	convenio_retorno a
where	a.nr_sequencia	= nr_seq_retorno_p
and	exists (select	1
		from	convenio_retorno_item x
		where	x.nr_seq_retorno	= a.nr_sequencia
		and 	vl_amenor between 0 and vl_amenor_max_p
		and	vl_amenor 		<> 0);

open c01;
loop
fetch c01 into
	nr_seq_ret_item_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	insert into convenio_retorno_item(nr_sequencia,
		nr_seq_retorno,
		vl_pago,
		vl_glosado,
		dt_atualizacao,
		nm_usuario,
		ie_glosa,
		nr_interno_conta,
		cd_autorizacao,
		ds_observacao,
		vl_adicional,
		nr_titulo,
		vl_amenor,
		vl_adequado,
		cd_motivo_glosa,
		ie_libera_repasse,
		ie_analisada,
		vl_desconto,
		cd_autorizacao_conv,
		cd_centro_custo_desc,
		nr_seq_motivo_desc,
		vl_perdas,
		vl_guia,
		ie_autorizacao,
		vl_amenor_post,
		vl_glosado_post)
	SELECT	nextval('convenio_retorno_item_seq'),
		nr_seq_retorno_w,
		0,
		CASE WHEN ie_aceitar_glosa_w='S' THEN a.vl_amenor  ELSE 0 END ,		--O valor a menor passa a ser glosa
		clock_timestamp(),
		nm_usuario_p,
		'S',
		a.nr_interno_conta,
		a.cd_autorizacao,
		a.ds_observacao,
		a.vl_adicional,
		a.nr_titulo,
		CASE WHEN ie_aceitar_glosa_w='N' THEN a.vl_amenor  ELSE 0 END ,
		0,
		a.cd_motivo_glosa,
		a.ie_libera_repasse,
		'N',
		a.vl_desconto,
		a.cd_autorizacao_conv,
		a.cd_centro_custo_desc,
		a.nr_seq_motivo_desc,
		a.vl_perdas,
		a.vl_guia,
		a.ie_autorizacao,
		0,
		0
	from	convenio_retorno_item a
	where	a.nr_sequencia	= nr_seq_ret_item_w
	and	exists (SELECT	1
			from	convenio_retorno x
			where	x.nr_sequencia	= nr_seq_retorno_w);

	nr_seq_ret_gerado_p	:= nr_seq_retorno_w;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_retorno_valor_a_menor (cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_retorno_p bigint, vl_amenor_max_p bigint, nr_seq_ret_gerado_p INOUT text, ie_aceitar_glosa_p text) FROM PUBLIC;

