CREATE OR REPLACE PROCEDURE gerar_ret_cobr_escrit_real ( nr_seq_cobr_escrit_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_reg_T_w		bigint;
nr_seq_reg_U_w		bigint;
nr_titulo_w		bigint;
vl_titulo_w		double precision;
vl_acrescimo_w		double precision;
vl_desconto_w		double precision;
vl_abatimento_w		double precision;
vl_liquido_w		double precision;
vl_outras_despesas_w	double precision;
dt_liquidacao_w		timestamp;

c01 CURSOR FOR
	SELECT	nr_sequencia,
		(substr(ds_string,59,15))::numeric ,
		(substr(ds_string,82,13) || ',' || substr(ds_string,95,2))::numeric
	from	w_retorno_banco
	where	nr_seq_cobr_escrit	= nr_seq_cobr_escrit_p
	and	substr(ds_string,8,1)	= '3'
	and	substr(ds_string,14,1)	= 'T';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_reg_T_w,
	nr_titulo_w,
	vl_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_seq_reg_U_w := nr_seq_reg_T_w + 1;

	select	(substr(ds_string,18,13) || ',' || substr(ds_string,31,2))::numeric ,
		(substr(ds_string,33,13) || ',' || substr(ds_string,46,2))::numeric ,
		(substr(ds_string,48,13) || ',' || substr(ds_string,61,2))::numeric ,
		(substr(ds_string,93,13) || ',' || substr(ds_string,106,2))::numeric ,
		(substr(ds_string,108,13) || ',' || substr(ds_string,121,2))::numeric ,
		to_date(substr(ds_string,146,8),'dd/mm/yyyy')
	into STRICT	vl_acrescimo_w,
		vl_desconto_w,
		vl_abatimento_w,
		vl_liquido_w,
		vl_outras_despesas_w,
		dt_liquidacao_w
	from	w_retorno_banco
	where	nr_sequencia	= nr_seq_reg_U_w;

	insert	into titulo_receber_cobr(	NR_SEQUENCIA,
					NR_TITULO,
					CD_BANCO,
					VL_COBRANCA,
					VL_DESCONTO,
					VL_ACRESCIMO,
					VL_DESPESA_BANCARIA,
					VL_LIQUIDACAO,
					DT_LIQUIDACAO,
					DT_ATUALIZACAO,
					NM_USUARIO,
					NR_SEQ_COBRANCA)
			values (	nextval('titulo_receber_cobr_seq'),
					nr_titulo_w,
					356,
					vl_titulo_w,
					vl_desconto_w,
					vl_acrescimo_w,
					vl_outras_despesas_w,
					vl_liquido_w,
					dt_liquidacao_w,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_cobr_escrit_p);

	exception
	when others then
		/* Erro ao importar o título nr_titulo_w!
		Verifique se este título existe no sistema.
		sqlerrm */
		CALL wheb_mensagem_pck.exibir_mensagem_abort(262746,	'NR_TITULO_W='||nr_titulo_w || ';' ||
								'SQLERRM_W=' || sqlerrm);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
