SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_ret_cobr_santander_v25 ( nr_seq_cobr_escrit_p bigint, nm_usuario_p text) AS $body$
DECLARE


--Padrão 240 ¿ Cobrança
--Versão 2.5
--Setembro/2014
nr_seq_reg_t_w		bigint;
nr_seq_reg_u_w		bigint;
nr_titulo_w		bigint;
cont_w			bigint;
vl_titulo_w		double precision;
vl_acrescimo_w		double precision;
vl_desconto_w		double precision;
vl_abatimento_w		double precision;
vl_liquido_w		double precision;
vl_outras_despesas_w	double precision;
dt_liquidacao_w		timestamp;
ds_lista_titulos_w		varchar(4000);
ds_nosso_numero_w	varchar(13);
cd_motivo_w		varchar(20);
nr_seq_ocorrencia_ret_w	bigint;
nr_seq_ocorr_motivo_w	bigint;
vl_saldo_titulo_w		double precision;
ds_titulo_w		varchar(13);
vl_despesa_bancaria_w	double precision;
cd_ocorrencia_w		bigint;
ds_mensagem_w		varchar(255);

c01 CURSOR FOR
SELECT	nr_sequencia,
	(substr(ds_string,41,13))::numeric  nr_titulo,
	lpad(trim(both substr(ds_string,41,13)),13,'0') ds_nosso_numero,
	(substr(ds_string,78,13) || ',' || substr(ds_string,91,2))::numeric  vl_cobranca,
	substr(ds_string,209,10) cd_motivo,
	0 vl_despesa_bancaria
from	w_retorno_banco
where	nr_seq_cobr_escrit	= nr_seq_cobr_escrit_p
and	substr(ds_string,8,1)	= '3'
and	substr(ds_string,14,1)	= 'T';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_reg_T_w,
	nr_titulo_w,
	ds_nosso_numero_w,
	vl_titulo_w,
	cd_motivo_w,
	vl_despesa_bancaria_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_titulo_w	:= ds_nosso_numero_w;

	select	count(*),
		max(vl_saldo_titulo)
	into STRICT	cont_w,
		vl_saldo_titulo_w
	from	titulo_receber
	where	nr_titulo	= nr_titulo_w;

	if (cont_w = 0) then

		select	max(a.nr_titulo),
			max(a.vl_saldo_titulo)
		into STRICT	nr_titulo_w,
			vl_saldo_titulo_w
		from	titulo_receber a
		where	lpad(a.nr_nosso_numero,13,'0')	= ds_nosso_numero_w
		and	coalesce(a.nr_nosso_numero,'0')	<> '0';

		if (coalesce(nr_titulo_w::text, '') = '') and (length(ds_lista_titulos_w) <= 3990) then

			if (coalesce(ds_lista_titulos_w::text, '') = '') then
				ds_lista_titulos_w	:= nr_titulo_w;
			else
				ds_lista_titulos_w	:= ds_lista_titulos_w || ', ' || nr_titulo_w;
			end if;

		end if;

	end if;

	if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then

		nr_seq_reg_U_w := nr_seq_reg_T_w + 1;

		select	(substr(ds_string,123,13) || ',' || substr(ds_string,136,2))::numeric ,
			(substr(ds_string,33,13) || ',' || substr(ds_string,46,2))::numeric ,
			(substr(ds_string,48,13) || ',' || substr(ds_string,61,2))::numeric ,
			(substr(ds_string,93,13) || ',' || substr(ds_string,106,2))::numeric ,
			vl_despesa_bancaria_w,
			to_date(substr(ds_string,138,8),'dd/mm/yyyy'),
			(substr(ds_string,16,2))::numeric
		into STRICT	vl_acrescimo_w,
			vl_desconto_w,
			vl_abatimento_w,
			vl_liquido_w,
			vl_outras_despesas_w,
			dt_liquidacao_w,
			cd_ocorrencia_w
		from	w_retorno_banco
		where	nr_sequencia	= nr_seq_reg_U_w;

		/* OS 508827 - wcbernardino
		if	(vl_acrescimo_w <= 0) then -- afstringari, OS165149, 10/09/2009
			vl_acrescimo_w	:= vl_liquido_w - vl_titulo_w;
		end if;*/
		/*if	(cont_w = 0) then	AAMFIRMO OS 644868 - Tirei pq o count já é verificado se for 0 antes de entrar nesse if, onde já busca pelo titulo e apenas entra aqui se o nr_titulo for diferente de nulo
			wheb_mensagem_pck.exibir_mensagem_abort(219198,'NR_TITULO_W=' || nr_titulo_w);
		end if;*/
		select 	max(a.nr_sequencia)
		into STRICT	nr_seq_ocorrencia_ret_w
		from	banco_ocorr_escrit_ret a
		where	a.cd_banco	= 33
		and	a.cd_ocorrencia = cd_ocorrencia_w;

		select	max(a.nr_sequencia)
		into STRICT	nr_seq_ocorr_motivo_w
		from	banco_ocorr_motivo_ret a
		where	position(a.cd_motivo in cd_motivo_w)	> 0
		and	a.nr_seq_escrit_ret		= nr_seq_ocorrencia_ret_w;

		insert	into titulo_receber_cobr(nr_sequencia,
			nr_titulo,
			cd_banco,
			vl_cobranca,
			vl_desconto,
			vl_acrescimo,
			vl_despesa_bancaria,
			vl_liquidacao,
			dt_liquidacao,
			dt_atualizacao,
			nm_usuario,
			nr_seq_cobranca,
			nr_seq_ocorrencia_ret,
			nr_seq_ocorr_motivo,
			vl_saldo_inclusao)
		values (nextval('titulo_receber_cobr_seq'),
			nr_titulo_w,
			033,
			coalesce(vl_titulo_w,0),
			0,
			0,
			vl_outras_despesas_w,
			coalesce(vl_liquido_w,0) - coalesce(vl_outras_despesas_w,0),
			dt_liquidacao_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_cobr_escrit_p,
			nr_seq_ocorrencia_ret_w,
			nr_seq_ocorr_motivo_w,
			vl_saldo_titulo_w);

	else
		ds_mensagem_w := wheb_mensagem_pck.get_texto(304957,'ds_titulo_w=' || ds_titulo_w);
		insert	into cobranca_escrit_log(ds_log,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec,
			nr_nosso_numero,
			nr_seq_cobranca,
			nr_sequencia)
			values (ds_mensagem_w,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			ds_nosso_numero_w,
			nr_seq_cobr_escrit_p,
			nextval('cobranca_escrit_log_seq'));

	end if;

	end;
end loop;
close C01;

commit;

if (ds_lista_titulos_w IS NOT NULL AND ds_lista_titulos_w::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(219196,'DS_TITULOS_W=' || ds_lista_titulos_w);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_ret_cobr_santander_v25 ( nr_seq_cobr_escrit_p bigint, nm_usuario_p text) FROM PUBLIC;

