SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_ret_deb_caixa_150_v04 ( nr_seq_cobr_escrit_p bigint, nm_usuario_p text) AS $body$
DECLARE


-- OS 410127
-- Layout Padrão Febraban 240 posições V04
-- AGOSTO/1998
nr_titulo_w					bigint;
vl_titulo_w					double precision;
vl_acrescimo_w				double precision;
vl_desconto_w				double precision;
vl_abatimento_w				double precision;
vl_liquido_w				double precision;
vl_outras_despesas_w		double precision;
dt_liquidacao_w				varchar(8);
ds_titulo_w					varchar(255);
vl_cobranca_w				double precision;
vl_alterar_w				double precision;
cd_ocorrencia_w				bigint;
nr_seq_ocorrencia_ret_w		bigint;
ds_mensagem_w				varchar(4000);

c01 CURSOR FOR
	SELECT	trim(both substr(ds_string,70,60)),
		0,
		0,
		0,
		0,
		(substr(ds_string,53,15))::numeric /100,
		0,
		substr(ds_string,45,8),
		substr(ds_string,68,2)
	from	w_retorno_banco
	where	nr_seq_cobr_escrit	= nr_seq_cobr_escrit_p
	and	substr(ds_string,1,1)	= 'F';

BEGIN

open C01;
loop
fetch C01 into
	ds_titulo_w,
	vl_cobranca_w,
	vl_acrescimo_w,
	vl_desconto_w,
	vl_abatimento_w,
	vl_liquido_w,
	vl_outras_despesas_w,
	dt_liquidacao_w,
	cd_ocorrencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	vl_alterar_w	:= 0;

	select	max(nr_titulo)
	into STRICT	nr_titulo_w
	from	titulo_receber
	where	nr_titulo	= somente_numero(ds_titulo_w);

	/* Se encontrou o título importa, senão grava no log */

	if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then
		select	coalesce(max(vl_titulo),0)
		into STRICT	vl_titulo_w
		from	titulo_receber
		where	nr_titulo	= nr_titulo_w;

		select 	coalesce(to_char(max(a.nr_sequencia)),0)
		into STRICT	nr_seq_ocorrencia_ret_w
		from	banco_ocorr_escrit_ret a
		where	a.cd_banco = 104
		and	a.cd_ocorrencia = cd_ocorrencia_w;

		/* Tratar acrescimos/descontos */

		if (vl_titulo_w <> vl_liquido_w) then
			vl_alterar_w	:= vl_liquido_w - vl_titulo_w;

			if (vl_alterar_w > 0) then
				vl_acrescimo_w	:= vl_alterar_w;
			else
				vl_desconto_w	:= abs(vl_alterar_w);
			end if;
		end if;

		insert	into titulo_receber_cobr(
			nr_sequencia,
			nr_titulo,
			cd_banco,
			vl_cobranca,
			vl_desconto,
			vl_acrescimo,
			vl_despesa_bancaria,
			vl_liquidacao,
			dt_liquidacao,
			dt_atualizacao,
			nm_usuario,
			nr_seq_cobranca,
			nr_seq_ocorrencia_ret)
		values (	nextval('titulo_receber_cobr_seq'),
			nr_titulo_w,
			104,
			vl_titulo_w,
			vl_desconto_w,
			vl_acrescimo_w,
			vl_outras_despesas_w,
			vl_liquido_w,
			coalesce(to_date(dt_liquidacao_w,'yyyymmdd'),clock_timestamp()),
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_cobr_escrit_p,
			nr_seq_ocorrencia_ret_w);
	else
		ds_mensagem_w := substr(wheb_mensagem_pck.get_texto(304633,'ds_titulo_w=' || ds_titulo_w),1,4000);
		CALL fin_gerar_log_controle_banco(3,ds_mensagem_w,nm_usuario_p,'N');
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
