SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_ret_item_amaior_prop ( nm_usuario_p text, vl_amaior_p bigint, nr_seq_retorno_p text, nr_seq_lista_ret_item_p text) AS $body$
DECLARE


vl_guia_w	double precision;
vl_amaior_w	double precision;
pr_amaior_w	double precision;
nr_sequencia_w	bigint;			
vl_pago_w	double precision;
vl_adicional_w	double precision;
vl_adicional_total_w double precision := 0;
ds_erro_w		varchar(4000);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		vl_pago,
		vl_adicional
	from	convenio_retorno_item
	where	nr_seq_retorno = nr_seq_retorno_p
	and (obter_se_contido_char(nr_sequencia,nr_seq_lista_ret_item_p) = 'S');


BEGIN

begin
dbms_application_info.SET_ACTION('GERAR_RET_ITEM_AMAIOR_PROP');

select	coalesce(sum(vl_pago),0)
into STRICT	vl_guia_w
from	convenio_retorno_item
where	nr_seq_retorno	= nr_seq_retorno_p
and (obter_se_contido_char(nr_sequencia,nr_seq_lista_ret_item_p) = 'S');

vl_amaior_w	:= coalesce(vl_amaior_p,0);

if (vl_guia_w > 0) and (vl_amaior_p > 0) then
	--(vl_amaior_p > 0) and 

	--(vl_guia_w > vl_amaior_p) then
	begin

	if (dividir_sem_round(vl_amaior_w, vl_guia_w) > 0) then
		begin
		open C01;
		loop
		fetch C01 into	
			nr_sequencia_w,
			vl_pago_w,
			vl_adicional_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			vl_adicional_w	:= vl_adicional_w + (vl_pago_w * dividir_sem_round(vl_amaior_w, vl_guia_w));
			
			update	convenio_retorno_item
			set	vl_adicional		= vl_adicional_w
			where	nr_sequencia		= nr_sequencia_w
			and	nr_seq_retorno		= nr_seq_retorno_p;
			
			vl_adicional_total_w	:= vl_adicional_total_w + vl_adicional_w;

			end;
		end loop;
		close C01;
		end;
	end if;	

	if (vl_amaior_w > vl_adicional_total_w) then	
		
		vl_adicional_total_w	:= vl_amaior_w - vl_adicional_total_w;

		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	convenio_retorno_item
		where	nr_seq_retorno	= nr_seq_retorno_p
		and (obter_se_contido_char(nr_sequencia,nr_seq_lista_ret_item_p) = 'S');


		update	convenio_retorno_item
		set	vl_adicional		= vl_adicional + vl_adicional_total_w
		where	nr_sequencia		= nr_sequencia_w
		and	nr_seq_retorno		= nr_seq_retorno_p;
		
	elsif (vl_amaior_w < vl_adicional_total_w) then
		
		vl_adicional_total_w	:= vl_adicional_total_w - vl_amaior_w;
		
		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	convenio_retorno_item
		where	nr_seq_retorno	= nr_seq_retorno_p;
		
		update	convenio_retorno_item
		set	vl_adicional		= vl_adicional - vl_adicional_total_w
		where	nr_sequencia		= nr_sequencia_w
		and	nr_seq_retorno		= nr_seq_retorno_p;
		
	end if;
	
	end;
end if;

exception
when others then
	dbms_application_info.SET_ACTION('');
	ds_erro_w	:= sqlerrm;
	--r.aise_application_error(-20011,sqlerrm);
	CALL wheb_mensagem_pck.exibir_mensagem_abort(263395,'ds_erro_w='||ds_erro_w);		
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
