SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_ret_pagto_febraban_agrup (nr_seq_banco_escrit_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_nr_titulo_w		varchar(255);
ds_dt_pagamento_w	varchar(255);
ds_vl_pagamento_w	varchar(255);
ds_nr_documento_w	varchar(255);
ds_ocorrencia_w		varchar(255);
ds_segmento_w		varchar(1);

cd_reg_favorecido_w	varchar(50);
cd_conf_envio_w		varchar(50);
cd_retorno_liq_w	varchar(50);

nr_titulo_w		bigint;
cd_tipo_baixa_w		bigint;
nr_seq_trans_escrit_w	bigint;
nr_seq_conta_banco_w	bigint;
nr_sequencia_w		bigint;
dt_pagamento_w		timestamp;
vl_pagamento_w		double precision;
nr_documento_w		varchar(255);
ds_forma_pagto_w	varchar(50);
vl_liquido_w		double precision;

cd_estabelecimento_w	bigint;
nr_sequencia_inicial_w	bigint;
nr_sequencia_final_w	bigint;
nr_seq_interf_w		bigint;
vl_saldo_titulo_w	double precision;
qt_titulo_w		bigint;
vl_despesa_w		double precision;
vl_acrescimo_w		double precision;
ie_vl_associado_w	varchar(2);
qt_baixa_w		bigint;

ds_vl_real_w		varchar(15);
vl_real_w		double precision;
nr_seq_segmento_b_w	bigint;
ie_tipo_inscricao_w	smallint;
nr_inscricao_w		bigint;
nr_seq_remessa_w	integer;

vl_escritural_w		titulo_pagar_escrit.vl_escritural%type;
vl_acrescimo_escrit_w	titulo_pagar_escrit.vl_acrescimo%type;
vl_desconto_w		titulo_pagar_escrit.vl_desconto%type;
vl_juros_w		titulo_pagar_escrit.vl_juros%type;
vl_multa_w		titulo_pagar_escrit.vl_multa%type;
vl_despesa_escrit_w	titulo_pagar_escrit.vl_despesa%type;
cd_ocorrencia_w		titulo_pagar_escrit.cd_ocorrencia%type;
ie_tipo_documento_w	titulo_pagar_escrit.ie_tipo_documento%type;
ie_tipo_servico_w	titulo_pagar_escrit.ie_tipo_servico%type;
cd_moeda_w		titulo_pagar.cd_moeda%type;

c01 CURSOR FOR 
SELECT	nr_sequencia, 
	substr(ds_conteudo,12,2) ds_forma_pagto 
from	w_interf_retorno_itau 
where	substr(ds_conteudo, 8, 1)	= '1' 
and	nr_seq_banco_escrit		= nr_seq_banco_escrit_p 
order	by nr_sequencia;

c02 CURSOR FOR 
SELECT	nr_sequencia, 
	substr(ds_conteudo,74,20) ds_nr_titulo, 
	substr(ds_conteudo,155,8) ds_dt_pagamento, 
	substr(ds_conteudo,163,15) ds_vl_pagamento, 
	substr(ds_conteudo,135,20) ds_nr_documento, 
	substr(ds_conteudo,231,2) ds_ocorrencia, 
	substr(ds_conteudo,120,15) ds_vl_real, 
	substr(ds_conteudo, 14,1) ds_segmento 
from	w_interf_retorno_itau 
where	substr(ds_conteudo, 14, 1)	= 'A' 
and	substr(ds_conteudo, 8, 1)	= '3' 
and	nr_seq_banco_escrit		= nr_seq_banco_escrit_p 
and	ds_forma_pagto_w		in ('01','03','41','07','06','43','05') 
and	nr_sequencia			> nr_sequencia_inicial_w 
and	nr_sequencia			< nr_sequencia_final_w 		-- pagamento em doc/ted/cc/cp 
union
 
SELECT	nr_sequencia, 
	substr(ds_conteudo,183,20) ds_nr_titulo, 
	substr(ds_conteudo,145,8) ds_dt_pagamento, 
	substr(ds_conteudo,153,15) ds_vl_pagamento, 
	'' ds_nr_documento, 
	substr(ds_conteudo,231,2) ds_ocorrencia, 
	null ds_vl_real, 
	substr(ds_conteudo, 14,1) ds_segmento 
from	w_interf_retorno_itau 
where	substr(ds_conteudo, 14, 1)	= 'J' 
and	substr(ds_conteudo, 8, 1)	= '3' 
and	nr_seq_banco_escrit		= nr_seq_banco_escrit_p 
and	ds_forma_pagto_w		in ('30','31') 
and	nr_sequencia			> nr_sequencia_inicial_w 
and	nr_sequencia			< nr_sequencia_final_w 		-- pagamento com bloqueto 
union
 
select	nr_sequencia, 
	substr(ds_conteudo,123,20) ds_nr_titulo, 
	substr(ds_conteudo,100,8) ds_dt_pagamento, 
	substr(ds_conteudo,108,15) ds_vl_pagamento, 
	'' ds_nr_documento, 
	substr(ds_conteudo,231,2) ds_ocorrencia, 
	null ds_vl_real, 
	substr(ds_conteudo, 14,1) ds_segmento 
from	w_interf_retorno_itau 
where	substr(ds_conteudo, 14, 1)	= 'O' 
and	substr(ds_conteudo, 8, 1)	= '3' 
and	nr_seq_banco_escrit		= nr_seq_banco_escrit_p 
and	ds_forma_pagto_w		= '11' 
and	nr_sequencia			> nr_sequencia_inicial_w 
and	nr_sequencia			< nr_sequencia_final_w 		-- pagamento com bloqueto 
order	by nr_sequencia;

C03 CURSOR FOR 
SELECT	a.nr_titulo, 
	coalesce(b.vl_escritural,0) + coalesce(b.vl_acrescimo,0) - coalesce(b.vl_desconto,0) + coalesce(b.vl_juros,0) + coalesce(b.vl_multa,0) + coalesce(b.vl_despesa,0) vl_liquido, 
	coalesce(b.vl_escritural,0), 
	coalesce(b.vl_acrescimo,0), 
	coalesce(b.vl_desconto,0), 
	coalesce(b.vl_juros,0), 
	coalesce(b.vl_multa,0), 
	coalesce(b.vl_despesa,0), 
	b.cd_ocorrencia, 
	b.ie_tipo_documento, 
	b.ie_tipo_servico, 
	a.cd_moeda 
FROM banco_escritural d, titulo_pagar_escrit b, titulo_pagar a
LEFT OUTER JOIN pessoa_fisica c ON (a.cd_pessoa_fisica = c.cd_pessoa_fisica)
WHERE a.nr_titulo		= b.nr_titulo and d.nr_sequencia		= b.nr_seq_escrit  and coalesce(d.nr_remessa, d.nr_sequencia)	= nr_seq_remessa_w and CASE WHEN ie_tipo_inscricao_w=1 THEN c.nr_cpf WHEN ie_tipo_inscricao_w=2 THEN a.cd_cgc END  = nr_inscricao_w;


BEGIN 
 
begin 
select	b.cd_reg_favorecido, 
	b.cd_conf_envio, 
	b.cd_retorno_liq, 
	a.cd_estabelecimento, 
	a.nr_seq_conta_banco, 
	a.nr_seq_trans_financ 
into STRICT	cd_reg_favorecido_w, 
	cd_conf_envio_w, 
	cd_retorno_liq_w, 
	cd_estabelecimento_w, 
	nr_seq_conta_banco_w, 
	nr_seq_trans_escrit_w 
from	banco_retorno_cp b, 
	banco_escritural a 
where	a.cd_banco		= b.cd_banco 
and	a.nr_sequencia		= nr_seq_banco_escrit_p;
exception 
when others then 
	/* Cadastro do retorno de pagamento escritural nÃ£o encontrado! Verifica os cadastros financeiros. */
 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(198870);
end;
 
if (coalesce(nr_seq_trans_escrit_w::text, '') = '') then 
 
	select	nr_seq_trans_escrit 
	into STRICT	nr_seq_trans_escrit_w 
	from	parametro_tesouraria 
	where	cd_estabelecimento	= cd_estabelecimento_w;
 
end if;
 
select	coalesce(cd_tipo_baixa_padrao, 1) 
into STRICT	cd_tipo_baixa_w 
from	parametros_contas_pagar 
where	cd_estabelecimento	= cd_estabelecimento_w;
 
ie_vl_associado_w := obter_param_usuario(857, 33, Obter_Perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_vl_associado_w);
 
select	somente_numero(substr(max(ds_conteudo), 158, 6)) 
into STRICT	nr_seq_remessa_w 
from	w_interf_retorno_itau 
where	substr(ds_conteudo, 8, 1)	= '0' 
and	nr_seq_banco_escrit		= nr_seq_banco_escrit_p 
order	by nr_sequencia;
 
open c01;
loop 
fetch c01 into 
	nr_sequencia_inicial_w, 
	ds_forma_pagto_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	select	min(nr_sequencia) 
	into STRICT	nr_sequencia_final_w 
	from	w_interf_retorno_itau 
	where	substr(ds_conteudo, 8, 1)	<> '3' 
	and	nr_seq_banco_escrit		= nr_seq_banco_escrit_p 
	and	nr_sequencia			> nr_sequencia_inicial_w;
	 
	open c02;
	loop 
	fetch c02 into 
		nr_seq_interf_w, 
		ds_nr_titulo_w, 
		ds_dt_pagamento_w, 
		ds_vl_pagamento_w, 
		ds_nr_documento_w, 
		ds_ocorrencia_w, 
		ds_vl_real_w, 
		ds_segmento_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin 
		 
		if (ds_segmento_w = 'A') then 
		 
			select	min(a.nr_sequencia) 
			into STRICT	nr_seq_segmento_b_w 
			from	w_interf_retorno_itau a 
			where	a.nr_sequencia	> nr_seq_interf_w 
			and	substr(a.ds_conteudo,14,1)	= 'B';
			 
			select	substr(a.ds_conteudo,18,1), 
				substr(a.ds_conteudo,19,14) 
			into STRICT	ie_tipo_inscricao_w, 
				nr_inscricao_w 
			from	w_interf_retorno_itau a 
			where	a.nr_sequencia	= nr_seq_segmento_b_w;
		 
		end if;
		 
		commit;
		open C03;
		loop 
		fetch C03 into	 
			nr_titulo_w, 
			vl_liquido_w, 
			vl_escritural_w, 
			vl_acrescimo_escrit_w, 
			vl_desconto_w, 
			vl_juros_w, 
			vl_multa_w, 
			vl_despesa_escrit_w, 
			cd_ocorrencia_w, 
			ie_tipo_documento_w, 
			ie_tipo_servico_w, 
			cd_moeda_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin 
 
			if (ds_dt_pagamento_w	= '00000000') or (ds_dt_pagamento_w	= '99999999') then 
				dt_pagamento_w	:= clock_timestamp();
			else 
				dt_pagamento_w	:= to_date(ds_dt_pagamento_w, 'ddmmyyyy');
			end if;
 
			/*vl_pagamento_w		:= to_number(ds_vl_pagamento_w); 
			vl_pagamento_w		:= dividir_sem_round(vl_pagamento_w, 100);*/
 
			nr_documento_w		:= ds_nr_documento_w;
			vl_real_w		:= somente_numero(ds_vl_real_w) / 100;
 
			--gerar_titulo_escritural(nr_titulo_w,nr_seq_banco_escrit_p,nm_usuario_p); 
			 
			insert	into Titulo_Pagar_escrit( 
				nr_seq_escrit, 
				nr_titulo, 
				dt_atualizacao, 
				nm_usuario, 
				cd_ocorrencia, 
				vl_escritural, 
				vl_desconto, 
				vl_acrescimo, 
				ie_tipo_documento, 
				ie_tipo_servico, 
				ie_moeda, 
				vl_juros, 
				vl_multa, 
				vl_despesa) 
			values (nr_seq_banco_escrit_p, 
				nr_titulo_w, 
				clock_timestamp(), 
				nm_usuario_p, 
				cd_ocorrencia_w, 
				vl_escritural_w, 
				vl_desconto_w, 
				vl_acrescimo_escrit_w, 
				ie_tipo_documento_w, 
				ie_tipo_servico_w, 
				cd_moeda_w, 
				vl_juros_w, 
				vl_multa_w, 
				vl_despesa_escrit_w);
			 
			if (cd_reg_favorecido_w = ds_ocorrencia_w) then 
				update	titulo_pagar_escrit 
				set	ds_erro			= ds_ocorrencia_w 
				where	nr_seq_escrit		= nr_seq_banco_escrit_p 
				and	nr_titulo		= nr_titulo_w;
				 
				commit;
			elsif (cd_conf_envio_w = ds_ocorrencia_w) then 
				update	titulo_pagar_escrit 
				set	ds_erro			= ds_ocorrencia_w 
				where	nr_seq_escrit		= nr_seq_banco_escrit_p 
				and	nr_titulo		= nr_titulo_w;
				 
				commit;
			elsif (cd_retorno_liq_w = ds_ocorrencia_w) then 
 
				RAISE NOTICE 'nr_titulo_w = %', nr_titulo_w;
 
				select	max(a.vl_saldo_titulo), 
					max(a.vl_outras_despesas), 
					max(a.vl_outros_acrescimos) 
				into STRICT	vl_saldo_titulo_w, 
					vl_despesa_w, 
					vl_acrescimo_w 
				from	titulo_pagar a 
				where	a.nr_titulo	= nr_titulo_w;
				 
				vl_pagamento_w	:= vl_liquido_w;
 
				if (ie_vl_associado_w	= 'S') then 
 
					vl_pagamento_w	:= coalesce(vl_pagamento_w,0) - coalesce(vl_despesa_w,0) - coalesce(vl_acrescimo_w,0);
 
				end if;
 
				if (coalesce(vl_pagamento_w,0)	= 0) then 
 
					vl_pagamento_w	:= vl_real_w;
 
				end if;
 
				qt_baixa_w	:= coalesce(qt_baixa_w,0) + 1;
				 
				--Gerar_W_Tit_Pag_imposto(nr_titulo_w, nm_usuario_p); 
 
				CALL baixa_titulo_pagar(cd_estabelecimento_w, 
						cd_tipo_baixa_w, 
						nr_titulo_w, 
						vl_pagamento_w, 
						nm_usuario_p, 
						nr_seq_trans_escrit_w, 
						null, 
						nr_seq_banco_escrit_p, 
						dt_pagamento_w, 
						nr_seq_conta_banco_w);
 
				select	max(nr_sequencia) 
				into STRICT	nr_sequencia_w 
				from	titulo_pagar_baixa 
				where	nr_titulo	= nr_titulo_w;
 
				CALL gerar_movto_tit_baixa(nr_titulo_w, 
						nr_sequencia_w, 
						'P', 
						nm_usuario_p, 
						'N');
 
				CALL atualizar_saldo_tit_pagar(nr_titulo_w, nm_usuario_p);
 
				update	titulo_pagar_escrit 
				set	ds_erro			= ds_ocorrencia_w 
				where	nr_seq_escrit		= nr_seq_banco_escrit_p 
				and	nr_titulo		= nr_titulo_w;
				 
				commit;
			else 
				update	titulo_pagar_escrit 
				set	ds_erro			= ds_ocorrencia_w 
				where	nr_seq_escrit		= nr_seq_banco_escrit_p 
				and	nr_titulo		= nr_titulo_w;
				 
				commit;
			end if;
			 
			exception 
			when others then 
				rollback;
				delete	from w_interf_retorno_itau 
				where	nr_seq_banco_escrit	= nr_seq_banco_escrit_p;
				commit;
				/* sqlerrm 
				nr_titulo_w = nr_titulo_w 
				ds_nr_titulo_w = ds_nr_titulo_w 
				ds_dt_pagamento_w = ds_dt_pagamento_w 
				ds_vl_pagamento_w = ds_vl_pagamento_w 
				ds_nr_documento_w = ds_nr_documento_w 
				ds_ocorrencia_w = ds_ocorrencia_w */
 
				CALL wheb_mensagem_pck.exibir_mensagem_abort(198871,	'SQL_ERRM=' || sqlerrm || 
										';NR_TITULO_W=' || nr_titulo_w || 
										';DS_NR_TITULO_W=' || ds_nr_titulo_w || 
										';DS_DT_PAGAMENTO_W=' || ds_dt_pagamento_w || 
										';DS_VL_PAGAMENTO_W=' || ds_vl_pagamento_w || 
										';DS_NR_DOCUMENTO_W=' || ds_nr_documento_w || 
										';DS_OCORRENCIA_W=' || ds_ocorrencia_w);
			end;
		end loop;
		close C03;
		 
		end;
	end loop;
	close c02;
	 
	end;
end loop;
close c01;
 
if (coalesce(qt_baixa_w,0)	> 0) then 
 
	update	banco_escritural 
	set	dt_baixa	= clock_timestamp() 
	where	nr_sequencia	= nr_seq_banco_escrit_p;
 
end if;
 
delete	from w_interf_retorno_itau 
where	nr_seq_banco_escrit	= nr_seq_banco_escrit_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_ret_pagto_febraban_agrup (nr_seq_banco_escrit_p bigint, nm_usuario_p text) FROM PUBLIC;

