SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_ret_pag_santander_nbaixa ( nr_seq_banco_escrit_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/* 
Layout CNAB 240 - PgFor Trib Conc SET-2009_3.pdf 
 
*/
 
 
ds_dt_liquidacao_w			varchar(255);
ds_vl_liquidacao_w			varchar(255);
cd_arquivo_w			varchar(255);

cd_retorno_liq_w			varchar(50);

nr_titulo_w			bigint;
cd_tipo_baixa_w			bigint;
nr_seq_trans_escrit_w		bigint;
nr_seq_conta_banco_w		bigint;
nr_sequencia_w			bigint;
dt_liquidacao_w			timestamp;
vl_liquidacao_w			double precision;
qt_reg_w				bigint;
cd_estabelecimento_w		bigint;
vl_escritural_w			double precision;
cd_tipo_baixa_padrao_w		parametros_contas_pagar.cd_tipo_baixa_padrao%type;

cd_ocorrencia1_w			varchar(255);
cd_ocorrencia2_w			varchar(255);
cd_ocorrencia3_w			varchar(255);
cd_ocorrencia4_w			varchar(255);
cd_ocorrencia5_w			varchar(255);
cd_banco_w			bigint;
ie_estornar_baixa_w		varchar(10);
nr_seq_baixa_w			bigint;

dt_remessa_retorno_w		timestamp;
ds_nr_titulo_w			varchar(20);
nr_seq_trans_fin_baixa_w	titulo_pagar.nr_seq_trans_fin_baixa%type;
cd_ocorrencia_ret_banco_w	erro_escritural.cd_erro%type;

ie_liquidacao_w			varchar(1);
ds_desconto_w			varchar(255);
vl_desconto_w			titulo_pagar_escrit.vl_desconto%type;

c01 CURSOR FOR 
SELECT	substr(ds_string,74,20) nr_titulo, 
	substr(ds_string,94,8)			ds_dt_liquidacao, 
	substr(ds_string,120,15)		ds_vl_liquidacao, 
	substr(ds_string,231,2)			cd_ocorrencia1, 
	substr(ds_string,233,2)			cd_ocorrencia2, 
	substr(ds_string,235,2)			cd_ocorrencia3, 
	substr(ds_string,237,2)			cd_ocorrencia4, 
	substr(ds_string,239,2)			cd_ocorrencia5, 
	'0'								vl_desconto 
from	w_retorno_banco 
where	substr(ds_string,8,1)	= '3' 
and	substr(ds_string,14,1)	= 'A' 
and	nr_seq_banco_escrit	= nr_seq_banco_escrit_p 

union
 
SELECT	substr(ds_string,18,20) nr_titulo, 
	substr(ds_string,88,8)			ds_dt_liquidacao, 
	substr(ds_string,96,15)			ds_vl_liquidacao, 
	substr(ds_string,231,2)			cd_ocorrencia1, 
	substr(ds_string,233,2)			cd_ocorrencia2, 
	substr(ds_string,235,2)			cd_ocorrencia3, 
	substr(ds_string,237,2)			cd_ocorrencia4, 
	substr(ds_string,239,2)			cd_ocorrencia5, 
	'0'								vl_desconto 
from	w_retorno_banco 
where	substr(ds_string,8,1)	= '3' 
and	substr(ds_string,14,1)	= 'N' 
and	nr_seq_banco_escrit	= nr_seq_banco_escrit_p 

union
 
select	substr(ds_string,183,10) nr_titulo, 
	substr(ds_string,145,8)			ds_dt_liquidacao, 
	substr(ds_string,153,15)		ds_vl_liquidacao, 
	substr(ds_string,231,2)			cd_ocorrencia1, 
	substr(ds_string,233,2)			cd_ocorrencia2, 
	substr(ds_string,235,2)			cd_ocorrencia3, 
	substr(ds_string,237,2)			cd_ocorrencia4, 
	substr(ds_string,239,2)			cd_ocorrencia5, 
	substr(ds_string,115,15)		vl_desconto 
from	w_retorno_banco 
where	substr(ds_string,18,2)	<> '52' 
and	substr(ds_string,8,1)	= '3' 
and	substr(ds_string,14,1)	= 'J' 
and	nr_seq_banco_escrit	= nr_seq_banco_escrit_p 

union
 
/*Segmento O - Pagamento de Contas e tributos com código de barras Implementando em 25/04/2013 */
 
select	substr(ds_string,123,20) nr_titulo, 
	substr(ds_string,100,8)			ds_dt_liquidacao, 
	substr(ds_string,108,15)		ds_vl_liquidacao, 
	substr(ds_string,231,2)			cd_ocorrencia1, 
	substr(ds_string,233,2)			cd_ocorrencia2, 
	substr(ds_string,235,2)			cd_ocorrencia3, 
	substr(ds_string,237,2)			cd_ocorrencia4, 
	substr(ds_string,239,2)			cd_ocorrencia5, 
	'0'								vl_desconto 
from	w_retorno_banco 
where	substr(ds_string,8,1)	= '3' 
and	substr(ds_string,14,1)	= 'O' 
and	nr_seq_banco_escrit	= nr_seq_banco_escrit_p;


BEGIN 
 
begin 
select	b.cd_retorno_liq, 
	a.cd_estabelecimento, 
	a.nr_seq_conta_banco, 
	a.cd_banco, 
	a.dt_remessa_retorno 
into STRICT	cd_retorno_liq_w, 
	cd_estabelecimento_w, 
	nr_seq_conta_banco_w, 
	cd_banco_w, 
	dt_remessa_retorno_w 
from	banco_retorno_cp b, 
	banco_escritural a 
where	a.cd_banco		= b.cd_banco 
and	a.nr_sequencia		= nr_seq_banco_escrit_p;
exception 
	when no_data_found then 
	/* Não foi encontrado o código de retorno da liquidação! 
	Verifique o cadastro de "Retorno CP" no cadastro de bancos. */
 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(192789);
end;
 
select	max(nr_seq_trans_escrit) 
into STRICT	nr_seq_trans_escrit_w 
from	parametro_tesouraria 
where	cd_estabelecimento	= cd_estabelecimento_w;
 
select	coalesce(max(cd_tipo_baixa_padrao), 1) 
into STRICT	cd_tipo_baixa_padrao_w 
from	parametros_contas_pagar 
where	cd_estabelecimento	= cd_estabelecimento_w;
 
select	substr(ds_string,143,1) 
into STRICT	cd_arquivo_w 
from 	w_retorno_banco 
where	substr(ds_string,8,1) = '0' 
and	substr(ds_string,4,4) = '0000' 
and	nr_seq_banco_escrit	= nr_seq_banco_escrit_p;
 
if (cd_arquivo_w	= '2')	then 
 
	open c01;
	loop 
	fetch c01 into 
		ds_nr_titulo_w, 
		ds_dt_liquidacao_w, 
		ds_vl_liquidacao_w, 
		cd_ocorrencia1_w, 
		cd_ocorrencia2_w, 
		cd_ocorrencia3_w, 
		cd_ocorrencia4_w, 
		cd_ocorrencia5_w, 
		ds_desconto_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		 
		nr_titulo_w		:= somente_numero(ds_nr_titulo_w);
		dt_liquidacao_w		:= to_date(ds_dt_liquidacao_w,'DD/MM/YYYY');
		vl_liquidacao_w		:= somente_numero(ds_vl_liquidacao_w);
		vl_liquidacao_w		:= dividir_sem_round((vl_liquidacao_w)::numeric,100);
		vl_desconto_w		:= somente_numero(ds_desconto_w);
		vl_desconto_w		:= dividir_sem_round((vl_desconto_w)::numeric,100);		
 
		select	count(*) 
		into STRICT	qt_reg_w 
		from	titulo_pagar_escrit 
		where	nr_seq_escrit	= nr_seq_banco_escrit_p 
		and	nr_titulo			= nr_titulo_w;
 
		if (qt_reg_w = 0) then 
			CALL gerar_titulo_escritural(nr_titulo_w,nr_seq_banco_escrit_p,nm_usuario_p);
 
			qt_reg_w	:= 1;
		end if;
 
		ie_liquidacao_w	:= 'N';
 
		if (cd_retorno_liq_w IS NOT NULL AND cd_retorno_liq_w::text <> '') and (cd_retorno_liq_w	in (cd_ocorrencia1_w,cd_ocorrencia2_w,cd_ocorrencia3_w,cd_ocorrencia4_w,cd_ocorrencia5_w)) then 
 
			ie_liquidacao_w	:= 'S';
 
		else 
 
			select	coalesce(max('S'),'N') 
			into STRICT	ie_liquidacao_w 
			from	erro_escritural b 
			where	b.cd_banco	= cd_banco_w 
			and	b.ie_rejeitado	= 'L' 
			and	b.cd_erro	in (cd_ocorrencia1_w,cd_ocorrencia2_w,cd_ocorrencia3_w,cd_ocorrencia4_w,cd_ocorrencia5_w);
 
		end if;
		 
		if (coalesce(ie_liquidacao_w,'N')	= 'S') and (qt_reg_w	> 0)	then 
 
			select	vl_escritural 
			into STRICT	vl_escritural_w 
			from	titulo_pagar_escrit 
			where	nr_seq_escrit	= nr_seq_banco_escrit_p 
			and	nr_titulo	= nr_titulo_w;
			/* Colocado este tratamento para não efetuar baixa com valor maior que o título */
 
			if (vl_liquidacao_w	> vl_escritural_w) then 
				vl_liquidacao_w	:= vl_escritural_w;
			end if;
 
			if (coalesce(vl_liquidacao_w,0)	<> 0) then 
 
				update	titulo_pagar_escrit 
				set	vl_liquidacao	= vl_liquidacao_w 
				where	nr_titulo	= nr_titulo_w 
				and	nr_seq_escrit	= nr_seq_banco_escrit_p;
 
			end if;
		else 
 
			if (qt_reg_w	= 0) then 
 
				CALL gerar_log_escritural('P',nr_seq_banco_escrit_p,nm_usuario_p,'O título ' || ds_nr_titulo_w || ' não consta no sistema!','N');
 
			end if;
 
			select	coalesce(max(ie_estornar_baixa),'N') 
			into STRICT	ie_estornar_baixa_w 
			from	erro_escritural 
			where	cd_erro		in (cd_ocorrencia1_w,cd_ocorrencia2_w,cd_ocorrencia3_w,cd_ocorrencia4_w,cd_ocorrencia5_w) 
			and	cd_banco	= cd_banco_w;
 
			if (ie_estornar_baixa_w = 'S') then 
				begin 
 
				select	max(nr_sequencia) 
				into STRICT	nr_seq_baixa_w 
				from	titulo_pagar_baixa 
				where	nr_titulo	= nr_titulo_w 
				and	(nr_seq_escrit IS NOT NULL AND nr_seq_escrit::text <> '');
 
				if (nr_seq_baixa_w IS NOT NULL AND nr_seq_baixa_w::text <> '') then 
					CALL estornar_tit_pagar_baixa(nr_titulo_w, nr_seq_baixa_w, coalesce(dt_liquidacao_w,dt_remessa_retorno_w), nm_usuario_p,'S');
				end if;
				end;
			end if;
 
		end if;
 
		update	titulo_pagar_escrit 
		set	ds_erro			= substr(cd_ocorrencia1_w||cd_ocorrencia2_w||cd_ocorrencia3_w||cd_ocorrencia4_w||cd_ocorrencia5_w,1,120) 
		where	nr_titulo		= nr_titulo_w 
		and	nr_seq_escrit		= nr_seq_banco_escrit_p;
		 
		if (coalesce(vl_desconto_w, 0) > 0) then	 
			begin			 
			 
			update	titulo_pagar_escrit 
			set	vl_desconto		= vl_desconto_w 
			where	nr_titulo	= nr_titulo_w 
			and	nr_seq_escrit	= nr_seq_banco_escrit_p;
			 
			end;
		end if;
 
	end loop;
	close c01;
 
else 
	/* O arquivo que está sendo importado não é de retorno, favor verifique! */
 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(192790);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_ret_pag_santander_nbaixa ( nr_seq_banco_escrit_p bigint, nm_usuario_p text) FROM PUBLIC;

