CREATE OR REPLACE PROCEDURE gerar_revisao_faq () AS $body$
DECLARE


dt_aprovacao_w		timestamp;
nr_sequencia_w		bigint;
cd_funcao_w		varchar(10);
nm_usuario_exec_w	varchar(50);

c01 CURSOR FOR
	SELECT	nr_sequencia,
		dt_aprovacao,
		cd_funcao
	from	com_faq;


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	dt_aprovacao_w,
	cd_funcao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		if	PKG_DATE_UTILS.start_of(dt_aprovacao_w, 'dd', 0) >= PKG_DATE_UTILS.start_of(PKG_DATE_UTILS.ADD_MONTH(clock_timestamp(), 12, 0), 'dd', 0) then

			update	com_faq
			set	ie_revisado = 'n'
			where	nr_sequencia = nr_sequencia_w;

			select	max(nm_usuario_exec)
			into STRICT	nm_usuario_exec_w
			from	funcao_grupo_sup
			where	cd_funcao = cd_funcao_w;

			envia_ci_alteracao_faq(nr_sequencia_w, nm_usuario_exec_w);
		end if;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
