SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_revisao_proc_interno (nr_seq_proc_interno_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_classif_w	bigint;
nr_seq_estagio_w	bigint;
ie_ordem_w		bigint;
cd_estabelecimento_w	integer := wheb_usuario_pck.Get_cd_estabelecimento;
ie_permite_loc_revisao_w	varchar(5) := 'S';
QT_PROC_REVISAO_W	bigint;
C01 CURSOR FOR
	SELECT	nr_seq_estagio,
		ie_ordem
	from	regra_rev_estagio_classif
	where	nr_seq_classif = nr_seq_classif_w
	order by ie_ordem desc;



BEGIN

ie_permite_loc_revisao_w := obter_param_usuario(947, 75, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_permite_loc_revisao_w);

select 	coalesce(max(nr_seq_classif),0)
into STRICT	nr_seq_classif_w
from 	proc_interno
where 	nr_sequencia = nr_seq_proc_interno_p;

select	count(*)
into STRICT	qt_proc_revisao_w
from	proc_interno_revisao
where	nr_seq_proc_interno = nr_seq_proc_interno_p
and	coalesce(dt_liberacao::text, '') = '';

if (nr_seq_classif_w > 0) and (qt_proc_revisao_w = 0)then

	nr_seq_estagio_w:= 0;
	open C01;
	loop
	fetch C01 into
		nr_seq_estagio_w,
		ie_ordem_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		nr_seq_estagio_w:= nr_seq_estagio_w;
		end;
	end loop;
	close C01;

	if (nr_seq_estagio_w > 0) then

		insert into proc_interno_revisao(nr_sequencia,
			nr_seq_proc_interno,
			nr_seq_estagio,
			nr_seq_classif,
			dt_revisao,
			nm_usuario_revisao,
			dt_liberacao,
			ie_ordem,
			dt_atualizacao,
			nm_usuario)
		values (nextval('proc_interno_revisao_seq'),
			nr_seq_proc_interno_p,
			nr_seq_estagio_w,
			nr_seq_classif_w,
			clock_timestamp(),
			nm_usuario_p,
			null,
			ie_ordem_w,
			clock_timestamp(),
			nm_usuario_p);

		if (coalesce(ie_permite_loc_revisao_w,'S') = 'N') THEN

			update	proc_interno
			set	IE_LOCALIZADOR 	= 'N',
				nm_usuario	= nm_usuario_p,
				dt_atualizacao  = clock_timestamp()
			where	nr_sequencia	= nr_seq_proc_interno_p
			and	ie_localizador	= 'S';
		end if;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
