CREATE OR REPLACE PROCEDURE gerar_saida_real_alta ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, cd_perfil_p bigint, nm_usuario_p text, ds_consistencia_p INOUT text, ie_permite_inicio_higienizar_p INOUT text, ie_permite_fim_higienizar_p INOUT text) AS $body$
DECLARE

		 
ie_permite_inicio_higienizar_w	varchar(1)	:= 'S';
ie_permite_fim_higienizar_w	varchar(1)	:= 'S';
ds_consistencia_w		varchar(255)	:= '';
cd_estabelecimento_p		integer;


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then 
	begin	 
	ie_permite_inicio_higienizar_w	:= obter_se_regra_lib_setor_higie(cd_setor_atendimento_p, cd_perfil_p, nm_usuario_p, 0);
	ie_permite_fim_higienizar_w	:= obter_se_regra_lib_setor_higie(cd_setor_atendimento_p, cd_perfil_p, nm_usuario_p, 1);
	 
	ds_consistencia_w := consiste_regra_alta_saida_real(clock_timestamp(), nr_atendimento_p, cd_setor_atendimento_p, ds_consistencia_w, nm_usuario_p);
	 
	if (coalesce(ds_consistencia_w::text, '') = '') then 
		begin 
		update	atendimento_paciente 
		set	dt_saida_real		= clock_timestamp(), 
			nm_usuario_saida	= nm_usuario_p 
		where	nr_atendimento		= nr_atendimento_p;
		 
		CALL avisar_higienizacao_camareira(cd_setor_atendimento_p, cd_unidade_basica_p, cd_unidade_compl_p, 'S');		
		end;
	end if;	
	end;
end if;
 
ie_permite_inicio_higienizar_p	:= ie_permite_inicio_higienizar_w;
ie_permite_fim_higienizar_p	:= ie_permite_fim_higienizar_w;
ds_consistencia_p		:= ds_consistencia_w;
 
commit;
 
end;
$body$
