CREATE OR REPLACE PROCEDURE gerar_saida_real_local ( cd_estabelecimento_p bigint, nr_atendimento_p bigint, nr_seq_local_destino_p bigint, nm_usuario_p text, dt_saida_real_p timestamp) AS $body$
DECLARE

 
cd_pessoa_fisica_w		varchar(10);
nr_seq_evento_w			bigint;
							
c04 CURSOR FOR 
	SELECT	a.nr_seq_evento 
	from	regra_envio_sms a 
	where	a.cd_estabelecimento	= cd_estabelecimento_p 
	and	a.ie_evento_disp	= 'ASR' 
	and	coalesce(ie_situacao,'A') = 'A';			
			 

BEGIN 
 
update 	atendimento_paciente 
set	dt_saida_real 		= coalesce(dt_saida_real_p,clock_timestamp()), 
	nm_usuario_saida 	= nm_usuario_p, 
	nm_usuario	 	= nm_usuario_p, 
	dt_atualizacao	 	= clock_timestamp(), 
	nr_seq_local_destino	= CASE WHEN nr_seq_local_destino_p=0 THEN null  ELSE nr_seq_local_destino_p END  
where 	nr_atendimento = nr_atendimento_p;		
 
 
select	max(cd_pessoa_fisica) 
into STRICT 	cd_pessoa_fisica_w 
from 	atendimento_paciente 
where 	nr_atendimento = nr_atendimento_p;
 
open c04;
	loop 
	fetch c04 into	 
		nr_seq_evento_w;
	EXIT WHEN NOT FOUND; /* apply on c04 */
		begin 
		CALL gerar_evento_paciente_trigger(nr_seq_evento_w,nr_atendimento_p,cd_pessoa_fisica_w,null,nm_usuario_p);
		end;
	end loop;
	close c04;
 
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
