CREATE OR REPLACE PROCEDURE gerar_saida_visitante_estab ( dt_parametro_p timestamp, cd_estabelecimento_p bigint) AS $body$
BEGIN

if (dt_parametro_p IS NOT NULL AND dt_parametro_p::text <> '') then
	begin
	
	update	atendimento_visita
	set	dt_saida		= clock_timestamp(),
		nm_usuario	= 'TasyJob',
		dt_atualizacao	= clock_timestamp(),
		ie_status = 'F',		
		nm_usuario_saida = 'TasyJob'
	where	trunc(dt_entrada)	= trunc(dt_parametro_p)
	and	dt_entrada < dt_parametro_p
	and	((obter_estab_atendimento(nr_atendimento) = coalesce(cd_estabelecimento_p,0)) or (obter_dados_setor(cd_setor_atendimento,'E') = coalesce(cd_estabelecimento_p,0)) or (coalesce(cd_estabelecimento_p,0) = 0))
	and	coalesce(dt_saida::text, '') = '';

	commit;
	
	end;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
