CREATE OR REPLACE PROCEDURE gerar_saida_visita_acomp ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	
	begin
	update	atendimento_visita
	set	dt_saida = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		ie_status = 'F',
		nm_usuario_saida = nm_usuario_p
	where 	nr_atendimento = nr_atendimento_p
	and	coalesce(dt_saida::text, '') = '';
	
	update atendimento_acompanhante
	set	dt_saida = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_atendimento = nr_atendimento_p
	and	coalesce(dt_saida::text, '') = '';
	
	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
