CREATE OR REPLACE PROCEDURE gerar_same_cpi_prontuario ( nr_prontuario_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w			bigint;
cd_estabelecimento_w		smallint;
nr_sequencia_w			bigint;
ie_emprestar_w			varchar(1);
cd_setor_atendimento_w		integer;
nr_seq_emp_w			bigint;


BEGIN

if (nr_prontuario_p IS NOT NULL AND nr_prontuario_p::text <> '') then

	select	coalesce(cd_estabelecimento_p,min(cd_estabelecimento))
	into STRICT	cd_estabelecimento_w
	from	estabelecimento;

	select	count(*)
	into STRICT	qt_existe_w
	from	same_cpi_prontuario
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	nr_prontuario		= nr_prontuario_p;

	if (qt_existe_w = 0) then
		select	nextval('same_cpi_prontuario_seq')
		into STRICT	nr_sequencia_w
		;

		insert into same_cpi_prontuario(
			nr_sequencia,
			cd_estabelecimento,
			dt_atualizacao,
			nm_usuario,
			nr_prontuario,
			cd_pessoa_fisica,
			qt_volume,
			ds_localizacao,
			dt_atualizacao_nrec,
			nm_usuario_nrec)
		values (	nr_sequencia_w,
			cd_estabelecimento_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_prontuario_p,
			cd_pessoa_fisica_p,
			1,
			null,
			clock_timestamp(),
			nm_usuario_p);

		ie_emprestar_w := Obter_Param_Usuario(1007, 11, obter_perfil_ativo, nm_usuario_p, coalesce(cd_estabelecimento_p,0), ie_emprestar_w);

		if (ie_emprestar_w = 'S') then
			begin
			select	coalesce(max(cd_setor_atendimento),0)
			into STRICT	cd_setor_atendimento_w
			from	usuario
			where	nm_usuario	= nm_usuario_p;

			if (cd_setor_atendimento_w > 0) then
				begin
				select	nextval('same_cpi_emp_seq')
				into STRICT	nr_seq_emp_w
				;

				insert into same_cpi_emp(
					nr_sequencia,
					cd_estabelecimento,
					nr_prontuario,
					dt_atualizacao,
					nm_usuario,
					cd_setor_atendimento,
					dt_emprestimo,
					nm_usuario_empr,
					ie_operacao,
					dt_atualizacao_nrec,
					nm_usuario_nrec)
				values (	nr_seq_emp_w,
					cd_estabelecimento_w,
					nr_prontuario_p,
					clock_timestamp(),
					nm_usuario_p,
					cd_setor_atendimento_w,
					clock_timestamp(),
					nm_usuario_p,
					'E',
					clock_timestamp(),
					nm_usuario_p);
				end;
			end if;
			end;
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
