SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_sbs_export_pf_atend ( dt_inicial_p timestamp, dt_final_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
ds_registro_hospitalar_w		varchar(10);
nm_pessoa_fisica_w		varchar(50);
ds_endereco_w			varchar(40);
ds_bairro_w			varchar(30);
cd_cep_w			varchar(8);
nm_pai_w			varchar(40);
nm_mae_w			varchar(40);
ds_tipo_documento_w		varchar(10);
nr_documento_w			varchar(20);
ie_sexo_w			varchar(1);
nm_hospital_w			varchar(40);
dt_internacao_w			varchar(8);
nr_quarto_w			varchar(5);
nr_leito_w			varchar(5);
dt_nascimento_w			varchar(8);
cd_internacao_clinica_w		varchar(10);
nm_pessoa_resp_w			varchar(40);
ds_endereco_resp_w		varchar(50);
nr_telefone_resp_w			varchar(30);
hr_internacao_w			varchar(4);
dt_alta_w				varchar(8);
dt_obito_w			varchar(8);
ds_convenio_abrev_w		varchar(10);
nr_atendimento_w			bigint;
nr_seq_int_catc_w			bigint;
nr_seq_int_unid_w			bigint;

C01 CURSOR FOR 
	SELECT	a.cd_pessoa_fisica, 
		substr(a.nm_pessoa_fisica,1,50) nm_pessoa_fisica		 
	from	pessoa_fisica a 
	where	a.dt_atualizacao between dt_inicial_p and dt_final_p 
	
union
 
	SELECT	a.cd_pessoa_fisica, 
		substr(a.nm_pessoa_fisica,1,50) nm_pessoa_fisica 
	from	compl_pessoa_fisica b, 
		pessoa_fisica a 
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
	and	b.dt_atualizacao between dt_inicial_p and dt_final_p 
	
union
 
	select	a.cd_pessoa_fisica, 
		substr(a.nm_pessoa_fisica,1,50) nm_pessoa_fisica 
	from	atendimento_paciente b, 
		pessoa_fisica a 
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
	and	b.dt_atualizacao between dt_inicial_p and dt_final_p 
	
union
 
	select	a.cd_pessoa_fisica, 
		substr(a.nm_pessoa_fisica,1,50) nm_pessoa_fisica 
	from	atend_categoria_convenio c, 
		atendimento_paciente b, 
		pessoa_fisica a 
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
	and	b.nr_atendimento = c.nr_atendimento 
	and	c.nr_seq_interno = obter_atecaco_atendimento(b.nr_atendimento) 
	and	c.dt_atualizacao between dt_inicial_p and dt_final_p 
	
union
 
	select	a.cd_pessoa_fisica, 
		substr(a.nm_pessoa_fisica,1,50) nm_pessoa_fisica 
	from	atend_paciente_unidade c, 
		atendimento_paciente b, 
		pessoa_fisica a 
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
	and	b.nr_atendimento = c.nr_atendimento 
	and	c.nr_seq_interno = obter_atepacu_paciente(b.nr_atendimento,'A') 
	and	c.dt_atualizacao between dt_inicial_p and dt_final_p 
	order by 2,1;


BEGIN 
delete FROM w_sbs_export_pf_atend where nm_usuario = nm_usuario_p;
 
open C01;
loop 
fetch C01 into 
	ds_registro_hospitalar_w, 
	nm_pessoa_fisica_w;	
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	dt_internacao_w		:= null;
	hr_internacao_w		:= null;
	dt_alta_w			:= null;
	nm_pessoa_resp_w		:= null;
	ds_endereco_resp_w	:= null;
	nr_telefone_resp_w		:= null;
	ds_convenio_abrev_w	:= null;
	nm_hospital_w		:= null;
	cd_internacao_clinica_w	:= null;
	nr_leito_w		:= null;
	 
	select	substr(coalesce(obter_compl_pf(a.cd_pessoa_fisica,1,'EDN'),obter_compl_pf(a.cd_pessoa_fisica,3,'EDN')),1,40) ds_endereco, 
		substr(coalesce(obter_compl_pf(a.cd_pessoa_fisica,1,'B'),obter_compl_pf(a.cd_pessoa_fisica,3,'B')),1,30) ds_bairro, 
		substr(coalesce(obter_compl_pf(a.cd_pessoa_fisica,1,'CEP'),obter_compl_pf(a.cd_pessoa_fisica,3,'CEP')),1,8) cd_cep, 
		substr(obter_compl_pf(a.cd_pessoa_fisica,4,'N'),1,40) nm_pai, 
		substr(coalesce(obter_compl_pf(a.cd_pessoa_fisica,1,'N'),coalesce(obter_compl_pf(a.cd_pessoa_fisica,3,'N'),obter_compl_pf(a.cd_pessoa_fisica,5,'N'))),1,40) nm_mae, 
		CASE WHEN coalesce(a.nr_cpf::text, '') = '' THEN CASE WHEN coalesce(a.nr_identidade::text, '') = '' THEN ''  ELSE 'RG' END   ELSE 'CPF' END  ds_tipo_documento, 
		coalesce(a.nr_cpf,a.nr_identidade) nr_documento, 
		a.ie_sexo, 
		to_char(a.dt_nascimento,'ddmmyyyy') dt_nascimento, 
		to_char(a.dt_obito,'ddmmyyyy') dt_obito, 
		substr(coalesce(a.nr_telefone_celular,obter_compl_pf(a.cd_pessoa_fisica,1,'T')),1,30) 
	into STRICT	ds_endereco_w, 
		ds_bairro_w, 
		cd_cep_w, 
		nm_pai_w, 
		nm_mae_w,	 
		ds_tipo_documento_w, 
		nr_documento_w, 
		ie_sexo_w, 
		dt_nascimento_w, 
		dt_obito_w, 
		nr_telefone_resp_w 
	from	pessoa_fisica a 
	where	cd_pessoa_fisica = ds_registro_hospitalar_w;
	 
	select	max(a.nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from	atendimento_paciente a 
	where	a.cd_pessoa_fisica = ds_registro_hospitalar_w 
	and	a.dt_entrada = (SELECT	max(x.dt_entrada) 
				from	atendimento_paciente x 
				where	x.cd_pessoa_fisica = a.cd_pessoa_fisica);				
	 
	if (coalesce(nr_atendimento_w,0) > 0) then 
		begin 
		select	to_char(a.dt_entrada,'ddmmyyyy') dt_internacao, 
			to_char(a.dt_entrada,'hh24mi') hr_internacao, 
			to_char(a.dt_alta,'ddmmyyyy') dt_alta, 
			substr(obter_nome_pf(a.cd_pessoa_responsavel),1,40) nm_pessoa_resp, 
			substr(obter_compl_pf(a.cd_pessoa_responsavel,1,'EC'),1,50) ds_endereco_resp, 
			substr(coalesce(nr_telefone_resp_w,obter_dados_pf(a.cd_pessoa_responsavel,'TC')),1,30) nr_telefone_resp, 
			substr(obter_atecaco_atendimento(a.nr_atendimento),1,10) nr_seq_int_catc, 
			substr(obter_atepacu_paciente(a.nr_atendimento,'A'),1,10) nr_seq_int_unid 
		into STRICT	dt_internacao_w, 
			hr_internacao_w, 
			dt_alta_w, 
			nm_pessoa_resp_w, 
			ds_endereco_resp_w, 
			nr_telefone_resp_w, 
			nr_seq_int_catc_w, 
			nr_seq_int_unid_w 
		from	atendimento_paciente a 
		where	a.nr_atendimento = nr_atendimento_w;
		 
		if (coalesce(nr_seq_int_catc_w,0) > 0) then 
			select	substr(b.nm_fantasia,1,10) ds_convenio_abrev 
			into STRICT	ds_convenio_abrev_w 
			from	convenio c, 
				pessoa_juridica b, 
				atend_categoria_convenio a	 
			where	a.cd_convenio = c.cd_convenio 
			and	c.cd_cgc = b.cd_cgc 
			and	a.nr_atendimento = nr_atendimento_w 
			and	a.nr_seq_interno = nr_seq_int_catc_w;
		end if;
		 
		if (coalesce(nr_seq_int_unid_w,0) > 0) then 
			select	substr(obter_dados_setor(b.cd_setor_atendimento,'DA'),1,40) nm_hospital, 
				b.cd_centro_custo, 
				substr(a.cd_unidade_compl,1,5) nr_leito 
			into STRICT	nm_hospital_w, 
				cd_internacao_clinica_w, 
				nr_leito_w 
			from	setor_atendimento b, 
				atend_paciente_unidade a 
			where	b.cd_setor_atendimento = a.cd_setor_atendimento 
			and	a.nr_atendimento = nr_atendimento_w 
			and	a.nr_seq_interno = nr_seq_int_unid_w;
		end if;
		end;
	end if;
	 
	insert into w_sbs_export_pf_atend( 
				nr_sequencia, 
				dt_atualizacao, 
				nm_usuario, 
				dt_atualizacao_nrec, 
				nm_usuario_nrec, 
				ds_registro_hospitalar, 
				nm_pessoa_fisica, 
				ds_endereco, 
				ds_bairro, 
				cd_cep, 
				nm_pai, 
				nm_mae, 
				ds_tipo_documento, 
				nr_documento, 
				ie_sexo, 
				nm_hospital, 
				dt_internacao, 
				nr_quarto, 
				nr_leito, 
				dt_nascimento, 
				cd_internacao_clinica, 
				nm_pessoa_resp, 
				ds_endereco_resp, 
				nr_telefone_resp, 
				hr_internacao, 
				dt_alta, 
				dt_obito, 
				ds_convenio_abrev) 
			values (	nextval('w_sbs_export_pf_atend_seq'), 
				clock_timestamp(), 
				nm_usuario_p, 
				clock_timestamp(), 
				nm_usuario_p, 
				ds_registro_hospitalar_w, 
				substr(elimina_caractere_especial(nm_pessoa_fisica_w),1,50), 
				substr(elimina_caractere_especial(ds_endereco_w),1,40), 
				substr(elimina_caractere_especial(ds_bairro_w),1,30), 
				cd_cep_w, 
				substr(elimina_caractere_especial(nm_pai_w),1,40), 
				substr(elimina_caractere_especial(nm_mae_w),1,40), 
				ds_tipo_documento_w, 
				nr_documento_w, 
				ie_sexo_w, 
				substr(elimina_caractere_especial(nm_hospital_w),1,40), 
				dt_internacao_w, 
				nr_quarto_w, 
				nr_leito_w, 
				dt_nascimento_w, 
				cd_internacao_clinica_w, 
				substr(elimina_caractere_especial(nm_pessoa_resp_w),1,40), 
				substr(elimina_caractere_especial(ds_endereco_resp_w),1,50), 
				nr_telefone_resp_w, 
				hr_internacao_w, 
				dt_alta_w, 
				dt_obito_w, 
				substr(elimina_caractere_especial(ds_convenio_abrev_w),1,10));
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_sbs_export_pf_atend ( dt_inicial_p timestamp, dt_final_p timestamp, nm_usuario_p text) FROM PUBLIC;

