CREATE OR REPLACE PROCEDURE gerar_senha_atendimento_eup ( nr_atendimento_p bigint, ie_tipo_atendimento_p bigint, nr_seq_regra_funcao_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_senha_nova_w	varchar(255);
cd_pessoa_fisica_w varchar(10);


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
end if;
 
 
ds_senha_nova_w := gerar_senha_atendimento(ie_tipo_atendimento_p, nr_seq_regra_funcao_p, cd_pessoa_fisica_w, '');
 
if (ds_senha_nova_w IS NOT NULL AND ds_senha_nova_w::text <> '') then -- Retirado da cláusula a condição de vazio, pois esta, não entrava quando a variável possuía valor - sbherkenhoff 
 
	update	atendimento_paciente 
	set	ds_senha = ds_senha_nova_w 
	where	nr_atendimento = nr_Atendimento_p;
		 
end if;
 
commit;
 
end;
$body$
