CREATE OR REPLACE PROCEDURE gerar_seq_lab_externo (nr_seq_lote_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lab_ext_w	bigint;
nr_prescricao_w		bigint;
nr_seq_prescr_w		integer;
c01 CURSOR FOR
SELECT	distinct
	nr_prescricao
from	prescr_procedimento
where	nr_seq_lote_externo = nr_seq_lote_p;

c02 CURSOR FOR
SELECT	distinct
	nr_sequencia
from 	prescr_procedimento
where 	nr_prescricao = nr_prescricao_w
and	nr_seq_lote_externo = nr_seq_lote_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	select	coalesce(max(somente_numero(nr_seq_lab_ext)),0)+1
	into STRICT	nr_seq_lab_ext_w
	from 	prescr_procedimento;

	if (nr_seq_lab_ext_w = 1) then

		select	coalesce(nr_Seq_inicial_externo,0)
		into STRICT	nr_seq_lab_ext_w
		from	lab_parametro
		where	cd_estabelecimento = cd_estabelecimento_p;

	end if;

	open c02;
	loop
	fetch c02 into
		nr_seq_prescr_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */

		update	prescr_procedimento
		set	nr_seq_lab_ext	= nr_seq_lab_ext_w,
			nm_usuario	= nm_usuario_p
		where	nr_prescricao 	= nr_prescricao_w
		and	nr_sequencia	= nr_seq_prescr_w;

	END LOOP;
	CLOSE C02;

END LOOP;
CLOSE C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
