CREATE OR REPLACE PROCEDURE gerar_servicos_agenda ( nr_seq_agenda_p bigint, ds_lista_servico_p text, nm_usuario_p text, ds_lista_erro_p INOUT text) AS $body$
DECLARE

		 
ds_lista_servico_w	varchar(4000);
ds_lista_erro_w		varchar(4000);
ds_erro_w		varchar(255);
nr_seq_proc_servico_w 	bigint;
ie_pos_virgula_w 	integer;
ie_pos_separador_w	integer;

BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (ds_lista_servico_p IS NOT NULL AND ds_lista_servico_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	ds_lista_servico_w := ds_lista_servico_p;
	 
	while(ds_lista_servico_w IS NOT NULL AND ds_lista_servico_w::text <> '') loop 
		begin 
		ie_pos_virgula_w	:= position(',' in ds_lista_servico_w);
		 
		if (ie_pos_virgula_w <> 0) then 
			begin 
			nr_seq_proc_servico_w	:= substr(ds_lista_servico_w,1,(ie_pos_virgula_w - 1));
			ds_lista_servico_w	:= substr(ds_lista_servico_w,(ie_pos_virgula_w + 1),length(ds_lista_servico_w));			
			end;
		else 
			begin			 
			nr_seq_proc_servico_w	:= (ds_lista_servico_w)::numeric;			
			ds_lista_servico_w	:= null;
			end;
		end if;
	 
		if (nr_seq_proc_servico_w IS NOT NULL AND nr_seq_proc_servico_w::text <> '') and (nr_seq_proc_servico_w > 0) then 
			begin	 
			ds_erro_w := null;
			ds_erro_w := gravar_servico_agenda( 
				nr_seq_proc_servico_w, nr_seq_agenda_p, nm_usuario_p, null, null, null, ds_erro_w);
			if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then 
				begin 
				ds_lista_erro_w := ds_lista_erro_w || chr(13) || chr(10) || obter_desc_proc_interno(nr_seq_proc_servico_w) || ' - ' || ds_erro_w;
				end;
			end if;
			end;
		end if;		
		end;
	end loop;
	end;
end if;
ds_lista_erro_p := ds_lista_erro_w;
commit;
end;
