CREATE OR REPLACE PROCEDURE gerar_servicos_paciente_atend ( nr_atendimento_p bigint, ds_lista_servicos_p text, nm_usuario_p text, lancar_conta_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
ds_lista_servico_w	varchar(255);	
tam_lista_w		varchar(255);	
ie_pos_virgula_w	varchar(255);
nr_seq_servico_w	varchar(255);	
qt_servicos_pac_w	bigint;		
 

BEGIN 
 
ds_lista_servico_w := substr(ds_lista_servicos_p,1,255);
 
if (coalesce(nr_atendimento_p,0) > 0) and (ds_lista_servicos_p IS NOT NULL AND ds_lista_servicos_p::text <> '') then 
 
	while(ds_lista_servico_w IS NOT NULL AND ds_lista_servico_w::text <> '') and (trim(both ds_lista_servico_w) <> ',') loop 
		tam_lista_w		:= length(ds_lista_servico_w);
		ie_pos_virgula_w	:= position(',' in ds_lista_servico_w);
		if (ie_pos_virgula_w <> 0) then 
			nr_seq_servico_w	:= substr(ds_lista_servico_w,1,(ie_pos_virgula_w - 1));
			ds_lista_servico_w	:= trim(both substr(ds_lista_servico_w,(ie_pos_virgula_w + 1), tam_lista_w));
			 
			CALL gravar_servicos_paciente_atend(nr_atendimento_p,nr_seq_servico_w,nm_usuario_p,cd_estabelecimento_p,lancar_conta_p);
		end if;
	end loop;
end if;
 
commit;
 
end;
$body$
