CREATE OR REPLACE PROCEDURE gerar_servico_nut_periodo ( nr_seq_nut_p bigint, qt_dias_p bigint, nm_usuario_p text, cd_pessoa_fisica_p text, ie_permite_mesmo_dia_p text) AS $body$
DECLARE


nr_seq_perfil_pf_w		bigint;
nr_seq_reserva_w		bigint;
qt_itens_w		bigint;

BEGIN

select	coalesce(nr_seq_nut_perfil, 0)
into STRICT	nr_seq_perfil_pf_w
from	pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_p;


for i in 1..qt_dias_p loop

	select	nextval('nut_reserva_seq')
	into STRICT	nr_seq_reserva_w
	;

	insert into nut_reserva(
		nr_sequencia,
		cd_estabelecimento,
		nr_seq_servico,
		nr_seq_restaurante,
		dt_servico,
		hr_inicio,
		hr_final,
		qt_reserva,
		qt_min_antecedencia,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_atualizacao,
		nm_usuario)
	SELECT
		nr_seq_reserva_w,
		a.cd_estabelecimento,
		a.nr_seq_servico,
		a.nr_seq_restaurante,
		a.dt_servico + i,
		a.hr_inicio,
		a.hr_final,
		a.qt_reserva,
		a.qt_min_antecedencia,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p
	from	nut_reserva a
	where	a.nr_sequencia = nr_seq_nut_p
	and	'S' = obter_se_perfil_nut_liberado(nr_seq_perfil_pf_w, a.nr_sequencia)
	and	(((ie_permite_mesmo_dia_p = 'N') and not exists (SELECT 1
							from 	nut_reserva x
							where	to_char(x.hr_inicio, 'hh24:mi:ss') = to_char(a.hr_inicio, 'hh24:mi:ss')
							and	x.nr_seq_servico = a.nr_seq_servico
							and	x.nr_seq_restaurante = a.nr_seq_restaurante
							and	x.cd_estabelecimento = a.cd_estabelecimento
							and	x.dt_servico	=	a.dt_servico + i)) or (ie_permite_mesmo_dia_p = 'S'));


	select	count(*)
	into STRICT	qt_itens_w
	from	nut_reserva
	where	nr_sequencia = nr_seq_reserva_w;

	if (qt_itens_w > 0) then
		insert into nut_perfil_reserva(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_nut_reserva,
						nr_seq_perfil)
		SELECT	nextval('nut_perfil_reserva_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_reserva_w,
			nr_seq_perfil
		from	nut_perfil_reserva
		where	nr_seq_nut_reserva = nr_seq_nut_p;
	end if;

end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_servico_nut_periodo ( nr_seq_nut_p bigint, qt_dias_p bigint, nm_usuario_p text, cd_pessoa_fisica_p text, ie_permite_mesmo_dia_p text) FROM PUBLIC;

